import 'package:ieca_mobile/models/_import.dart';

class HymnsHolySpirit{
  static List<HymnsContent> list = [
    HymnsContent(id: 345, position: 1, content: "Ó divinal Instruidor Vem revelar-nos o Senhor. As nossas almas vem encher Da Tua luz, do teu poder.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_83, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 346, position: 2, content: "Ó inefável Preceptor, Com Teu altíssimo favor Vem nossas almas inflamar, No santo ardor do Teu altar.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_83, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 347, position: 3, content: "Ó vem, Espírito veraz, A escuridão em nós desfaz, Revela ao mundo a Tua luz, A redenção que vem da cruz.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_83, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 348, position: 1, content: "Ó Espírito Benigno, Suplicamos teus dons; Mas de todos eles bons! Dá-nos o amor!", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_84, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 349, position: 2, content: "Fale eu língua angelical, Tenha eu dom de profecia; Nada me aproveitaria, Só com o amor.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_84, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 350, position: 3, content: "A inveja e a soberba Em nós sejam expungidos! E a calúnia também cesse; Dá-nos o amor.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_84, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 351, position: 4, content: "O amor não é invejoso, Não se porta com indecência, Tudo sofre tudo espera; Dá-nos o amor.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_84, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 352, position: 5, content: "O amor quando é real É constante é leal; Ó Espírito Benfeitor Dá-nos o amor.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_84, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 353, position: 1, content: "Espírito vivificante, Que renovas corações, Cura-me pois estou cego; Para que eu veja melhor.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_85, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 354, position: 2, content: "Espírito vivificante, A alma é tão vacilante; Dá robustez e vigor. Faz-me viver com fulgor.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_85, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 355, position: 3, content: "Espírito vivificante Tu, ó constante renovador, Livra-me do perigo, Pois o mundo renego.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_85, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 356, position: 4, content: "Espírito vivificante; Torna já cantante Meu aflito coração; Viva eu com rectidão.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_85, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 357, position: 1, content: "Dirija-me Espírito Santo: Onde quer que seja irei. Tua mão me sustenta. No sombrio vale da morte, Espanto nenhum terei; Teus ensinos escutarei. Ouço tua voz dizendo: Eu dirijo-te não temas.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_86, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 358, position: 2, content: "Meu Senhor, fica comigo; Que eu vá onde tu queiras. Não me rechaces, contrito estou, O inimigo me cercou. Vacilante ando na terra, Lentamente caindo vou. Ouço tua voz dizendo: Eu dirijo-te não temas.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_86, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 359, position: 3, content: "Vou contigo, mais que vencedor: A batalha final vencerei. Tranquilize meu coração, Pois o inimigo me laça. Então chegarei à mansão, Lá na eterna Caná. Ouço tua voz dizendo; Eu dirijo-te não temas.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_86, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 360, position: 1, content: "Espírito Poderoso! Para a minha caminhada, Dá-me teu poder glorioso, Para marchar vitorioso; Encoraja-me a avançar, Meu Senhor, meu protector", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_87, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 361, position: 2, content: "Espírito verdadeiro! Vem, submeta meu coração. Tua vontade sempre faça. Meus intentos controlar. Se p'ra o mal eu sou tentado, Meu escudo é o Teu amor.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_87, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 362, position: 3, content: "Espírito de amor, Caridoso anelo ser. Minhas obras manifestem, O amor do Salvador. E em todo o meu falar Transpareça a Tua voz.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_87, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 363, position: 4, content: "Espírito autor da paz, De mim, ofensa dissipar. Onde houver violência e ódio, Manifeste o Teu amor. Sempre eu lembre o Teu perdão Que no calvário concedeu.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_87, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 364, position: 1, content: "Vem, Espírito divino, Grande Ensinador! Vem! descobre às nossas almas Cristo, o Salvador.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_88, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 365, content: "Mestre, Mestre, Ouve com favor! Em poder e graça insigne Obre o Teu amor!", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_88, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 366, position: 2, content: "Vem! demole os alicerces Da enganosa paz, Aos errados concedendo Salvação veraz!", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_88, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 367, position: 3, content: "Vem! Reveste a tua Igreja De energia e luz! Vem! Atrai os desviados Ao Senhor Jesus!", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_88, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 368, position: 4, content: "Maravilhas soberanas Outros povos vêem; Oh! derrama a mesma bênção Sobre nós também.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_88, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 369, position: 1, content: "Na despedida, o Senhor Seu benefício deu; Espírito Santo p’ra ser Auxiliador.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_89, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 370, position: 2, content: "Enviou-O para nos tornar, Justiça do Deus Pai; Importa que tenhamos fé No Seu poder.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_89, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 371, position: 3, content: "Ele também nos dá poder, Para vencer o mal; A inteligência Ele nos dá, Do Reino Seu.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_89, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 372, position: 4, content: "Se sobrevém desvio em nós, No trilho do viver, Sua bondade e amor, Nos faz voltar.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_89, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 373, position: 5, content: "Ó poderoso Espírito, Purificar-nos vem; Lembrar-nos vem da morte atroz, Do Salvador.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_89, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 374, position: 6, content: "Vem atear o fogo em nós, Justiça e amor; Tornando nossos corações Tua habitação.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_89, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 375, position: 1, content: "Vem, Espírito divino, Poderoso Benfeitor, A minha alma fraca e pobre Revelar divino amor.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_90, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 376, position: 2, content: "Vem-me descobrir a glória De Jesus, o Salvador, Que morrendo, trouxe ao mundo O poder de vero amor.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_90, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 377, position: 3, content: "Com vivificante graça, E com todo o Teu fulgor, Vem raiar em minha vida Verdadeiro e puro amor.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_90, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 378, position: 4, content: "Que eu aos tristes e cansados, Carregados de temor, Possa por divina graça, Mediar divino amor.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_90, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 379, position: 1, content: "Ó Divino Preceptor! Bom, fiel consolador! Faze agora em todos nós Poderosa a Tua voz.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_91, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 380, position: 2, content: "Grande e vero instruidor, Com celestial favor, Mostra como te adorar E este culto a Deus prestar.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_91, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 381, position: 3, content: "Santo Espírito de Deus, Enche de fervor os Teus, Para entoarem o louvor De Jesus, o Salvador.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_91, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 382, position: 4, content: "Vem, Consolador veraz; Dá-nos firme, estável paz. No poder da Tua luz Guia as almas a Jesus!", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_91, typeStanza: HymnsContentCode.VERSE),
  ];
