import 'package:ieca_mobile/db/_import.dart';
import 'package:ieca_mobile/enums/HymnsContentCode.dart';
import 'package:ieca_mobile/models/_import.dart';

class HymnsPassionAndDeathOfJesus{
  static List<HymnsContent> list = [
    HymnsContent(id: 239, position: 1, content: "Quando eu contemplo aquela Cruz\nNa qual Jesus, meu Rei, morreu,\nMeu oiro a cinzas se reduz.\nE a coisa vil o orgulho meu.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_58, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 240, position: 2, content: "Não tenha eu glória, ó Deus jamais\nSenão na Cruz do meu Senhor;\nE as coisas vãs que anelo mais\nAs sacrifico ao Seu amor.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_58, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 241, position: 3, content: "Nos pés, nas mãos, no rosto\nSeu A dor eu vejo e vejo o amor.\nAh! quem de espinhos já nos deu\nCoroa assim de tal valor?", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_58, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 242, position: 4, content: "Tudo o que existe sob o céu\nNão era nada p'ra Lhe dar:\nMinh'alma e vida, quando é meu\nConsagro ao Seu amor sem par.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_58, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 243, position: 1, content: "Mui longe o monte verde está,\nAo norte de Sião,\nNo qual Jesus na santa cruz,\nNos deu a salvação.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_59, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 244, position: 2, content: "Quem sondará, quem contará\nA dor que padeceu?\nMas confiamos foi por nós\nQue lá na cruz sofreu.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_59, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 245, position: 3, content: "Morreu p'ra dar-nos o perdão;\nMorreu p'ra sermos bons;\nP'ra entrarmos na mansão de Deus,\nCom puros corações.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_59, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 246, position: 4, content: "Oh, quanto, quanto nos amou!\nAmemo-lo também,\nE, confiando em Seu amor,\nFaçamos todo o bem.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_59, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 247, position: 1, content: "Oh, como foi que meu Jesus\nAssim sofreu na triste cruz?!\nNão só na cruz mas no jardim\nAgonizou, e foi por mim!", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_60, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 248, content: "Ali na cruz, ali na cruz,\nOh, sim, por mim Jesus sofreu!\nAli na cruz, ali na cruz, Oh, sim,\nJesus por mim morreu!", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_60, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 249, position: 2, content: "O grande horror da escuridão\nApavorou a multidão!\nRasgado o véu lhes fez saber\nQue terminou o Seu sofrer.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_60, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 250, position: 3, content: "Que dor cruel, na cruz sofreu!\nSeu sangue ali Jesus verteu.\nSim, foi por mim, p'ra me salvar,\nPara eu, enfim, no Céu morar.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_60, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 251, position: 1, content: "Se sentires as tentações,\nDirija-se a Getesêmane.\nVê a agonia de Jesus,\nReflicta como padeceu.\nBusca exemplo a Jesus;\nOre como Ele orou.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_61, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 252, position: 2, content: "Vai com Ele à Justiça,\nOnde foi julgado,\nSofrimento árduo vê,\nEnvolvido na solidão.\nBusca exemplos a Jesus; Não fujas o escárnio.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_61, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 253, position: 3, content: "Eleva-te ao Gólgota,\nAdore e venere a Cruz;\nÉ aí onde Jesus morreu,\nExpiação de pecados.\nBusca exemplo a Jesus,\nLevante firme Sua Cruz.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_61, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 254, position: 4, content: "Madrugue para o sepulcro,\nLá onde foi sepultado,\nReconheça Sua Santidade;\nComo venceu a morte,\nTal como Ele ressuscitou,\nTambém ressuscitamos.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_61, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 255, position: 1, content: "Naquela noite, no Jardim\nCoberto de escuridão;\nOnde Jesus orou,\nPedindo força e consolação.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_62, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 256, position: 2, content: "Na noite de tristeza e dor,\nJesus estava na solidão;\nConsolador fenomenal,\nNão tinha quem o proteger.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_62, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 257, position: 3, content: "Naquela noite de solidão\nJesus foi rejeitado,\nMesmo ignorado por todos,\nDeus, porém, o fortaleceu.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_62, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 258, position: 4, content: "Tal noite Ele, o Redentor,\nVenceu o receio e medo;\nCom força dada por Deus,\nDecidiu firme à morte.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_62, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 259, position: 1, content: "Com grande amor Jesus\nPor mim desceu do céu;\nNo mundo sobre a cruz,\nTudo por mim sofreu.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_63, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 260, content: "Cantai, louvai o Salvador.(2x)\nLouvai o Seu eterno amor.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_63, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 261, position: 2, content: "Pecados expiou Quando por mim morreu;\nAs manchas me lavou\nCom sangue que verteu.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_63, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 262, position: 3, content: "Em glória está no céu;\nComigo aqui está.\nA salvação me deu.\nSempre me guardará.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_63, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 263, position: 4, content: "Sou Teu, meu Salvador;\nA Ti eu seguirei,\nE sempre Teu louvor\nAlegre cantarei.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_63, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 264, position: 1, content: "Ao pé da cruz de Cristo\nEu sempre quero estar.\nMinh’alma forte e fiel\nAli quer se achar.\nNo meio do deserto aqui\nNão há nenhum lugar,\nQue do calor e do sofrer\nEu possa descansar.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_64, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 265, position: 2, content: "Naquela cruz de Cristo\nAs vezes vejo eu,\nA forma do meu Salvador\nQuando por mim morreu.\nE meu contrito coração\nConfessa humilde a Ti:\nQue sou indigno pecador\nE Tu meu Rei fiel.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_64, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 266, position: 3, content: "Oh! Cristo sempre quero,\nTeu rosto contemplar.\nNenhuma outra bênção há,\nDo que contigo andar.\nSe atractivo o mundo está,\nEu ando por Tua luz.\nLiberto, agora vou louvar\nTeu nome, meu Jesus.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_64, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 267, position: 1, content: "Vinde, aflitos pecadores\nCheios de tristeza e dor,\nA Jesus, que vos convida\nCom instante e terno amor.\nEle é forte;\nEle é forte:\nConfiai em seu favor.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_65, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 268, position: 2, content: "Vinde, pobres e famintos,\nE o favor de Deus buscai;\nFé bendita, toda a graça\nSó em Cristo procurai.\nSem dinheiro; sem dinheiro;\nVinde a Cristo e já comprai.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_65, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 269, position: 3, content: "A consciência diz bem claro\n És indigno de favor.\nDiz Jesus: eu pelo indigno\nJá morri, sou fiador.\nVinde logo; vinde logo;\nAceitai seu rico amor.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_65, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 270, position: 4, content: "Eis Jesus, Deus humanado,\nSobre o trono de esplendor;\nEis ferida a fronte e o lado,\nMostra o sangue redentor.\nIsso basta; isso basta;\nVinde, vinde sem temor.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_65, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 271, position: 1, content: "Chegado à cruz do meu Salvador,\nProstrado aos pés do meu Redentor,\nEle atendeu logo ao meu clamor;\nGlória ao Salvador!", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_66, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 272, content: "Glória ao Salvador!(2x)\nAgora sei que Ele me salvou.\nGlória ao Salvador!", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_66, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 273, position: 2, content: "Que maravilha Jesus me amou!\nTudo de graça me perdoou!\nQuebrou meus laços e me livrou!\nGlória ao Salvador!", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_66, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 274, position: 3, content: "Vem, alma, escuta ao teu Salvador;\nFala-te agora com terno amor;\nMorri por ti, pobre pecador;\nGlória ao Salvador!", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_66, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 275, position: 1, content: "Por fé contemplo a redenção;\nA fonte carmesim,\nJesus nos dá a salvação\nA vida deu por mim.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_67, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 276, content: "Contemplo a fonte carmesim;\nEu bebo e me limpa a mim.\nOh! glória a Deus, mais branco enfim,\nDo que a neve, limpa sim.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_67, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 277, position: 2, content: "A vida entrego a meu Jesus;\nAs dúvidas tirou.\nMinh'alma goza Sua luz;\nAs dívidas pagou.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_67, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 278, position: 3, content: "Quão inefável gozo é;\nSaber que salvo sou.\nJesus é meu só pela fé;\nAo céu eu sei que vou.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_67, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 279, position: 4, content: "Ó graça excelsa de meu Deus;\nProfundo Seu amor;\nQue nos salvou, fez filhos Seus;\nÉ nosso Redentor.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_67, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 280, position: 1, content: "Cristo em tua cruz há glória,\nDominando o caos maior;\nToda a luz da sacra história\nSe concentra ao seu redor.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_68, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 281, position: 2, content: "Se me abatem vis temores,\nE a esperança se desfaz,\nÉ na cruz que encontro flores:\nDela vem prazer e paz.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_68, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 282, position: 3, content: "Se o sol brilha da ventura,\nAlegrando o meu viver,\nLá da cruz a luz fulgura\nE me traz maior prazer.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_68, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 283, position: 4, content: "Por igual, já são benditas\nA ventura e a dor, na cruz;\nAlegrias inauditas\nNela trouxe o bom Jesus.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_68, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 284, position: 1, content: "Na cidade Santa\nDe grande turbilhão,\nFoi morto Homem-Deus;\nFoi por mim...! Que morreu!", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_69, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 285, position: 2, content: "Com coroa de espinhos,\nFerido no Seu corpo Levou a Sua Cruz;\nFoi por mim...! Que morreu!", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_69, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 286, position: 3, content: "Entre os salteadores\nFoi levado à morte,\nCrucificado no madeiro; Foi por mim...! Que morreu!", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_69, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 287, position: 4, content: "Jesus Senhor da vida\nQuis morrer por mim,\nP'ra dar-me vida Eterna;\nFoi por mim...! Que morreu!", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_69, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 288, position: 5, content: "Que assim Jesus fez por mim,\nTambém inteiro me entrego,\nP'ra servi-Lo com amor;\nFoi por mim...! Que morreu!", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_69, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 289, position: 1, content: "Morri na cruz por ti;\nMorri p’ra te livrar.\nMeu sangue, sim verti\nA fim de te salvar.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_70, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 290, content: "Morri, morri na cruz por ti.\nQue fazes tu por mim? (2x)", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_70, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 291, position: 2, content: "Vivi assim por ti,\nCom dor, com dissabor.\nSim, tudo fiz aqui,\nP’ra ser teu Salvador.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_70, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 292, position: 3, content: "Sofri na cruz por ti,\nA fim de te salvar;\nA vida consegui,\nQue tu irás gozar.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_70, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 293, position: 4, content: "Eu trouxe a salvação,\nDos altos céus favor;\nÉ livre meu perdão;\nÉ grande o meu amor.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_70, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 294, position: 1, content: "Achei a fonte carmesim\nQue meu Jesus abriu\nNa cruz, morrendo ali por mim,\nMinha alma assim remiu.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_71, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 295, content: "Eu creio, sim, eu creio, sim.\nJesus por mim morreu;\nQue sobre a Cruz, p’ra me salvar,\nCastigo padeceu.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_71, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 296, position: 2, content: "Na Cruz meu Cristo já lavou\nO mal que cometi,\nE pela morte que penou\nA vida eu consegui.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_71, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 297, position: 3, content: "Assim, pois, fez-me com amor\nAndar no trilho Seu.\nConfio Nele com fervor.\nEm quem por mim morreu.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_71, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 298, position: 1, content: "Aflito e triste coração,\nDeus cuidará de ti.\nPor ti opera a Sua mão.\nDeus cuidará de ti.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_72, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 299, content: "Deus cuidará de ti:\nEm cada dia proverá.\nDeus cuidará de ti.\nDeus cuidará de ti.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_72, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 300, position: 2, content: "Na dor cruel, na provação\nDeus cuidará de ti.\nSocorro dá e salvação.\nDeus cuidará de ti.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_72, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 301, position: 3, content: "A tua fé Deus quer provar,\nDeus cuidará de ti;\nO teu amor quer aumentar,\nDeus cuidará de ti.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_72, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 302, position: 4, content: "Nos seus tesouros tudo tens.\nDeus cuidará de ti.\nBens desta vida e celestes bens.\nDeus cuidará de ti.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_72, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 303, position: 5, content: "O que é mister te pode dar,\nQuem cuidará de ti;\nNos braços seus te sustentar,\nPois cuidará de ti.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_72, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 304, position: 1, content: "Oh! Que tão precioso sangue\nMeu Senhor verteu,\nQuando para resgatar-nos,\nPadeceu!", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_73, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 305, position: 2, content: "Oh! Que tão precioso sangue!\nFala-nos de paz;\nTudo quanto a lei exige\nSatisfaz!", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_73, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 306, position: 3, content: "Oh! Que tão precioso sangue!\nTraz-nos salvação;\nDeus por Ele dá aos crentes\nO perdão!", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_73, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 307, position: 4, content: "Oh! Que tão precioso sangue\nDo meu Salvador!\nPois que a todos manifesta\nSeu amor!", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_73, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 308, position: 5, content: "Oh! Que tão precioso sangue\nQue liberta os réus!\nPodem ter por ele entrada\nLá nos céus!", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_73, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 309, position: 1, content: "Rude cruz se erigiu,\nDela o dia fugiu,\nComo emblema\nDe afronta e de dor;\nMas eu amo essa cruz,\nPorque nela, Jesus\nDeu a vida por mim, pecador.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_74, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 310, content: "Sim, eu sempre amarei essa cruz;\nSeu triunfo meu gozo será;\nPois um dia, em lugar de uma cruz,\mA coroa Jesus me dará.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_74, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 311, position: 2, content: "Desde a glória dos céus,\nO Cordeiro de Deus\nAo Calvário humilhante baixou;\nTem a cruz para mim,\nAtractivos sem fim,\nPorque nela Jesus me salvou.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_74, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 312, position: 3, content: "Lá na cruz padeceu,\nDesprezado e morreu,\nMeu Jesus para dar-me o perdão;\nDela agora provém\nPara mim todo o bem,\nTenho nela real salvação.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_74, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 313, position: 4, content: "Eu aqui, com Jesus,\nA vergonha da cruz,\nQuero sempre levar e sofrer.\nQuando Cristo voltar\nPara aqui me buscar,\nSua glória eu irei receber.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_74, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 314, position: 1, content: "O grande amor de meu Jesus\nPor mim morrendo sobre a cruz,\nDa perdição p'ra me salvar,\nQuem poderá contar?", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_75, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 315, content: "Quem pode o seu amor contar?(2x)\nO grande amor do Salvador,\nQuem poderá contar?", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_75, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 316, position: 2, content: "O cálix que Jesus bebeu,\nA maldição que padeceu,\nNa cruz por mim, p'ra me salvar,\nQuem poderá contar?", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_75, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 317, position: 3, content: "A zombaria tão cruel,\nA cruz sangrenta, o amargo fel Que ali sofreu p'ra me salvar,\nQuem poderá contar?", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_75, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 318, position: 4, content: "Incomparável Salvador,\nQuão inefável teu amor!\nQuão impossível de sondar!\nImensidão sem-par!", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_75, typeStanza: HymnsContentCode.VERSE),

