import 'package:ieca_mobile/db/_import.dart';
import 'package:ieca_mobile/enums/HymnsContentCode.dart';
import 'package:ieca_mobile/models/_import.dart';

class HymnsPassionAndDeathOfJesus{
  static List<HymnsContent> list = [
    HymnsContent(id: 239, position: 1, content: "Quando eu contemplo aquela Cruz Na qual Jesus, meu Rei, morreu, Meu oiro a cinzas se reduz. E a coisa vil o orgulho meu.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_58, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 240, position: 2, content: "Não tenha eu glória, ó Deus jamais Senão na Cruz do meu Senhor; E as coisas vãs que anelo mais As sacrifico ao Seu amor.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_58, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 241, position: 3, content: "Nos pés, nas mãos, no rosto Seu A dor eu vejo e vejo o amor. Ah! quem de espinhos já nos deu Coroa assim de tal valor?", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_58, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 242, position: 4, content: "Tudo o que existe sob o céu Não era nada p'ra Lhe dar: Minh'alma e vida, quando é meu Consagro ao Seu amor sem par.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_58, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 243, position: 1, content: "Mui longe o monte verde está, Ao norte de Sião, No qual Jesus na santa cruz, Nos deu a salvação.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_59, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 244, position: 2, content: "Quem sondará, quem contará A dor que padeceu? Mas confiamos foi por nós Que lá na cruz sofreu.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_59, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 245, position: 3, content: "Morreu p'ra dar-nos o perdão; Morreu p'ra sermos bons; P'ra entrarmos na mansão de Deus, Com puros corações.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_59, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 246, position: 4, content: "Oh, quanto, quanto nos amou! Amemo-lo também, E, confiando em Seu amor, Façamos todo o bem.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_59, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 247, position: 1, content: "Oh, como foi que meu Jesus Assim sofreu na triste cruz?! Não só na cruz mas no jardim Agonizou, e foi por mim!", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_60, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 248, content: "Ali na cruz, ali na cruz, Oh, sim, por mim Jesus sofreu! Ali na cruz, ali na cruz, Oh, sim, Jesus por mim morreu!", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_60, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 249, position: 2, content: "O grande horror da escuridão Apavorou a multidão! Rasgado o véu lhes fez saber Que terminou o Seu sofrer.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_60, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 250, position: 3, content: "Que dor cruel, na cruz sofreu! Seu sangue ali Jesus verteu. Sim, foi por mim, p'ra me salvar, Para eu, enfim, no Céu morar.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_60, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 251, position: 1, content: "Se sentires as tentações, Dirija-se a Getesêmane. Vê a agonia de Jesus, Reflicta como padeceu. Busca exemplo a Jesus; Ore como Ele orou.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_61, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 252, position: 2, content: "Vai com Ele à Justiça, Onde foi julgado, Sofrimento árduo vê, Envolvido na solidão. Busca exemplos a Jesus; Não fujas o escárnio.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_61, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 253, position: 3, content: "Eleva-te ao Gólgota, Adore e venere a Cruz; É aí onde Jesus morreu, Expiação de pecados. Busca exemplo a Jesus, Levante firme Sua Cruz.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_61, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 254, position: 4, content: "Madrugue para o sepulcro, Lá onde foi sepultado, Reconheça Sua Santidade; Como venceu a morte, Tal como Ele ressuscitou, Também ressuscitamos.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_61, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 254, position: 1, content: "Naquela noite, no Jardim Coberto de escuridão; Onde Jesus orou, Pedindo força e consolação.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_62, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 255, position: 2, content: "Na noite de tristeza e dor, Jesus estava na solidão; Consolador fenomenal, Não tinha quem o proteger.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_62, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 256, position: 3, content: "Naquela noite de solidão Jesus foi rejeitado, Mesmo ignorado por todos, Deus, porém, o fortaleceu.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_62, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 257, position: 4, content: "Tal noite Ele, o Redentor, Venceu o receio e medo; Com força dada por Deus, Decidiu firme à morte.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_62, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 258, position: 1, content: "Com grande amor Jesus Por mim desceu do céu; No mundo sobre a cruz, Tudo por mim sofreu.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_63, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 259, content: "Cantai, louvai o Salvador. (2x) Louvai o Seu eterno amor.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_63, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 260, position: 2, content: "Pecados expiou Quando por mim morreu; As manchas me lavou Com sangue que verteu.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_63, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 261, position: 3, content: "Em glória está no céu; Comigo aqui está. A salvação me deu. Sempre me guardará.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_63, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 262, position: 4, content: "Sou Teu, meu Salvador; A Ti eu seguirei, E sempre Teu louvor Alegre cantarei.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_63, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 263, position: 1, content: "Ao pé da cruz de Cristo Eu sempre quero estar. Minh’alma forte e fiel Ali quer se achar. No meio do deserto aqui Não há nenhum lugar, Que do calor e do sofrer Eu possa descansar.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_64, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 264, position: 2, content: "Naquela cruz de Cristo As vezes vejo eu, A forma do meu Salvador Quando por mim morreu. E meu contrito coração Confessa humilde a Ti: Que sou indigno pecador E Tu meu Rei fiel.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_64, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 265, position: 3, content: "Oh! Cristo sempre quero, Teu rosto contemplar. Nenhuma outra bênção há, Do que contigo andar. Se atractivo o mundo está, Eu ando por Tua luz. Liberto, agora vou louvar Teu nome, meu Jesus.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_64, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 266, position: 1, content: "Vinde, aflitos pecadores Cheios de tristeza e dor, A Jesus, que vos convida Com instante e terno amor. Ele é forte; Ele é forte: Confiai em seu favor.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_65, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 267, position: 2, content: "Vinde, pobres e famintos, E o favor de Deus buscai; Fé bendita, toda a graça Só em Cristo procurai. Sem dinheiro; sem dinheiro; Vinde a Cristo e já comprai.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_65, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 268, position: 3, content: "A consciência diz bem claro: És indigno de favor. Diz Jesus: eu pelo indigno Já morri, sou fiador. Vinde logo; vinde logo; Aceitai seu rico amor.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_65, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 269, position: 4, content: "Eis Jesus, Deus humanado, Sobre o trono de esplendor; Eis ferida a fronte e o lado, Mostra o sangue redentor. Isso basta; isso basta; Vinde, vinde sem temor.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_65, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 270, position: 1, content: "Chegado à cruz do meu Salvador, Prostrado aos pés do meu Redentor, Ele atendeu logo ao meu clamor; Glória ao Salvador!", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_66, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 271, content: "Glória ao Salvador!(2x) Agora sei que Ele me salvou. Glória ao Salvador!", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_66, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 272, position: 2, content: "Que maravilha Jesus me amou! Tudo de graça me perdoou! Quebrou meus laços e me livrou! Glória ao Salvador!", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_66, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 273, position: 3, content: "Vem, alma, escuta ao teu Salvador; Fala-te agora com terno amor; Morri por ti, pobre pecador; Glória ao Salvador!", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_66, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 274, position: 1, content: "Por fé contemplo a redenção; A fonte carmesim, Jesus nos dá a salvação A vida deu por mim.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_67, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 275, content: "Contemplo a fonte carmesim; Eu bebo e me limpa a mim. Oh! glória a Deus, mais branco enfim, Do que a neve, limpa sim.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_67, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 276, position: 2, content: "A vida entrego a meu Jesus; As dúvidas tirou. Minh'alma goza Sua luz; As dívidas pagou.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_67, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 277, position: 3, content: "Quão inefável gozo é; Saber que salvo sou. Jesus é meu só pela fé; Ao céu eu sei que vou.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_67, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 278, position: 4, content: "Ó graça excelsa de meu Deus; Profundo Seu amor; Que nos salvou, fez filhos Seus; É nosso Redentor.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_67, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 279, position: 1, content: "Cristo em tua cruz há glória, Dominando o caos maior; Toda a luz da sacra história Se concentra ao seu redor.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_68, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 280, position: 2, content: "Se me abatem vis temores, E a esperança se desfaz, É na cruz que encontro flores: Dela vem prazer e paz.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_68, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 281, position: 3, content: "Se o sol brilha da ventura, Alegrando o meu viver, Lá da cruz a luz fulgura E me traz maior prazer.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_68, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 282, position: 4, content: "Por igual, já são benditas A ventura e a dor, na cruz; Alegrias inauditas Nela trouxe o bom Jesus.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_68, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 283, position: 1, content: "Na cidade Santa De grande turbilhão, Foi morto Homem-Deus; Foi por mim...! Que morreu!", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_69, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 284, position: 2, content: "Com coroa de espinhos, Ferido no Seu corpo Levou a Sua Cruz; Foi por mim...! Que morreu!", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_69, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 285, position: 3, content: "Entre os salteadores Foi levado à morte, Crucificado no madeiro; Foi por mim...! Que morreu!", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_69, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 286, position: 4, content: "Jesus Senhor da vida Quis morrer por mim, P'ra dar-me vida Eterna; Foi por mim...! Que morreu!", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_69, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 287, position: 5, content: "Que assim Jesus fez por mim, Também inteiro me entrego, P'ra servi-Lo com amor; Foi por mim...! Que morreu!", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_69, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 288, position: 1, content: "Morri na cruz por ti; Morri p’ra te livrar. Meu sangue, sim verti A fim de te salvar.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_70, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 289, content: "Morri, morri na cruz por ti. Que fazes tu por mim? (2x)", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_70, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 290, position: 2, content: "Vivi assim por ti, Com dor, com dissabor. Sim, tudo fiz aqui, P’ra ser teu Salvador.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_70, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 291, position: 3, content: "Sofri na cruz por ti, A fim de te salvar; A vida consegui, Que tu irás gozar.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_70, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 292, position: 4, content: "Eu trouxe a salvação, Dos altos céus favor; É livre meu perdão; É grande o meu amor.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_70, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 293, position: 1, content: "Achei a fonte carmesim Que meu Jesus abriu Na cruz, morrendo ali por mim, Minha alma assim remiu.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_71, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 294, content: "Eu creio, sim, eu creio, sim. Jesus por mim morreu; Que sobre a Cruz, p’ra me salvar, Castigo padeceu.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_71, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 295, position: 2, content: "Na Cruz meu Cristo já lavou O mal que cometi, E pela morte que penou A vida eu consegui.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_71, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 296, position: 3, content: "Assim, pois, fez-me com amor Andar no trilho Seu. Confio Nele com fervor. Em quem por mim morreu.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_71, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 297, position: 1, content: "Aflito e triste coração, Deus cuidará de ti. Por ti opera a Sua mão. Deus cuidará de ti.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_72, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 298, content: "Deus cuidará de ti: Em cada dia proverá. Deus cuidará de ti. Deus cuidará de ti.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_72, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 299, position: 2, content: "Na dor cruel, na provação Deus cuidará de ti. Socorro dá e salvação. Deus cuidará de ti.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_72, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 300, position: 3, content: "A tua fé Deus quer provar, Deus cuidará de ti; O teu amor quer aumentar, Deus cuidará de ti.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_72, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 301, position: 4, content: "Nos seus tesouros tudo tens. Deus cuidará de ti. Bens desta vida e celestes bens. Deus cuidará de ti.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_72, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 302, position: 5, content: "O que é mister te pode dar, Quem cuidará de ti; Nos braços seus te sustentar, Pois cuidará de ti.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_72, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 303, position: 1, content: "Oh! Que tão precioso sangue Meu Senhor verteu, Quando para resgatar-nos, Padeceu!", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_73, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 304, position: 2, content: "Oh! Que tão precioso sangue! Fala-nos de paz; Tudo quanto a lei exige Satisfaz!", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_73, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 305, position: 3, content: "Oh! Que tão precioso sangue! Traz-nos salvação; Deus por Ele dá aos crentes O perdão!", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_73, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 306, position: 4, content: "Oh! Que tão precioso sangue Do meu Salvador! Pois que a todos manifesta Seu amor!", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_73, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 307, position: 5, content: "Oh! Que tão precioso sangue Que liberta os réus! Podem ter por ele entrada Lá nos céus!", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_73, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 308, position: 1, content: "Rude cruz se erigiu, Dela o dia fugiu, Como emblema De afronta e de dor; Mas eu amo essa cruz, Porque nela, Jesus Deu a vida por mim, pecador.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_74, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 309, content: "Sim, eu sempre amarei essa cruz; Seu triunfo meu gozo será; Pois um dia, em lugar de uma cruz, A coroa Jesus me dará.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_74, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 310, position: 2, content: "Desde a glória dos céus, O Cordeiro de Deus Ao Calvário humilhante baixou; Tem a cruz para mim, Atractivos sem fim, Porque nela Jesus me salvou.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_74, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 311, position: 3, content: "Lá na cruz padeceu, Desprezado e morreu, Meu Jesus para dar-me o perdão; Dela agora provém Para mim todo o bem, Tenho nela real salvação.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_74, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 312, position: 4, content: "Eu aqui, com Jesus, A vergonha da cruz, Quero sempre levar e sofrer. Quando Cristo voltar Para aqui me buscar, Sua glória eu irei receber.", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_74, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 313, position: 1, content: "O grande amor de meu Jesus Por mim morrendo sobre a cruz, Da perdição p'ra me salvar, Quem poderá contar?", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_75, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 314, content: "Quem pode o seu amor contar? (2x) O grande amor do Salvador, Quem poderá contar?", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_75, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 315, position: 2, content: "O cálix que Jesus bebeu, A maldição que padeceu, Na cruz por mim, p'ra me salvar, Quem poderá contar?", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_75, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 316, position: 3, content: "A zombaria tão cruel, A cruz sangrenta, o amargo fel Que ali sofreu p'ra me salvar, Quem poderá contar?", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_75, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 317, position: 4, content: "Incomparável Salvador, Quão inefável teu amor! Quão impossível de sondar! Imensidão sem-par!", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_75, typeStanza: HymnsContentCode.VERSE),

    //HymnsContent(id: 313, position: 1, content: "", hymnsNumber: HymnsNumberSeeder.PASSION_AND_DEATH_OF_JESUS_75, typeStanza: HymnsContentCode.VERSE),
  ];
}