  static List<HymnsContent> list = [
    HymnsContent(id: 3637, position: 1, content: "Chika muchipemesa? \nE manyinga a Yesu wika. \nChika muchinguwuka? \nE manyinga a Yesu wika.", hymnsNumber: CoHymnsNumberSeeder.MESA_YA_MWENE_21, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 3638, content: "Manyinga alemu! \nA kutomesa to; \nAkuhi eka ngwo, \nE manyinga a Yesu wika.", hymnsNumber: CoHymnsNumberSeeder.MESA_YA_MWENE_21, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 3639, position: 2, content: "Ni ha kungusambisa, \nE manyinga a Yesu wika, \nNi ha kunguwanyisa, \nE manyinga a Yesu wika.", hymnsNumber: CoHymnsNumberSeeder.MESA_YA_MWENE_21, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 3640, position: 3, content: "Ni ha kungukukila, \nE manyinga a Yesu wika. \nSambukila hatwaha, \nE manyinga a Yesu wika.", hymnsNumber: CoHymnsNumberSeeder.MESA_YA_MWENE_21, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 3641, position: 4, content: "Nakukumba no nawa, \nE manyinga a Yesu wika. \nMwilu mungukaheta, \nKu manyinga a Yesu wika.", hymnsNumber: CoHymnsNumberSeeder.MESA_YA_MWENE_21, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 3642, position: 1, content: "Ye mbolo ya mwono ngupalwile, \nNgwe ku Ngalileya wahanyine; \nMba nguye ni Yena nikuwaha; \nTe Liji lie mulinguyukisa.", hymnsNumber: CoHymnsNumberSeeder.MESA_YA_MWENE_22, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 3643, position: 2, content: "Ye mbolo ya mwono ngupalwile, \nNgulise ngwe waze ku chitende; \nManyonga jami eswe ungwolwele, \nNi molu jami ungufungiele.", hymnsNumber: CoHymnsNumberSeeder.MESA_YA_MWENE_22, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 3644, position: 3, content: "Lie Liji lia Yena ngufukwile, \nNgwe ku Ngalileya matangwa eswe; \nMba nikuliendela mutuyaya, \nNi mwono we kumungusolwela.", hymnsNumber: CoHymnsNumberSeeder.MESA_YA_MWENE_22, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 3645, position: 4, content: "Ni Liji lia Yena nguwahise, \nNgwe waawahishile ku chitende; \nUfunge wa mbunge mba kumuuhwa, \nNi keke ni yeswe munguwana.", hymnsNumber: CoHymnsNumberSeeder.MESA_YA_MWENE_22, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 3646, position: 1, content: "Ha kutala Yesu  \nWafwile ha kulusu, \nMwono kuuli mukulichijila; \nMweswe mukwa shili  \nTalako kuli Yesu, \nWafwile mukukulamwina.", hymnsNumber: CoHymnsNumberSeeder.MESA_YA_MWENE_23, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 3647, content: "Wa, wa, we mwono! \nHa kutala Yesu wafwile ha kulusu, \nMwono kuuli kulichijila.", hymnsNumber: CoHymnsNumberSeeder.MESA_YA_MWENE_23, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 3648, position: 2, content: "Kuchi te wafwile  \nNikumbata ye shili, \nKachi kamutwikile shili ye? \nNi ku lumbu manyinga  \nAhichile kuchi, \nKachi longo lie kafwetele?", hymnsNumber: CoHymnsNumberSeeder.MESA_YA_MWENE_23, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 3649, position: 3, content: "We uhiso kuchiza  \nMasoji kahona, \nTe manyinga wika maukatwisa: \nHali Yoze wika  \nWaamwangine kashika, \nShili ye yeswe langumuna.", hymnsNumber: CoHymnsNumberSeeder.MESA_YA_MWENE_23, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 3650, position: 4, content: "Kanda uhangulula,  \nZambi mwene wambile, \nNgwo, Nichimwe chasala chakuhi; \nHa songo lia miaka  \nKâ€™Listu wasolokele, \nYe milimo wamanunwine.", hymnsNumber: CoHymnsNumberSeeder.MESA_YA_MWENE_23, typeStanza: HymnsContentCode.VERSE),
