class FtLitanyContentSeeder{

  static List<LitanyContent> items() {
    return [
      LitanyContent(id: 400, person: LitanyPerson.DIRECTOR, litanyTitle: FtLitanyTitleSeeder.NDOMBO_A_NSAMBUDULU, books: "Habak. 2:20", content: "Yave wena mu nzo andi yanlongo. Bika ntoto wamvimba wakala napi va ntadisi andi."),
      LitanyContent(id: 401, person: LitanyPerson.CONGREGATION, litanyTitle: FtLitanyTitleSeeder.NDOMBO_A_NSAMBUDULU, books: "Nkunga 46:1", content: "Nzolele wa mavovanga. Nzambi, Mfumu, kadi uvovanga yenge kwa bantu bandi."),
      LitanyContent(id: 402, person: LitanyPerson.DIRECTOR, litanyTitle: FtLitanyTitleSeeder.NDOMBO_A_NSAMBUDULU, books: "Nkunga 85:8", content: "Nzambi i swamunu kito ye ngolo zeto, yandi i nsadisi wena lukufi mu ntangu zanzungu."),
      LitanyContent(id: 403, person: LitanyPerson.CONGREGATION, litanyTitle: FtLitanyTitleSeeder.NDOMBO_A_NSAMBUDULU, books: "Nkunga 46:2", content: "I kuma ka tuna tina ko, vo ntoto una bukana ye miongo mina bukumuka mu nsin’ambu."),
      LitanyContent(id: 404, person: LitanyPerson.DIRECTOR, litanyTitle: FtLitanyTitleSeeder.NDOMBO_A_NSAMBUDULU, books: "Nkunga 130:7", content: "Sa vuvu mu Mfumu, kadi kwa Mfumu kwena nlemvo, ye nkudulu yayingi yena yandi."),
      LitanyContent(id: 405, person: LitanyPerson.CONGREGATION, litanyTitle: FtLitanyTitleSeeder.NDOMBO_A_NSAMBUDULU, books: "Nkunga 130:5", content: "Itadidilanga Mfumu, mwel’ami uvingilanga, ye isanga vuvu mu mambu mandi."),
      LitanyContent(id: 406, person: LitanyPerson.DIRECTOR, litanyTitle: FtLitanyTitleSeeder.NDOMBO_A_NSAMBUDULU, books: "Nkunga 34:3", content: "Tusikeno Mfumu, beto yeno, ye tuzangiseno nkumbu andi va kimosi."),
      LitanyContent(id: 407, person: LitanyPerson.CONGREGATION, litanyTitle: FtLitanyTitleSeeder.NDOMBO_A_NSAMBUDULU, books: "Nkunga 96:4", content: "Kadi Mfumu wena waunene ye ufweni mu zituswa."),
      LitanyContent(id: 408, person: LitanyPerson.DIRECTOR, litanyTitle: FtLitanyTitleSeeder.NDOMBO_A_NSAMBUDULU, books: "Nkunga 103:8", content: "Nkenda ye nlemvo biena ye Mfumu ye vibidila ye vibidila ye mbidi anlembami."),
      LitanyContent(id: 409, person: LitanyPerson.CONGREGATION, litanyTitle: FtLitanyTitleSeeder.NDOMBO_A_NSAMBUDULU, books: "Nkunga 103:10", content: "Yandi kakutuvangilanga bonso bwena masumu meto ko, kakutuvutudilanga bonso bwena tufuma tueto ko."),
      LitanyContent(id: 410, person: LitanyPerson.DIRECTOR, litanyTitle: FtLitanyTitleSeeder.NDOMBO_A_NSAMBUDULU, books: "Nkunga 103:11", content: "Bonso bwalambuka zulu va ntandu antoto, i buna bwenina nlemvo andi wawingi kwa bakumvuminanga."),
      LitanyContent(id: 411, person: LitanyPerson.CONGREGATION, litanyTitle: FtLitanyTitleSeeder.NDOMBO_A_NSAMBUDULU, books: "Nkunga 103:12", content: "Bonso bwatatukila este mu weste, i bobo katatudilanga ntulumunu zeto ntama ye beto."),
      LitanyContent(id: 412, person: LitanyPerson.DIRECTOR, litanyTitle: FtLitanyTitleSeeder.NDOMBO_A_NSAMBUDULU, books: "Zingana 3:5", content: "Sila Yave vuvu mu ntim’aku wamvimba, kusilani nduka zaku vwandi ko."),
      LitanyContent(id: 413, person: LitanyPerson.CONGREGATION, litanyTitle: FtLitanyTitleSeeder.NDOMBO_A_NSAMBUDULU, books: "Nkunga 139:23-24", content: "U Nzambi, bika wamfiongunina ye zaya ntim’ami; untonta ye zaya mambanza nami ye tala, kani ngiena nzila yambi, ye untuadila mu nzila yi ka yimani."),
      LitanyContent(id: 414, person: LitanyPerson.DIRECTOR, litanyTitle: FtLitanyTitleSeeder.NDOMBO_A_NSAMBUDULU, books: "Yoane 13:1", content: "Lutala zola kuna kwatuzodila Se, bu tubikwanga bana ba Nzambi."),
      LitanyContent(id: 415, person: LitanyPerson.CONGREGATION, litanyTitle: FtLitanyTitleSeeder.NDOMBO_A_NSAMBUDULU, books: "II Kolinto 9:15", content: "Bika Nzambi katondwa mu nkailu andi u ka uvovakani."),

      LitanyContent(id: 416, person: LitanyPerson.DIRECTOR, litanyTitle: FtLitanyTitleSeeder.NGIA_MBUDULU_A_MASUMU, books: "Yesaya 55:6-7", content: "Lutomba Yave bu kalenda solulwa; lumbombidila bu kena lukufi.Bika wambi kasisa nzil’andi ye ukondolo lunungu mabanza mandi, bika kabangumuka kwa Yave, buna yandi si kakunlemvukila, ye kwa Nzambi eto, kadi kwa yandi kwena ngiambudulu yayingi."),
      LitanyContent(id: 417, person: LitanyPerson.CONGREGATION, litanyTitle: FtLitanyTitleSeeder.NGIA_MBUDULU_A_MASUMU, books: "Nkunga 100:5", content: "Kadi Mfumu wena wambote, nlemvo andi usikilanga ntangu yi ka yimani ye kinkwikizi kiandi mu mbandu ka mbandu."),
      LitanyContent(id: 418, person: LitanyPerson.DIRECTOR, litanyTitle: FtLitanyTitleSeeder.NGIA_MBUDULU_A_MASUMU, books: "Yesaya 1:18; Yoane I 1:7", content: "Vo masumu meno mena mambwaki bonso ntinta yanango, mana kala mampembe bonso mvula zampembe; vo mena mambwaki bonso nlele wampumbu, mana kala bonso mika miamameme mampembe.Kansi vo tudiatanga mu kia, bonso yandi wena mu kia, buna tuenasana kintuadi, menga ma Yesu, i Mwan’andi, makutuvedisanga mu masumu mamonsono."),
      LitanyContent(id: 419, person: LitanyPerson.CONGREGATION, litanyTitle: FtLitanyTitleSeeder.NGIA_MBUDULU_A_MASUMU, books: "Yoane 11:8-9", content: "Vo tuvovanga, vo ka tuena ye masumu ko, tukukivunanga mu masumu mamonsono.Vo tutambudilanga masumu meto, yandi, wena wakwikama ye walunungu, mu kutuyambudila masumu meto ye kutuvedisa mu mambi mamonsono."),
      LitanyContent(id: 420, person: LitanyPerson.DIRECTOR, litanyTitle: FtLitanyTitleSeeder.NGIA_MBUDULU_A_MASUMU, books: "Zingana 28:13; Mink. 32:1", content: "Uswekanga ntulumunu zandi kana toma ko; kansi utambudidi zo ye sisidi zo una tambula nkenda.Mayangi mena ye ndiena uyambudulu ntulumunu andi ye fukulu sumu diandi."),
      LitanyContent(id: 421, person: LitanyPerson.CONGREGATION, litanyTitle: FtLitanyTitleSeeder.NGIA_MBUDULU_A_MASUMU, books: "Nkunga 32:5", content: "Buna yakuzaikisa sumu diami ye kiafuka nkanu ami ko.Mono yavova: Nzolele ta ntulumunu zami kwa Mfumu; buna ngeye wangiambudila lufuma luasumu diami."),
      LitanyContent(id: 422, person: LitanyPerson.DIRECTOR, litanyTitle: FtLitanyTitleSeeder.NGIA_MBUDULU_A_MASUMU, books: "Yelemia 29:11-13; Miamaniongo 3:40-41", content: "Kadi mono nzeyi mabanza maikulubanzilanga, buvovele Yave, mabanza mayenge, ka makubu ko, mu kuluvana nsukulu yilusilanga vuvu. Bika tuafiongunina ye tonta nzila zeto ye bangumuka kwa Yave. Bika tuazangula ntima mieto ye moko meto Kwa Nzambi mu zulu."),
      LitanyContent(id: 423, person: LitanyPerson.CONGREGATION, litanyTitle: FtLitanyTitleSeeder.NGIA_MBUDULU_A_MASUMU, books: "Nkunga 51:1,10; 139:24", content: "Nzambi unsila nlemvo, bonso bwena wote waku, vinsikisa ntulumunu zami, bonso bwena nlemvo aku waunene.Nzambi, unsa ntima wavedila ye umpana mpeve yamona yalumungu.Ye untuadila um nzila yi ka yimani."),
      LitanyContent(id: 424, person: LitanyPerson.DIRECTOR, litanyTitle: FtLitanyTitleSeeder.NGIA_MBUDULU_A_MASUMU, books: "Kolinto II, 5:21; Loma 6:11", content: "Ndiena walembwa zaya masumu, yandi wansa masumu mu diambu dieto, mpasi tuakala lumungu lua Nzambi mu yandi.Bonso bobo lufwiti kukibanzila mpe, vo luafwila masumu, kansi kwa Nzambi luenina moyo mu Kristo Yesu."),
      LitanyContent(id: 425, person: LitanyPerson.CONGREGATION, litanyTitle: FtLitanyTitleSeeder.NGIA_MBUDULU_A_MASUMU, books: "Efeso 1:7", content: "Mutuena nkudulu mu menga mandi, ngiambudulu antulumunu zeto, bonso bwena kimvwama kianlemvo andi."),
      LitanyContent(id: 426, person: LitanyPerson.DIRECTOR_CONGREGATION, litanyTitle: FtLitanyTitleSeeder.NGIA_MBUDULU_A_MASUMU, books: "Kolinto II, 9:15", content: "Bika Nzambi katondwa mu nkailu andi u ka uvovakani."),

      LitanyContent(id: 427, person: LitanyPerson.DIRECTOR, litanyTitle: FtLitanyTitleSeeder.MVUTUDULU_A_MATONDO, books: "", content: "Tu vana matondo kwa nzambi mu nzaikusu ama sonika mandi kwa beto bantu, mu diambu di bimangu bi wombo bi tawa ai bitu monanga bi ka vanganga kwa beto, makatu vovila muna Mwan’andi Yesu Klisto, N’sadi eto.Yandi Uvangwa nzila eto ya kedika i ya moyo."),
      LitanyContent(id: 428, person: LitanyPerson.CONGREGATION, litanyTitle: FtLitanyTitleSeeder.MVUTUDULU_A_MATONDO, books: "", content: "Matondo kwa Nzambi muna nka yulu andi uka usuki ko."),
      LitanyContent(id: 429, person: LitanyPerson.DIRECTOR, litanyTitle: FtLitanyTitleSeeder.MVUTUDULU_A_MATONDO, books: "", content: "Tu vana matondo kwa Nzambi mu masonama mandi malongo, ma vangwa mwinda kwa malu mami ye ki mu nzil’ami, ma lenda kutu tuadisa kumpulusu ye mu luzingu lukalusuki. Tuku mvutudila mpe matondo mudiambu katu fidisila no una kutu longisa mambu mandi ma nlongo i una kutu songisa mpulusu ya kedika."),
      LitanyContent(id: 430, person: LitanyPerson.CONGREGATION, litanyTitle: FtLitanyTitleSeeder.MVUTUDULU_A_MATONDO, books: "", content: "Matondo kwa Nzambi muna nka yulu andi uka usuki ko."),
      LitanyContent(id: 431, person: LitanyPerson.DIRECTOR, litanyTitle: FtLitanyTitleSeeder.MVUTUDULU_A_MATONDO, books: "", content: "Tu vana matondo kwa Nzambi mu diambu katu tela mbila mvingi tu kota mu kisalu kiandi kwa nlongo i mu kola Nsamuni. Tu mvutidilenu diaka matondo, mu dimbu di kisalu kiandi kwa nlongo tu lembu sadisa nkangu mu nsi etu, ayi tu bundi sanga ma ngolo mu di bundu mu mvangi nsamu wa mbote mu beto i beto."),
      LitanyContent(id: 432, person: LitanyPerson.CONGREGATION, litanyTitle: FtLitanyTitleSeeder.MVUTUDULU_A_MATONDO, books: "", content: "Matondo kwa Nzambi muna nka yulu andi uka usuki ko."),
      LitanyContent(id: 433, person: LitanyPerson.DIRECTOR, litanyTitle: FtLitanyTitleSeeder.MVUTUDULU_A_MATONDO, books: "", content: "Tu vana matondo kwa Nzambi, mu bila katubika ko mu ngindudulu ko, vayi utu bikila lutumu luzabakana, lu Nfumu etu Yesu klistu, utuba ti: “luenda mu nza ya mvimba lwe samuna nsamu wa mbote kwa bantu babosono i utu bikila nsilulu ti”: “tala momo ngiena yeno lumbu biabiosono nate ye mbanunu antangu”."),
      LitanyContent(id: 434, person: LitanyPerson.CONGREGATION, litanyTitle: FtLitanyTitleSeeder.MVUTUDULU_A_MATONDO, books: "", content: "Matondo kwa Nzambi muna nka yulu andi uka usuki ko."),

      LitanyContent(id: 435, person: LitanyPerson.DIRECTOR, litanyTitle: FtLitanyTitleSeeder.MBUTUKULU_A_YESU, books: "", content: "Tu vana kembo kwa Mfumu azi mfumu, wa fidusua kwa bantu mu kutu kula mu masumu, wa vanga nsila mu vanga luzolo luandi."),
      LitanyContent(id: 436, person: LitanyPerson.CONGREGATION, litanyTitle: FtLitanyTitleSeeder.MBUTUKULU_A_YESU, books: "", content: "Zindinga ziazio za ntoto bika zi kembisa Mfumu, Aleluya Ntinu a Nlembami."),
      LitanyContent(id: 437, person: LitanyPerson.DIRECTOR, litanyTitle: FtLitanyTitleSeeder.MBUTUKULU_A_YESU, books: "Luka 1:32 33", content: "Yandisi si kakala waunene ye bikwa muan’a Nkwa kayengele, Mfumu nzambi si kakumvana kiandu kias’andi Davidi, si Kakala Ntinu anzo a yakobi mvu ya mvu, Kimfumu kiandi Ka kiena nsuka ko."),
      LitanyContent(id: 438, person: LitanyPerson.CONGREGATION, litanyTitle: FtLitanyTitleSeeder.MBUTUKULU_A_YESU, books: "", content: "Zindinga ziazio za ntoto bika zi kembisa Mfumu, Aleluya Ntinu a Nlembami."),
      LitanyContent(id: 439, person: LitanyPerson.DIRECTOR, litanyTitle: FtLitanyTitleSeeder.MBUTUKULU_A_YESU, books: "Luka 1:37;45;48-50", content: "Kadi widi diambu nkutu ditukidi kwa Nzambi ka dikondolo lulendo ko. Mayangi kena uleke minu, kadi makamba Mfumu kwa yandi si ma kwenda detila Kadi yandi wamwemi leyinunu luanang´andi wanketo. Kadi tala, tuka bwabu mbandu zazonsono zina Kumbika nkwa mayangi. Kadi nkwa tulendo wampangidi mambu mamanene. Nkumbu andi yena yalongo Nkenda zandi zena mu zimbandu ka zimbandu mu bau baku Mvuminanga."),
      LitanyContent(id: 440, person: LitanyPerson.CONGREGATION, litanyTitle: FtLitanyTitleSeeder.MBUTUKULU_A_YESU, books: "", content: "Zindinga ziazio za ntoto bika zi kembisa Mfumu, Aleluya Ntinu a Nlembami."),
      LitanyContent(id: 441, person: LitanyPerson.DIRECTOR, litanyTitle: FtLitanyTitleSeeder.MBUTUKULU_A_YESU, books: "Luka 1:37;45;48-50", content: "Kadi widi diambu nkutu ditukidi kwa Nzambi ka dikondolo lulendo ko. Mayangi kena uleke minu, kadi makamba Mfumu kwa yandi si ma kwenda detila Kadi yandi wamwemi leyinunu luanang´andi wanketo. Kadi tala, tuka bwabu mbandu zazonsono zina Kumbika nkwa mayangi. Kadi nkwa tulendo wampangidi mambu mamanene. Nkumbu andi yena yalongo Nkenda zandi zena mu zimbandu ka zimbandu mu bau baku Mvuminanga."),
      LitanyContent(id: 442, person: LitanyPerson.CONGREGATION, litanyTitle: FtLitanyTitleSeeder.MBUTUKULU_A_YESU, books: "", content: "Zindinga ziazio za ntoto bika zi kembisa Mfumu, Aleluya Ntinu a Nlembami."),
      LitanyContent(id: 443, person: LitanyPerson.DIRECTOR, litanyTitle: FtLitanyTitleSeeder.MBUTUKULU_A_YESU, books: "EBELEO 1:5-6; YEASYA9:6", content: "Kadi kwa nki ambasi kavovele yonso ntangu: Ngeye u mwan’ami, lumbu kiaki Ikubutidi? Diaka: Mono si yakala se kwa yandi, yandi si kakala mwana kwa mono? Bu kana kotisa diaka semokono va ntoto wena bantu, uvovele: Mbasi zazonsono, za Nzambi bika zansambila. Ka beto tubutulu mwana, tuveno mwana wabakala, luyalu luna kala va mavembo mandi; una bikwa Nkumbu: wangitukulu; Nluengisi, Nzambi yangolo, Se dia mvu ya mvu, Mfumu a yenge."),
      LitanyContent(id: 444, person: LitanyPerson.CONGREGATION, litanyTitle: FtLitanyTitleSeeder.MBUTUKULU_A_YESU, books: "", content: "Zindinga ziazio za ntoto bika zi kembisa Mfumu, aleluya Ntinu a Nlembami."),
      LitanyContent(id: 445, person: LitanyPerson.DIRECTOR, litanyTitle: FtLitanyTitleSeeder.MBUTUKULU_A_YESU, books: "NK 18: 49; 67:5: LM 14:11, NZAIK 7: 9-10", content: "I diau nzolele kuvutudila matondo, Mfumu, mu bapaien ye yimbila mu sika nkumbu aku, kwa ngeye nzambi bika bantu batonda, bika bantu babonsono batonda Kwa ngeye.Kadi dia sonama: Mfumu vovele: Kedika mu moyo ami, makungunu mamonsono si mafukama kwa mono, tudimi tuatonsono si tuazitisa Nzambi. Batelema va luse luakiandu ye va luse lua mvan´ameme, ba vwata mvwatu miampembe ye bakala mandala mu moko mau.Baboka mu ndinga yayinene, bavova: Mpulusu yena kwa Nzambi eto uvwandanga va kiandu ye kwa mwan´ameme."),
      LitanyContent(id: 446, person: LitanyPerson.CONGREGATION, litanyTitle: FtLitanyTitleSeeder.MBUTUKULU_A_YESU, books: "", content: "Zindinga ziazio za ntoto bika zikembisa Mfumu, aleluya Ntinu a Nlembami."),

      LitanyContent(id: 447, litanyTitle: FtLitanyTitleSeeder.MFULUKULU_A_YESU, person: LitanyPerson.DIRECTOR, content: "Nkembo kwa tata, ye Mwana, ye mpeve ya longo."),
      LitanyContent(id: 448, litanyTitle: FtLitanyTitleSeeder.MFULUKULU_A_YESU, person: LitanyPerson.CONGREGATION, content: "Tona muna mbadukulu, ye ntango yayi nate muna ntangu kayi suki. Ameni."),
      LitanyContent(id: 449, litanyTitle: FtLitanyTitleSeeder.MFULUKULU_A_YESU, person: LitanyPerson.DIRECTOR, content: "Lumbu kiantete mu lumingo nsuka nabu bayiza ku ndiamu, banata nzenzo miansunga mibakubika. Babwana tadi dinengomono mu ndiamu. (Luka 24:1-2)"),
      LitanyContent(id: 450, litanyTitle: FtLitanyTitleSeeder.MFULUKULU_A_YESU, person: LitanyPerson.CONGREGATION, content: "Aleluya! (Vobulendaka, bekidi bu kuimbidila)"),
      LitanyContent(id: 451, litanyTitle: FtLitanyTitleSeeder.MFULUKULU_A_YESU, person: LitanyPerson.DIRECTOR, content: "Bau bakatuka nswalu ku ndiamu ye wonnga ye kiese kiakingi, bayungusa mu samuna mo kwa nlonguki miandi. Tala Yesu waba bwabana, wavova: lukala kiese! Kansi bayiza simba malu mandi, basambila. (Luka 28: 8-9)"),
      LitanyContent(id: 452, litanyTitle: FtLitanyTitleSeeder.MFULUKULU_A_YESU, person: LitanyPerson.CONGREGATION, content: "Aleluya!"),
      LitanyContent(id: 453, litanyTitle: FtLitanyTitleSeeder.MFULUKULU_A_YESU, person: LitanyPerson.DIRECTOR, content: "Malia watelama va lueka ku mbasi andiamu, wadila. Bu kadila wayinama mu tala ndiamu. Bavova kwa yandi: Nketo, mu nkiama weti dila? Wavova kwa bau: Kadi bakatudi Mufumu ami, kizeyi ku bantudidi ko. Bu kamana mo vova, wabalumuka manima, wamana Yesu telemene vana, kazaya k o vo i Yesu. Yesu wavova kwa yandi: Nkento, mu nkiama weti dila? Nani tombele? Yandi wabanza, vo i mvati ankuzu, wavova kwa yandi: Mfumu, vo unkatudingi, unkamba ku untudidingi, mpasi yankatula. (Joane 20.11,13-15)"),
      LitanyContent(id: 454, litanyTitle: FtLitanyTitleSeeder.MFULUKULU_A_YESU, person: LitanyPerson.CONGREGATION, content: "Aleluya!"),
      LitanyContent(id: 455, litanyTitle: FtLitanyTitleSeeder.MFULUKULU_A_YESU, person: LitanyPerson.DIRECTOR, content: "Aleluya! Tala, kina lumbu bole mu baú boyenda yendingi ku vota, nkumbu avota emausi, diatatuka ye yelusalemi makuma sabanu mastadia. Bwakala bu bamokasana ye yuvasana, Yandi kebeni yesu wafinama, wayenda yau. (Luka 24:13-15)"),
      LitanyContent(id: 456, litanyTitle: FtLitanyTitleSeeder.MFULUKULU_A_YESU, person: LitanyPerson.CONGREGATION, content: "Aleluya!"),
      LitanyContent(id: 457, litanyTitle: FtLitanyTitleSeeder.MFULUKULU_A_YESU, person: LitanyPerson.DIRECTOR, content: "Tala, kina lumbu bole mu baú bayenda yendingi ku vata, nkumbu avata Emausi, diatatuka ye Yelusalemi makumasambanu mastadia. Bwakala bu bamokasana ye yuvasana, yandi kibeni Yesu Wafinama, wayenda yau. (Luk.24:13-15)"),
      LitanyContent(id: 458, litanyTitle: FtLitanyTitleSeeder.MFULUKULU_A_YESU, person: LitanyPerson.CONGREGATION, content: "Aleluya!"),
      LitanyContent(id: 459, litanyTitle: FtLitanyTitleSeeder.MFULUKULU_A_YESU, person: LitanyPerson.DIRECTOR, content: "Nkokila bu yabwa mu lumbu kiokio, i kiantete mu lumingu, mielo bu miabindama mwakala minloguki mu diambu diawonga mu ba yudayo, buna yesu wayiza, watelama va kati ye bau, wavova, kwa bau: yengue kwa beno! Bu kamana mo vova, wabasonga moko ye lubanzi. Nlonguki miakala kiese, bu bamona Mfumu. (Joane 20:19-20)"),
      LitanyContent(id: 460, litanyTitle: FtLitanyTitleSeeder.MFULUKULU_A_YESU, person: LitanyPerson.CONGREGATION, content: "Aleluya!"),
      LitanyContent(id: 461, litanyTitle: FtLitanyTitleSeeder.MFULUKULU_A_YESU, person: LitanyPerson.DIRECTOR, content: "Simoni Petolo wavova kwa bau: Mwendo ami tamba zimbizi. Bavova kwa yandi: Beto mpe tu kuenda yaku. Bavaika, bakota mu bota; fuku dina ka babaka lekwa ko. Bu kwakia, Yesu watelama va simu, kansi nlonguki ka miazaya ko, vo i Yesu. Buna nlonguki wowo wazolwa kwa Yesu wovova kwa Petelo: I mfumu kwandi! (Yoane 21:3-4-7)"),
      LitanyContent(id: 462, litanyTitle: FtLitanyTitleSeeder.MFULUKULU_A_YESU, person: LitanyPerson.CONGREGATION, content: "Aleluya!"),
      LitanyContent(id: 463, litanyTitle: FtLitanyTitleSeeder.MFULUKULU_A_YESU, person: LitanyPerson.DIRECTOR, content: "Mono ya kala mu Mpeve mu lumbu kia Mfumu, ya wa ndinga yayinene ku nim’ami bonso vo ya mpungi. Momo ya bangumuka mu tal ndinga ya vova kwa mono ya mona umosi ufwanane ye mwa n’amuntu wavwata yunga, wakangwa nsinga wawola mu ntulu andi. Kansi ntu andi ye nsuki biakala biampembe, bonso mika miampembe, bonso mvula zampembe, meso mandi makala bonso nlemo atia. Malu mandi mafwanane ye nsongo yabenguswa kwa tia, nding’andi bonso nding’anlangu miamingi bu yammona ya bwa va malu mandi bonso vo ya fwa, yandi watula koko kwandi kwalubakola va mono, wa vova: kukalani wonga ko; mono i wantete ye wansukunu ye ngiena moyo, ya fwa, kansi tala ngiena moyo mvu miamvu ya mvu, mbeki nsabi zalufwa ye zansi abafwa. (Nzaik. 1:10,12-15;17-18)"),
      LitanyContent(id: 464, litanyTitle: FtLitanyTitleSeeder.MFULUKULU_A_YESU, person: LitanyPerson.CONGREGATION, content: "Aleluya, Aleluya, Aleluya!"),
    ];
  }
}