import 'package:ieca_mobile/enums/hymns_content_code.dart';
import 'package:ieca_mobile/models/_import.dart';
import 'package:ieca_mobile/seeders/fiote/_import.dart';

class FtHymnsGeneral {
  static List<HymnsContent> list = [
    HymnsContent(id: 4500, position: 1, content: "Tuzitisa nge\nBu wavana Yesu\nWatufwila masumu,\nWafuluka mpe.", hymnsNumber: FtHymnsNumberSeeder.GENERAL_1, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 4501, content: "Haleluya! Tukutonda\nTukuzitisanga.\nHaleluya! Tukutonda\nDo, vumusanga.", hymnsNumber: FtHymnsNumberSeeder.GENERAL_1, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 4502, position: 2, content: "Ye Meme dio mpe,\nDiatufwila mambi\nYe wavedisa ntima\nMia bantu nave.", hymnsNumber: FtHymnsNumberSeeder.GENERAL_1, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 4503, position: 3, content: "Tuzitisa Nge\nBu wavana Mpeve,\nYatukienzula tombe\nYe tiemusa mpe.", hymnsNumber: FtHymnsNumberSeeder.GENERAL_1, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 4504, position: 4, content: "Tutondanga Nge\nU nkwa nlemvo wawo,\nWeti sungika beto\nMu nzil´a Dise.", hymnsNumber: FtHymnsNumberSeeder.GENERAL_1, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 4505, position: 5, content: "Do! Vumusanga,\nUtuwesa zola;\nUtulemisa mbazu\nMu ntima mieto.", hymnsNumber: FtHymnsNumberSeeder.GENERAL_1, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 4506, position: 1, content: "Nlongo! Nlongo! Nlongo!\nNzambi ya Tulendo,\nTuna yimbidila ngeye\nNkung’anzitusu;\nNgeye si wakwiza, wena ye\nwakala,\nTata ye Mwana ye Mpeve\nYangongo!", hymnsNumber: FtHymnsNumberSeeder.GENERAL_2, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 4507, position: 2, content: "Nlongo! Nlongo! Nlongo!\nNkangu mia kizulu\nMieti zitisanga nkumbu aku\nmvu yamvu.\nNgeye watufwila, Ngeye bu\nwanunga,\nTambula lendo ye nzitusu\nbiabio!", hymnsNumber: FtHymnsNumberSeeder.GENERAL_2, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 4508, position: 3, content: "Nlongo! Nlongo! Nlongo!\nNtinu a mintinu,\nZulu, ntoto ye mimbu\nbizitisanga Nge!\nNlongo! Nlongo! Nlongo\nZambi ya tulendo\nTata ye Mwana ye Mpeve\nYangolngo.", hymnsNumber: FtHymnsNumberSeeder.GENERAL_2, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 4509, position: 1, content: "Yesu, Ngeye utuvunga,\nKadi Ngeye Mvungudi,\nMu kuwila i mu vunda,\nNgeye mpe i Mvundisi.", hymnsNumber: FtHymnsNumberSeeder.GENERAL_3, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 4510, content: "Tukulanda, tukulanda.\nToma kutulonganga,\nTukulanda, tukulanda,\nToma kutulonganga.", hymnsNumber: FtHymnsNumberSeeder.GENERAL_3, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 4511, position: 2, content: "Mu nzil’aku utunata,\nUtutuamina ku ntu;\nMauzola utusonga,\nTuna sisa bieto fu.", hymnsNumber: FtHymnsNumberSeeder.GENERAL_3, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 4512, position: 3, content: "Beto tu mameme maku,\nNzila yituavengama,\nNgeye kala Mvungi eto,\nNgeye utusungika.", hymnsNumber: FtHymnsNumberSeeder.GENERAL_3, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 4513, position: 4, content: "Ngeye Yesu watutomba,\nNgeye watuvulusa,\nVuvu teuna mpe mu Ngeye,\nNgeye si wayala nza.", hymnsNumber: FtHymnsNumberSeeder.GENERAL_3, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 4514, position: 1, content: "Bika tuayimbila:\nAleluya amen!\nKunda ye zitisa,\nAleluya amen!\nKintuadi tuna sa\nMu kunda Yehova\nNzambi wazangama\nAleluya amen!", hymnsNumber: FtHymnsNumberSeeder.GENERAL_4, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 4515, position: 2, content: "Nzambi uyalanga.\nAleluya amen!\nYenge kavananga.\nAleluya amen!\nLuyalungunu bwe?\nUntoto, keti bwe!\nNsi ye kayengele!\nAleluya amen!", hymnsNumber: FtHymnsNumberSeeder.GENERAL_4, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 4516, position: 3, content: "Nkembo kwa Nzambi se!\nAleluya amen!\nKwa Mpeve nkembo npe!\nAleluya amen!\nYesu i Mvulusi\nWayiza fwa va nti,\nWabedisa mambi.\nAleluya amen!", hymnsNumber: FtHymnsNumberSeeder.GENERAL_4, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 4517, position: 4, content: "Nzambi tukembela:\nAleluya amen!\nYesu tuzitisa:\nAleluya amen!\nMfumu yanzitusi\nMvulusi mvu ya mvu.\nSe, mwana, Mpeve mpe.\nAleluya amen!", hymnsNumber: FtHymnsNumberSeeder.GENERAL_4, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 4518, position: 1, content: "Yadisa Ntinu wo\nMu koko kwa Dise;\nKu zulu nkung’anzitusu\nKwa Yesu tuna wa.\nMwel’ami sikama,\nMu Yesu yimbila\nWafwila beto, Ntinu wo,\nLunyandisa babo.", hymnsNumber: FtHymnsNumberSeeder.GENERAL_5, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 4519, position: 2, content: "Yadisa Ntinu wo,\nKiazolwa kia Dise,\nMu moko mandi fu biena,\nTutoma zitisa.\nKa mpofi mosi ko\nYilembwa yinama;\nBafukanga bizizi biau,\nBankembilanga mpe.", hymnsNumber: FtHymnsNumberSeeder.GENERAL_5, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 4520, position: 3, content: "Yadisa Ntinu wo\nWamoyo mvu ya mvu,\nWafuluka ye zituswa\nMu kututanina.\nWafwa ye fuluka,\nSi tuakunzitisa;\nWavana beto moyo mpe,\nLufwa kabedisa.", hymnsNumber: FtHymnsNumberSeeder.GENERAL_5, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 4521, position: 4, content: "Yadisa Ntinu wo,\nWazulu, ye Dise,\nWavana Mpeve kwa babo,\nWatambulwa kwa Se.\nSi tuakunkembela\nMu mvu miamionsono;\nMu zola watuzalanga;\nTutoma sambila.", hymnsNumber: FtHymnsNumberSeeder.GENERAL_5, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 4522, position: 1, content: "Kiese ngieti mona bwabu\nDiatanga ye Mvulusi,\nDiatanga mu tambi biandi\nKonso kukadiatanga.", hymnsNumber: FtHymnsNumberSeeder.GENERAL_6, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 4523, content: "Diatanga, diatanga,\nDiatanga ye Yesu,\nLumbu ki, mwamonso,\nDiatanga ye Yesu.", hymnsNumber: FtHymnsNumberSeeder.GENERAL_6, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 4524, position: 2, content: "Diatanga ye Mfumu Yesu,\nYekamanga Yandi mpe,\nKonso kukatuaminanga,\nKuna ngina namanga.", hymnsNumber: FtHymnsNumberSeeder.GENERAL_6, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 4525, position: 3, content: "Diatanga ye Yesu Klisto,\nLokula ka lokula;\nMiongo miena nda mu maka\nYe tulenda maka mio.", hymnsNumber: FtHymnsNumberSeeder.GENERAL_6, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 4526, position: 4, content: "Yesu utufinamanga\nLumbu ki ye mvu ka mvu;\nDiatanga mu tambi biaku,\nDiatanga ye Nge’ mwamo.", hymnsNumber: FtHymnsNumberSeeder.GENERAL_6, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 4527, position: 1, content: "Luzitisa Yesu Klisto\nDimeme dia Zambi.\nYandi i Mvulusi eto,\nLunsika, Mfumu anza!", hymnsNumber: FtHymnsNumberSeeder.GENERAL_7, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 4528, position: 2, content: "Zinsi zazo, bantu babo,\nLusadila Yesu.\nLumvana ntima mieno,\nLunsika, Mfumu anza!", hymnsNumber: FtHymnsNumberSeeder.GENERAL_7, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 4529, position: 3, content: "Lunzitisa, lunzitisa,\nBeno luavuluswa,\nMfumu eno, lunyandisa,\nLunsika, Mfumu anza!", hymnsNumber: FtHymnsNumberSeeder.GENERAL_7, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 4530, position: 4, content: "Mu zulu ntindi kadila\nYe bau bafonguswa\nYe sadila ye zitisa\nYesu, i Mfumu anza!", hymnsNumber: FtHymnsNumberSeeder.GENERAL_7, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 4531, position: 1, content: "Nani obanza o fuku dina vo\nnene wabutuka?\nNtemo ampwena se watema:\nOwu uzal´e nza\nMbasi z’e zulu kembela dio,\nNzambi o Se betondanga\nMwan’a Nzambi, Mwan’ a\nmuntu.\nWau kabutuka.", hymnsNumber: FtHymnsNumberSeeder.GENERAL_8, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 4532, content: "Wiza, wiza nza kungwisa,\nNsamu wau uzenzanga\nO Mesi’o Ntinu a nkembo.\nWau kabutuka", hymnsNumber: FtHymnsNumberSeeder.GENERAL_8, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 4533, position: 2, content: "Kiese kingi ayiz’atwasi\nE nza se yiyanzanga\nYesu i Ntwadisi a Walakazi\nSe tuyambana,\nNyazi a nsongi, Nkambami\nmpe\nBika wau twankembela;\nMfumu Yesu Mpungu-wete\nWau kabutuka.", hymnsNumber: FtHymnsNumberSeeder.GENERAL_8, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 4534, position: 3, content: "An’ayiva y’an’avezwa\nYesu yau kekokanga,\nSula-mabi, kondwa-nkenda,\nYau basikila.\nO ungudi uwokalanga,\nVana nzo se zolana\nMvangi a ngemba, mpungunkaka\nWau kabutuka.", hymnsNumber: FtHymnsNumberSeeder.GENERAL_8, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 4535, position: 4, content: "Bika nsangu yend’a\nmwangwa\nMfumu Yesu wayal’e nza\nAkwa ntantu se baseva,\nYesu i Konka-nsa.\nO luvevuku mpe katwasa,\nZavo Yandi otondwanga,\nO Mesia Mpungu-zola\nWau kabutuka.", hymnsNumber: FtHymnsNumberSeeder.GENERAL_8, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 4536, position: 1, content: "Ntangu bu yadimuka,\nBantu mpe balekanga.\nMbasi zau zamonika,\nNkunga wau bayimbila.", hymnsNumber: FtHymnsNumberSeeder.GENERAL_9, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 4537, content: "Glo…………………ria\nIn excelsis Deo\nGlo…………………ria\nIn excelsis De………..o", hymnsNumber: FtHymnsNumberSeeder.GENERAL_9, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 4538, position: 2, content: "Nsamu ubasamuna\nMbutulu a Mvulusi.\nNzambi si kakembuswa,\nBantu yangalalanga.", hymnsNumber: FtHymnsNumberSeeder.GENERAL_9, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 4539, position: 1, content: "Zola kwa Se dieto, Nzambi,\nLutanga kwakonsono,\nFiduswa va ntoto eto;\nNkenda zandi kembisa.\nYesu, Ngeye wena zola,\nKwakunene ye nave,\nKundanga mu beto bantu,\nKoteti mu ntim’ami.", hymnsNumber: FtHymnsNumberSeeder.GENERAL_10, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 4540, position: 2, content: "Vumunina Mpeve kwaku\nNtima mio miavinzuka;\nYe difwa, do, utuvana,\nMvundulu yasilwa yo;\nVunza zola kwa masumu;\nAlafa ye Omega,\nNsukulu a minu kieto,\nVana ntima ntanunu.", hymnsNumber: FtHymnsNumberSeeder.GENERAL_10, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 4541, position: 3, content: "Wiza, Yave, tanina ye\nVana moyo mvu ya mvu,\nVutuketi nswalu beni\nKutusisa diaka ko!\nNge’ tuzola vo wakunda\nNitu zeto mu nlongo;\nSambila ye toma sika,\nNge’ mu zola kwaku ko.", hymnsNumber: FtHymnsNumberSeeder.GENERAL_10, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 4542, position: 4, content: "Nsemono yampa yavewa\nLungisa yo mu mono,\nBika ngina toma songa\nZola kwaku kwa banka’;\nTuka nkembo kuna nkembo\nNunguswanga mu mamo.\nYe mu zulu ngina vana\nKoloa kiami kwa Yesu.", hymnsNumber: FtHymnsNumberSeeder.GENERAL_10, typeStanza: HymnsContentCode.VERSE),
  ];
}