import 'package:ieca_mobile/enums/_import.dart';
import 'package:ieca_mobile/models/_import.dart';
import 'package:ieca_mobile/seeders/_import.dart';

class HymnsDeathOfTheBeliever {
  static List<HymnsContent> list = [
    HymnsContent(id: 1347, position: 1, content: "Amigos meus, é doce\nTermos encontro aqui feliz,\nMas sempre vem a hora\nTriste, em que \"adeus\" se diz!", hymnsNumber: HymnsNumberSeeder.DEATH_OF_THE_BELIEVER_319, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1348, content: "Jamais se diz adeus alí,\nJamais se diz adeus.\nNo eterno lar de gozo e paz,\nJamais se diz adeus.", hymnsNumber: HymnsNumberSeeder.DEATH_OF_THE_BELIEVER_319, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 1349, position: 2, content: "Quão doce é pensarmos\nQue, quando em glória o céu se abrir,\nNós juntos vivermos,\nCristo virá nos reunir.", hymnsNumber: HymnsNumberSeeder.DEATH_OF_THE_BELIEVER_319, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1350, position: 3, content: "Jamais virá tristeza,\nNem despedida atroz, nem dor,\nSomente paz e gozo,\nGrata união e eterno amor.", hymnsNumber: HymnsNumberSeeder.DEATH_OF_THE_BELIEVER_319, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 1351, position: 1, content: "Estando com Jesus\nSempre eu firme sou.\nSua Palavra é vida\nE alegria sem fim.", hymnsNumber: HymnsNumberSeeder.DEATH_OF_THE_BELIEVER_320, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1352, position: 2, content: "No meu corpo carnal\nPouco tempo eu estou;\nNo dia em que eu de lá sair,\nNa glória chegarei.", hymnsNumber: HymnsNumberSeeder.DEATH_OF_THE_BELIEVER_320, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1353, position: 3, content: "Bem firme estou com Jesus,\nMarchando para o céu,\nE hoje a lida terminei,\nDescanso encontrei.", hymnsNumber: HymnsNumberSeeder.DEATH_OF_THE_BELIEVER_320, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1354, position: 4, content: "Desgostos e pesares,\nNo mundo suportei,\nAnseio estar lá no céu\nCom Cristo meu Senhor.", hymnsNumber: HymnsNumberSeeder.DEATH_OF_THE_BELIEVER_320, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 1355, position: 1, content: "Salvo por Jesus Cristo,\nTenho perfeita paz.\nA comunhão com Ele\nToda aflição desfaz.\nEle me deu certeza\nDa minha salvação.\nQue de inefável gozo\nEnche meu coração.", hymnsNumber: HymnsNumberSeeder.DEATH_OF_THE_BELIEVER_321, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1356, content: "Salvo por Jesus Cristo\nTenho perfeita paz;\nA comunhão com Ele\nToda a aflição desfaz.", hymnsNumber: HymnsNumberSeeder.DEATH_OF_THE_BELIEVER_321, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 1357, position: 2, content: "Cristo é a minha vida\nFonte de doce amor.\nEle me tira a mágoa,\nTodo o pesar e a dor.\nE se sofrer a prova,\nMui fácil me será,\nE se verter o pranto,\nLogo ele o enxugará.", hymnsNumber: HymnsNumberSeeder.DEATH_OF_THE_BELIEVER_321, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1358, position: 3, content: "E Passarei a noite\nCom Ele sem temor,\n'Até que amanhece o dia\nDe perenal fulgor.\nMagnificante e Santo\nVê-lo-ei a me fitar\n“E na mansão de glória\nVou com Jesus reinar”!", hymnsNumber: HymnsNumberSeeder.DEATH_OF_THE_BELIEVER_321, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 1359, position: 1, content: "Dormir em Cristo, sim, é bom,\nÉ sono sem perturbação;\nDescanso, mui precioso dom;\nDo mundo dá libertação.", hymnsNumber: HymnsNumberSeeder.DEATH_OF_THE_BELIEVER_322, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1360, position: 2, content: "Dormindo em Cristo, o nosso irmão,\nJá livre está de todo o mal;\nNão sofre dor nem tentação;\nJá goza a vida perenal.", hymnsNumber: HymnsNumberSeeder.DEATH_OF_THE_BELIEVER_322, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1361, position: 3, content: "Dormindo em Cristo, o nosso irmão\nBem junto está do Salvador.\nJá goza a santa comunhão\nCom os remidos do Senhor.", hymnsNumber: HymnsNumberSeeder.DEATH_OF_THE_BELIEVER_322, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1362, position: 4, content: "Dormir em Cristo é estar em paz,\nO despertar, supremo bem;\nPois Ele transportar-nos faz\nAo gozo divinal de além.", hymnsNumber: HymnsNumberSeeder.DEATH_OF_THE_BELIEVER_322, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 1363, position: 1, content: "Morte não há jamais,\nAos que pertencem à Deus.\nAlegres, pois descansarão\nNos braços de Jesus.", hymnsNumber: HymnsNumberSeeder.DEATH_OF_THE_BELIEVER_323, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1364, position: 2, content: "Morte não há jamais,\nPor quem Jesus morreu.\n'Inda que o corpo seja pó,\nNão finda a alma aqui.", hymnsNumber: HymnsNumberSeeder.DEATH_OF_THE_BELIEVER_323, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1365, position: 3, content: "Morte não há jamais,\nP’ra Quem se arrepender.\nDa morte a vida nascerá\nNo lar celestial.", hymnsNumber: HymnsNumberSeeder.DEATH_OF_THE_BELIEVER_323, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1366, position: 4, content: "Morte não há jamais\nP'ra quem Jesus seguir,\nFeliz com Ele entrará\nNo eterno e bom país.", hymnsNumber: HymnsNumberSeeder.DEATH_OF_THE_BELIEVER_323, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 1367, position: 1, content: "Quando o coração lesado\nCom os fardos de tristezas,\nOu se a solidão é imensa,\nO cajado é Jesus.", hymnsNumber: HymnsNumberSeeder.DEATH_OF_THE_BELIEVER_324, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1368, position: 2, content: "Se desfalecendo vamos\nDe pesares e pecados,\nOu se formos desprezados,\nO cajado é Jesus.", hymnsNumber: HymnsNumberSeeder.DEATH_OF_THE_BELIEVER_324, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1369, position: 3, content: "Quando o coração se aflige\nPor receios e temores,\nNão verguemos perturbados,\nO cajado é Jesus.", hymnsNumber: HymnsNumberSeeder.DEATH_OF_THE_BELIEVER_324, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1370, position: 4, content: "Quando amigos nos desertam\nE a morte nos chegar,\nFé na vida além da morte,\nNosso ser é Jesus.", hymnsNumber: HymnsNumberSeeder.DEATH_OF_THE_BELIEVER_324, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 1371, position: 1, content: "Adeus irmão, te desejamos bem,\nPois, nesta hora, no repouso estás.\nTodo o delírio hoje terminou.\nAdeus, irmão, adeus.", hymnsNumber: HymnsNumberSeeder.DEATH_OF_THE_BELIEVER_325, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1372, position: 2, content: "Até findar toda a escuridão,\nAté a solidão terminar,\nAté nos reencontrarmos lá no céu,\nAdeus, irmão, adeus.", hymnsNumber: HymnsNumberSeeder.DEATH_OF_THE_BELIEVER_325, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1373, position: 3, content: "Até que venha o Senhor Jesus,\nAté Sua glória se manifestar,\nAté que ressuscitem os cristãos,\nAdeus, irmão, adeus.", hymnsNumber: HymnsNumberSeeder.DEATH_OF_THE_BELIEVER_325, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1374, position: 4, content: "Até que todo o choro terminar,\nAté vitória sobre a morte houver,\nAté que juntos lá nos altos ceus,\nAdeus, irmão, adeus.", hymnsNumber: HymnsNumberSeeder.DEATH_OF_THE_BELIEVER_325, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 1375, position: 1, content: "Oh! dá-me o voar da fé\nP'ra eu no céu entrar\nE ver a glória que há lá,\nQue os crentes vão gozar.", hymnsNumber: HymnsNumberSeeder.DEATH_OF_THE_BELIEVER_326, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1376, position: 2, content: "Muitos são os crentes\nQue me esperam lá.\nSentados em tronos já.\nMuitas são as vozes\nQue me chamam cá,\nPara com fervor cantar.\nChamam-me para lá,\nChamam-me para lá,\nPara a nova Jerusalém!", hymnsNumber: HymnsNumberSeeder.DEATH_OF_THE_BELIEVER_326, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 1377, position: 3, content: "Aqui sofriam dores e ais,\nChoro e aflição.\nVencendo a carne e Satanás,\nQuerendo a redenção.", hymnsNumber: HymnsNumberSeeder.DEATH_OF_THE_BELIEVER_326, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1378, position: 4, content: "Quem tal vitória vos deu?\nDize-me, ó irmão.\n“Aquele que na cruz morreu\nP'ra dar-nos salvação”.", hymnsNumber: HymnsNumberSeeder.DEATH_OF_THE_BELIEVER_326, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 1379, position: 1, content: "Importará ao Senhor Jesus\nQue eu viva no mundo a ter\nO meu coração cheio de aflição?\nSentirá meu triste viver?", hymnsNumber: HymnsNumberSeeder.DEATH_OF_THE_BELIEVER_327, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1380, position: 2, content: "Oh, sim, eu sei, Jesus bem vê\nO que eu estou a sofrer;\nEm cruel peleja, pavor, inveja,\nJesus me quer valer.", hymnsNumber: HymnsNumberSeeder.DEATH_OF_THE_BELIEVER_327, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 1381, position: 3, content: "Importará ao Senhor Jesus\nQue eu viva com dissabor?\nPor andar sem luz, que me vem da cruz,\nSentirá, pois, meu Salvador?", hymnsNumber: HymnsNumberSeeder.DEATH_OF_THE_BELIEVER_327, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1382, position: 4, content: "Importará ao Senhor Jesus\nQue eu caia na tentação?\nSe Satã puder minha fé vencer\nMe dará de novo o perdão?", hymnsNumber: HymnsNumberSeeder.DEATH_OF_THE_BELIEVER_327, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1383, position: 5, content: "Importará ao Senhor Jesus\nSe a morte ferir meu lar?\nE que diga adeus aos queridos meus,\nSentirá Jesus? Pesará?", hymnsNumber: HymnsNumberSeeder.DEATH_OF_THE_BELIEVER_327, typeStanza: HymnsContentCode.VERSE)
  ];
}