import 'package:ieca_mobile/enums/_import.dart';
import 'package:ieca_mobile/models/_import.dart';
import 'package:ieca_mobile/seeders/_import.dart';

class HymnsHolySpirit{
  static List<HymnsContent> list = [
    HymnsContent(id: 345, position: 1, content: "Ó divinal Instruidor\nVem revelar-nos o Senhor.\nAs nossas almas vem encher\nDa Tua luz, do teu poder.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_83, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 346, position: 2, content: "Ó inefável Preceptor,\nCom Teu altíssimo favor\nVem nossas almas inflamar,\nNo santo ardor do Teu altar.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_83, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 347, position: 3, content: "Ó vem, Espírito veraz,\nA escuridão em nós desfaz,\nRevela ao mundo a Tua luz,\nA redenção que vem da cruz.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_83, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 348, position: 1, content: "Ó Espírito Benigno,\nSuplicamos teus dons;\nMas de todos eles bons!\nDá-nos o amor!", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_84, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 349, position: 2, content: "Fale eu língua angelical,\nTenha eu dom de profecia;\nNada me aproveitaria,\nSó com o amor.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_84, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 350, position: 3, content: "A inveja e a soberba\nEm nós sejam expungidos!\nE a calúnia também cesse;\nDá-nos o amor.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_84, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 351, position: 4, content: "O amor não é invejoso,\nNão se porta com indecência,\nTudo sofre tudo espera;\nDá-nos o amor.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_84, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 352, position: 5, content: "O amor quando é real\nÉ constante é leal;\nÓ Espírito Benfeitor\nDá-nos o amor.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_84, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 353, position: 1, content: "Espírito vivificante,\nQue renovas corações,\nCura-me pois estou cego;\nPara que eu veja melhor.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_85, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 354, position: 2, content: "Espírito vivificante,\nA alma é tão vacilante;\nDá robustez e vigor.\nFaz-me viver com fulgor.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_85, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 355, position: 3, content: "Espírito vivificante\nTu, ó constante renovador,\nLivra-me do perigo,\nPois o mundo renego.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_85, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 356, position: 4, content: "Espírito vivificante;\nTorna já cantante\nMeu aflito coração;\nViva eu com rectidão.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_85, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 357, position: 1, content: "Dirija-me Espírito Santo:\nOnde quer que seja irei.\nTua mão me sustenta.\nNo sombrio vale da morte,\nEspanto nenhum terei;\nTeus ensinos escutarei.\nOuço tua voz dizendo:\nEu dirijo-te não temas.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_86, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 358, position: 2, content: "Meu Senhor, fica comigo;\nQue eu vá onde tu queiras.\nNão me rechaces, contrito estou,\nO inimigo me cercou.\nVacilante ando na terra,\nLentamente caindo vou.\nOuço tua voz dizendo:\nEu dirijo-te não temas.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_86, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 359, position: 3, content: "Vou contigo, mais que vencedor:\nA batalha final vencerei.\nTranquilize meu coração,\nPois o inimigo me laça.\nEntão chegarei à mansão,\nLá na eterna Caná.\nOuço tua voz dizendo;\nEu dirijo-te não temas.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_86, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 360, position: 1, content: "Espírito Poderoso!\nPara a minha caminhada,\nDá-me teu poder glorioso,\nPara marchar vitorioso;\nEncoraja-me a avançar,\nMeu Senhor, meu protector", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_87, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 361, position: 2, content: "Espírito verdadeiro!\nVem, submeta meu coração.\nTua vontade sempre faça.\nMeus intentos controlar.\nSe p'ra o mal eu sou tentado,\nMeu escudo é o Teu amor.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_87, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 362, position: 3, content: "Espírito de amor,\nCaridoso anelo ser.\nMinhas obras manifestem, O amor do Salvador.\nE em todo o meu falar\nTranspareça a Tua voz.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_87, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 363, position: 4, content: "Espírito autor da paz,\nDe mim, ofensa dissipar.\nOnde houver violência e ódio,\nManifeste o Teu amor.\nSempre eu lembre o\nTeu perdão Que no calvário concedeu.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_87, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 364, position: 1, content: "Vem, Espírito divino,\nGrande Ensinador!\nVem! descobre às nossas almas\nCristo, o Salvador.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_88, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 365, content: "Mestre, Mestre,\nOuve com favor!\nEm poder e graça insigne\nObre o Teu amor!", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_88, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 366, position: 2, content: "Vem! demole os alicerces\nDa enganosa paz,\nAos errados concedendo\nSalvação veraz!", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_88, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 367, position: 3, content: "Vem! Reveste a tua Igreja\nDe energia e luz!\nVem! Atrai os desviados\nAo Senhor Jesus!", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_88, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 368, position: 4, content: "Maravilhas soberanas\nOutros povos vêem;\nOh! derrama a mesma bênção\nSobre nós também.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_88, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 369, position: 1, content: "Na despedida, o Senhor\nSeu benefício deu;\nEspírito Santo p’ra ser\nAuxiliador.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_89, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 370, position: 2, content: "Enviou-O para nos tornar,\nJustiça do Deus Pai;\nImporta que tenhamos fé\nNo Seu poder.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_89, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 371, position: 3, content: "Ele também nos dá poder,\nPara vencer o mal;\nA inteligência Ele nos dá,\nDo Reino Seu.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_89, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 372, position: 4, content: "Se sobrevém desvio em nós,\nNo trilho do viver,\nSua bondade e amor,\nNos faz voltar.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_89, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 373, position: 5, content: "Ó poderoso Espírito,\nPurificar-nos vem;\nLembrar-nos vem da morte atroz,\nDo Salvador.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_89, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 374, position: 6, content: "Vem atear o fogo em nós,\nJustiça e amor;\nTornando nossos corações\nTua habitação.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_89, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 375, position: 1, content: "Vem, Espírito divino,\nPoderoso Benfeitor,\nA minha alma fraca e pobre\nRevelar divino amor.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_90, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 376, position: 2, content: "Vem-me descobrir a glória\nDe Jesus, o Salvador,\nQue morrendo, trouxe ao mundo\nO poder de vero amor.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_90, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 377, position: 3, content: "Com vivificante graça,\nE com todo o Teu fulgor,\nVem raiar em minha vida\nVerdadeiro e puro amor.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_90, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 378, position: 4, content: "Que eu aos tristes e cansados,\nCarregados de temor,\nPossa por divina graça,\nMediar divino amor.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_90, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 379, position: 1, content: "Ó Divino Preceptor!\nBom, fiel consolador!\nFaze agora em todos nós\nPoderosa a Tua voz.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_91, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 380, position: 2, content: "Grande e vero instruidor,\nCom celestial favor,\nMostra como te adorar\nE este culto a Deus prestar.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_91, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 381, position: 3, content: "Santo Espírito de Deus,\nEnche de fervor os Teus,\nPara entoarem o louvor\nDe Jesus, o Salvador.", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_91, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 382, position: 4, content: "Vem, Consolador veraz;\nDá-nos firme, estável paz.\nNo poder da Tua luz\nGuia as almas a Jesus!", hymnsNumber: HymnsNumberSeeder.HOLY_SPIRIT_91, typeStanza: HymnsContentCode.VERSE),
  ];
}