import 'package:ieca_mobile/enums/_import.dart';
import 'package:ieca_mobile/models/_import.dart';
import 'package:ieca_mobile/seeders/_import.dart';

class HymnsNewYear {
  static List<HymnsContent> list = [
    HymnsContent(id: 1508, position: 1, content: "Mais uma vez Deus deu-nos\nUm belo e Ano Novo.\nLouvemos Sua graça,\nE Seu grande poder.\nNo Ano já passado,\nGuiou-nos muito bem.\nE neste Novo Ano,\nSerá o nosso guia.", hymnsNumber: HymnsNumberSeeder.NEW_YEAR_356, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1509, position: 2, content: "Se nos dias vindouros,\nHouver perturbações.\nNão temeremos nunca,\nTeremos esperança.\nPois temos a promessa,\nDe nosso Amado Cristo\nQue diz: \"venci o mundo,\nVós também vencereis\".", hymnsNumber: HymnsNumberSeeder.NEW_YEAR_356, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1510, position: 3, content: "Deus nunca desampara,\nOs que confiam n'Ele.\nProtege-os dos perigos,\nDos vendavais do mundo.\nMesmo nossas veredas,\nPassem por tentações.\nNele confiaremos,\nNada nos ferirá.", hymnsNumber: HymnsNumberSeeder.NEW_YEAR_356, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1511, position: 4, content: "O Senhor já consumou,\nTodo o Seu labor.\nPortanto não tenhamos,\nPreguiça ou temor.\nDevemos dedicar-nos,\nAlegres e esperançosos.\nP'ra que este Novo Ano,\nSeja o melhor de todos.", hymnsNumber: HymnsNumberSeeder.NEW_YEAR_356, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 1512, position: 1, content: "O ano que passou,\nTal como um sonho, foi,\nPois, Deus cuidou-me com paixão,\nNão tive dano algum.", hymnsNumber: HymnsNumberSeeder.NEW_YEAR_357, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1513, position: 2, content: "P´ra o ano que passou,\nPerdão eu peço à Deus.\nPelos pecados que cometi,\nVem me purificar.", hymnsNumber: HymnsNumberSeeder.NEW_YEAR_357, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1514, position: 3, content: "No ano que ali vem,\nMeu Deus dá-me fé.\nQue eu prossiga a me inclinar\nP'ra justiça e p'ra paz.", hymnsNumber: HymnsNumberSeeder.NEW_YEAR_357, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1515, position: 4, content: "Ao terminar ó Deus\nMinha jornada aqui,\nReceba-me no país de luz\nQue nunca apagará.", hymnsNumber: HymnsNumberSeeder.NEW_YEAR_357, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 1516, position: 1, content: "Rompe a aurora, vai-se embora\nMais um ano de labor;\nNão temamos, prossigamos,\nA lutar com mais ardor.", hymnsNumber: HymnsNumberSeeder.NEW_YEAR_358, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1517, content: "O ano findo, nunca mais veremos;\nO ano novo hoje recebemos!\nVê, vê, o belo dom que Deus nos dá!", hymnsNumber: HymnsNumberSeeder.NEW_YEAR_358, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 1518, position: 2, content: "Cada dia, Cristo, o Guia,\nNos renove o coração;\nTemos gozo, bom repouso,\nConfiando em Sua mão.", hymnsNumber: HymnsNumberSeeder.NEW_YEAR_358, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1519, position: 3, content: "Do pecado resgatados,\nPertencemos a Jesus;\nNova vida, santa lida,\nTemos nós por Sua cruz.", hymnsNumber: HymnsNumberSeeder.NEW_YEAR_358, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1520, position: 4, content: "Hinos santos entoemos\nE louvemos ao Senhor!\nVem, do arcano mais um ano\nQue anuncia Seu favor!", hymnsNumber: HymnsNumberSeeder.NEW_YEAR_358, typeStanza: HymnsContentCode.VERSE),
  ];
}