      LitanyContent(id: 453, person: LitanyPerson.CONGREGATION, litanyTitle: KmLitanyTitleSeeder.KUMUTU_UALA_KU_MUTUAMESA_O_MAKA_NA_JEHOVA, books: "Jisalamu 46:2", content: "Kienhiki, ki tu kala ni uoma, né-mbe oxi izamba, mba nilundu iê i suetuka katé mu kaxi ka musanza ua kalunga."),
      LitanyContent(id: 454, person: LitanyPerson.DIRECTOR, litanyTitle: KmLitanyTitleSeeder.KUMUTU_UALA_KU_MUTUAMESA_O_MAKA_NA_JEHOVA, books: "Jisal. 130:7", content: "Dielela kua Jihova, kuma kua Jihova sai-ku jihenda ni kulenduka; kua muene kuala eu kubhanduluka kua muene o kizuelu kie kiene ki nga dielela."),
      LitanyContent(id: 455, person: LitanyPerson.CONGREGATION, litanyTitle: KmLitanyTitleSeeder.KUMUTU_UALA_KU_MUTUAMESA_O_MAKA_NA_JEHOVA, books: "Jisál. 130:5", content: "Nga kinga Jihova; muenhu uami ua kinga ni kutala kua muene o kizuelu kiê kiene ki nga dielela."),
      LitanyContent(id: 456, person: LitanyPerson.DIRECTOR, litanyTitle: KmLitanyTitleSeeder.KUMUTU_UALA_KU_MUTUAMESA_O_MAKA_NA_JEHOVA, books: "Jisál. 34:3", content: "Ngi bandienu, akuetu, mu jingisa Jihova; tu kuatele kumoxi mu tumbula dijina diê."),
      LitanyContent(id: 457, person: LitanyPerson.CONGREGATION, litanyTitle: KmLitanyTitleSeeder.KUMUTU_UALA_KU_MUTUAMESA_O_MAKA_NA_JEHOVA, books: "Jisál. 96:4", content: "Kuma Jihova munéne, ua tokala ku mu ximana kiavulu."),
      LitanyContent(id: 458, person: LitanyPerson.DIRECTOR, litanyTitle: KmLitanyTitleSeeder.KUMUTU_UALA_KU_MUTUAMESA_O_MAKA_NA_JEHOVA, books: "Jisál. 103:8", content: "Jihova ua lenduka, mukua kisambu, ukokoloka o kutema, o jihenda jê ji vula."),
      LitanyContent(id: 459, person: LitanyPerson.CONGREGATION, litanyTitle: KmLitanyTitleSeeder.KUMUTU_UALA_KU_MUTUAMESA_O_MAKA_NA_JEHOVA, books: "Jisál 103:10", content: "Ka tu bhanga kala kia fuama mu ikuma ietu, ka tu kaxtikala kala kia tokala ituxi ietu."),
      LitanyContent(id: 460, person: LitanyPerson.DIRECTOR, litanyTitle: KmLitanyTitleSeeder.KUMUTU_UALA_KU_MUTUAMESA_O_MAKA_NA_JEHOVA, books: "Jisál. 103:11", content: "Mukonda kia di leka maúlu ku thandu dia xi, kiene kia soko jihenda jê já kuatela ió oso a mu kala ni uoma."),
      LitanyContent(id: 461, person: LitanyPerson.CONGREGATION, litanyTitle: KmLitanyTitleSeeder.KUMUTU_UALA_KU_MUTUAMESA_O_MAKA_NA_JEHOVA, books: "Jisál. 103:12", content: "Tunde kuthund’elele katé klumazanga, mukunâ ni dikanga dia texe o ikuma ietu."),
      LitanyContent(id: 462, person: LitanyPerson.DIRECTOR, litanyTitle: KmLitanyTitleSeeder.KUMUTU_UALA_KU_MUTUAMESA_O_MAKA_NA_JEHOVA, books: "Jisabhu 3:5", content: "Dielela kua Jihova ni muxima ué uoso; kana kudielela ngó o kilunji kié."),
      LitanyContent(id: 463, person: LitanyPerson.CONGREGATION, litanyTitle: KmLitanyTitleSeeder.KUMUTU_UALA_KU_MUTUAMESA_O_MAKA_NA_JEHOVA, books: "Jisál 139: 23-24", content: "Nzambi ngi solokote, u ngìjue muxima moxi; ngi lole, u ngi tongolole o ilunji ioso; ngi tangele, se ua ngi mono ngala kuila muengi; u ngi kutukise mu kikoka ki nangenena."),
      LitanyContent(id: 464, person: LitanyPerson.DIRECTOR, litanyTitle: KmLitanyTitleSeeder.KUMUTU_UALA_KU_MUTUAMESA_O_MAKA_NA_JEHOVA, books: "I Izuá 3:1", content: "Talesenu kia kulu o henda ia tu idikiza Tata! O henda iê ia tu idikiza ia kulu kinéne katé kia tu ixana tu ania a Nzambi."),
      LitanyContent(id: 465, person: LitanyPerson.CONGREGATION, litanyTitle: KmLitanyTitleSeeder.KUMUTU_UALA_KU_MUTUAMESA_O_MAKA_NA_JEHOVA, books: "II kolindo 9:15", content: "Tu sakidiliemu Nzambi mukonda dia henda iê i tanga ki i xina."),
      LitanyContent(id: 450, person: LitanyPerson.DIRECTOR, litanyTitle: KmLitanyTitleSeeder.KUMUTU_UALA_KU_MUTUAMESA_O_MAKA_NA_JEHOVA, books: "Habak.2:20", content: "Jehova phe uen`ie mu tembula iê ikola; ixi ioso i dixibhe bhu pholo ie."),
