import 'package:ieca_mobile/_import.dart';

class NgHymnsKuLania {
  static List<HymnsContent> list =[
    HymnsContent(id: 3267, position: 1, content: "Vose va na zivi, ambululenu\nMajui a ku ovoka kuose mu ka ya;\nKuli vantu vose mu ka va leka,\nUose a tava, iza.", hymnsNumber: NgHymnsNumberSeeder.KU_LANIA_18, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 3268, content: "Uose a tava, uose a tava,\nIza kuli Yesu A ka ku ovola;\nTata na cizemo vose A lania,\nUose a tava, iza.", hymnsNumber: NgHymnsNumberSeeder.KU_LANIA_18, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 3269, position: 2, content: "Uose a ka iza, keti u tate,\nLipito lia muilu na sokolola;\nMuafua ku-tsa Kuendi u ka kovela,\nUose a tava, iza.", hymnsNumber: NgHymnsNumberSeeder.KU_LANIA_18, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 3270, position: 3, content: "Uose a tava, cizango ca kama,\nUose a tava, muono a tambula;\nUose a tava, muilu a ka heta,\nUose a tava, iza.", hymnsNumber: NgHymnsNumberSeeder.KU_LANIA_18, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 3271, position: 1, content: "Iza kuli Yesu, tolilila!\nMu lijui Liendi A ku lania;\nHa kati ketu A imana,\nA ku sana, iza.", hymnsNumber: NgHymnsNumberSeeder.KU_LANIA_19, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 3272, content: "Cili, cili, tu ka zolela,\nOmo na Yesu tu liuana;\nNa vivi viahi tu ka kala\nKu ndzivo ya Njambi.", hymnsNumber: NgHymnsNumberSeeder.KU_LANIA_19, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 3273, position: 2, content: "Vantu va-vavi A va sana,\nVa pande lelo ku Mu kava;\nNa ngue tungelo va ka kala\nHamo Nendi muilu.", hymnsNumber: NgHymnsNumberSeeder.KU_LANIA_19, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 3274, position: 3, content: "Nga tu Mu kava tu ka uana\nNgozi ia kama ku tu niunga;\nHa ku holoka na ku huima\nKu vimbunge vietu.", hymnsNumber: NgHymnsNumberSeeder.KU_LANIA_19, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 3275, position: 4, content: "U ka vindama nga u hita,\nKesi na Yesu na ku-ovoka;\nS’pilitu Santu A ku kuasa\nU tave ololo.", hymnsNumber: NgHymnsNumberSeeder.KU_LANIA_19, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 3276, position: 1, content: "Nji hanga ku suena kuli Ove.\nNga cilindakano ci nji zundula,\nKasi nji ka lomba, \nNji suenese ngambu,\nNji suene kuli Ove, \nKalunga kange.", hymnsNumber: NgHymnsNumberSeeder.KU_LANIA_20, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 3277, position: 2, content: "Mua nji enda lika na vusiua,\nMavoko Ove a nji kolesako.\nNange nji ka lomba, \nNji suenese ngambu,\nNji suene kuli Ove, \nKalunga kange.", hymnsNumber: NgHymnsNumberSeeder.KU_LANIA_20, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 3278, position: 3, content: "Nji kemba kuli kalamba kange.\nKu Betel nji haka cimoeso ca ngozi.\nNji ka lomba kasi, \nNji suenese ngambu,\nNji suene kuli Ove \nKalamba kange.", hymnsNumber: NgHymnsNumberSeeder.KU_LANIA_20, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 3279, position: 1, content: "Ka zive lijui lia kaniungi\nYove muanike ua Yesu;\nU tave lelo, u kovele,\nMu limba Liendi lia ngozi.", hymnsNumber: NgHymnsNumberSeeder.KU_LANIA_21, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 3280, content: "Kovela, kovela,\nMu limba Liendi lia ngozi;\nKovela, kovela,\nMu limba Liendi lia ngozi.", hymnsNumber: NgHymnsNumberSeeder.KU_LANIA_21, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 3281, position: 2, content: "Ikeye ku-lia A tu lisa,\nIkeye vuzalo vuetu;\nA hana viose tua jena,\nKaniungi ketu ka ngozi.", hymnsNumber: NgHymnsNumberSeeder.KU_LANIA_21, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 3282, position: 3, content: "Kaniungi ketu Atu amena,\nNambe tu yoya, ni tu tsa;\nTu kovela na ku vuaha\nMu limba Liendi lia muilu.", hymnsNumber: NgHymnsNumberSeeder.KU_LANIA_21, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 3283, position: 1, content: "Nja tulile muono, \nNjecele maninga,\nMu ku kula yove \nNa ku ku hia muono.", hymnsNumber: NgHymnsNumberSeeder.KU_LANIA_22, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 3284, content: "Nja ku tulilile muono,\nVika ua Nji hanako?", hymnsNumber: NgHymnsNumberSeeder.KU_LANIA_22, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 3285, position: 2, content: "Vumpahu vua Tata \nNa ceke ca muilu,\nNja vi tolakanene \nNa vupi vua mavu.", hymnsNumber: NgHymnsNumberSeeder.KU_LANIA_22, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 3286, position: 3, content: "Nja ku yandelele \nNa lamba ya kama,\nMu ku ku patulako\nKu ngolo ya ku-tsa.", hymnsNumber: NgHymnsNumberSeeder.KU_LANIA_22, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 3287, position: 4, content: "Nji na ku nehela \nVuovole vua Tata,\nMulonga va ku ecela nga u ka tsiliela.\nUa nji tulilile muono,\nVika nji Ku hanako?", hymnsNumber: NgHymnsNumberSeeder.KU_LANIA_22, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 3288, position: 1, content: "Nji na u tambula muono,\nUa uvua ua Njambi yange;\nUkeuo vene nja suana,\nVufuko vua kama vene.", hymnsNumber: NgHymnsNumberSeeder.KU_LANIA_23, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 3289, content: "Tuayenu vose vakuetu,\nTu ye mumo tu va vose;\nTu kovele mu cifuti,\nCize ca civua ca Yesu;\nMu ka kala vantu Veni,\nVakua-ku-lela va Njambi.", hymnsNumber: NgHymnsNumberSeeder.KU_LANIA_23, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 3290, position: 2, content: "Muovole Ua enda nange,\nNgozi Yendi nja zivi;\nUa nji kuata ku livoko,\nMu ku nji tuamena vuino.", hymnsNumber: NgHymnsNumberSeeder.KU_LANIA_23, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 3291, position: 3, content: "S’pilitu A simutuila,\nMajui a ku vuahelesa;\nMu mbunge a tovalamo,\nMu ku nji hana ciyulo.", hymnsNumber: NgHymnsNumberSeeder.KU_LANIA_23, typeStanza: HymnsContentCode.VERSE),


  ];
}