import 'package:ieca_mobile/_import.dart';

class NgLitanyContentSeeder{

  static List<LitanyContent> items() {
    return [
      LitanyContent(id: 260, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.KULANIA_KULEMESA, books: "Hav 2:20", content: "Yehova a li mu tembele yeni ya ku lela: mavu ose a holoke ku meso eni."),
      LitanyContent(id: 261, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.KULANIA_KULEMESA, books: "Vis 85:8", content: "Nji hianga ku ivua vize vi a ka handeka Njambi Yehova, muomu a ka handeka ciyulo na vantu veni."),
      LitanyContent(id: 262, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.KULANIA_KULEMESA, books: "Vis 46:1", content: "Njambi ikeye vutinino vuetu na ngolo yetu, ku-kuasa ca mu hiehi mu kayando."),
      LitanyContent(id: 263, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.KULANIA_KULEMESA, books: "Vis 46:2", content: "Ngoco vene ka tu kevua vuoma, cipue mavu a aluluka, vutuhu micinda i ndundumukila mu kati ka mema akalunga."),
      LitanyContent(id: 264, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.KULANIA_KULEMESA, books: "Vis 130:7", content: "Lavelela kuli Yehova, muomu kuli Yehova ku li ngozi, na kuli ikeye kua lana vunkuli."),
      LitanyContent(id: 265, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.KULANIA_KULEMESA, books: "Vis 130:5", content: "Nji tumamena Yehova muono uange u tumamena, na ku lizi lieni nja lavelela."),
      LitanyContent(id: 266, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.KULANIA_KULEMESA, books: "Vis 34:3", content: "Halesenu Yehova hamo nange, kaha tu zumbangeyenu lizina lieni mumo lika."),
      LitanyContent(id: 267, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.KULANIA_KULEMESA, books: "Vis 96:4", content: "Muomu ua kama Yehova, ua ku sangaziala cikumakuma."),
      LitanyContent(id: 268, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.KULANIA_KULEMESA, books: "Vis 103:8", content: "Yehova ikeye mukua cikeketima na kavumbi, ua livandeka ku nienga, kaha a lana ngozi."),
      LitanyContent(id: 269, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.KULANIA_KULEMESA, books: "Vis 103:10", content: "Ke ku linga netu ngue ku vipi vietu, cipue ka tu lunguisile ngue ku milonga yetu."),
      LitanyContent(id: 270, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.KULANIA_KULEMESA, books: "Vis 103:11", content: "Muomu ngue melu a li helu cikuma lia mavu, ngecize mua neneha ngozi yeni kuli vaze va mu vuila vuoma."),
      LitanyContent(id: 271, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.KULANIA_KULEMESA, books: "Vis 103:12", content: "Ngue omo ku-iza ca litangua ca litepa na ku-ya ca litangua, ku laako ngecize na tu sondoluela vivulumuna vietu."),
      LitanyContent(id: 272, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.KULANIA_KULEMESA, books: "Vis 3:5", content: "Tsiliela Yehova na mutima uove uose, kati u zendamene ku mana ove ivene."),
      LitanyContent(id: 273, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.KULANIA_KULEMESA, books: "Vis 139:23-24", content: "Nji hilule, a Njambi, na ku tantekeya mutima uange: nji eseke, na ku tantekeya visinganieka viange, na ku mona nga ku i li njila ya ipi muli ange, kaha nji tuamene mu njila ize ka ye ku hua, ya ya-ye."),
      LitanyContent(id: 274, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.KULANIA_KULEMESA, books: "1 Yoano 3:1", content: "Talenu cilemo ca ku hiana Tata na tu hieco, omo va tu tumbula vana va Njambi."),
      LitanyContent(id: 275, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.KULANIA_KULEMESA, books: "II Kol 9:15", content: "Tu na santsela kuli Njambi omo vuana vueni vukevuo ka vu tava ku vu tumbula."),

      LitanyContent(id: 276, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.YA_KU_LITAVELA, books: "Vis 80:1,2", content: "Ivuilila, a kaniungi ua Isaleli, yove mukua ku tuamena Yosefe ngue vutanga; yove mukua ku tumama helu lia vaselumbini, tua ceke, ku meso a Efalimi na Mbenjamini na Manase, hindula zindzili ziove, kaha tuaya mu ku tu ohiela."),
      LitanyContent(id: 277, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.YA_KU_LITAVELA, books: "Vis 80:19", content: "Tu aluluise naua, a Yehova, Njambi ua vivunga; tuisa cihanga cove ceke, kaha tu kovoka."),
      LitanyContent(id: 278, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.YA_KU_LITAVELA, books: "Vis 80:8,9", content: "Ua fumisile muti ua viniu ku Ingito: ua sekuile vakua vifuti, na ku u tombeka. Ua u viukisilile lumo, na mizi ya tokelekele, kaha ua sulisile cifuti."),
      LitanyContent(id: 279, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.YA_KU_LITAVELA, books: "", content: "Tu aluluise naua, a Yehova, Njambi ua vivunga; tuisa cihanga cove ceke, kaha tu kovoka."),
      LitanyContent(id: 280, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.YA_KU_LITAVELA, books: "Vis 80:14,15", content: "A Njambi ua vivunga, talako fume muilu, kaha mona, na ku hinduisa muti ou. Na cimpindakati cize ci lia tombekele livoko liove lia cilio."),
      LitanyContent(id: 281, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.YA_KU_LITAVELA, books: "", content: "Tu aluluise naua, a Yehova, Njambi ua vivunga; tuisa cihanga cove ceke, kaha tu kovoka."),
      LitanyContent(id: 282, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.YA_KU_LITAVELA, books: "Vis 51:1,10", content: "Tu vuileko ci keketima, a Njambi, ngue ku ngozi yove: ngue ku ku-lana ca vikeketima viove vivotsi zimangana vivulumuna vietu. Tanga muli etu mutima ua ku lela, a Njambi, kaha naua cimbembesi ca ku sungama mu kati ketu."),
      LitanyContent(id: 283, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.YA_KU_LITAVELA, books: "", content: "Tu aluluise naua, a Yehova, Njambi ua vivunga; tuisa cihanga cove ceke, kaha tu kovoka."),
      LitanyContent(id: 284, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.YA_KU_LITAVELA, books: "Vis 51:2,4,15,12,17b", content: "Tu tanese to ku vipi vietu, kaha tu lelese ku vupi vuetu. Kuli ove, kuli ove lika, tu na vulumuna, kaha tu na lingi evi via vipi ku meso ove, Mangana ove u sungame omo u handeka, kaha u viuke omo u sompesa. A Sukulu, asamesa milungu yetu; kaha kanua ketu ka ka solola visangala viove. Tu hiluisile ndzolela ya vuovole vuove; kaha tu kuatese na cimbembesi ca ku ononoka. Mutima ua ku pokoka na ku aluluka, ko u ka yengula, a Njambi."),
      LitanyContent(id: 285, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.YA_KU_LITAVELA, books: "", content: "Tu aluluise naua, a Yehova, Njambi ua vivunga; tuisa cihanga cove ceke, kaha tu kovoka."),
      LitanyContent(id: 286, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.YA_KU_LITAVELA, books: "Vilinga 11:17-18b", content: "Amba ni nga Njambi ua va hele vuana vumo lika netu naua, omo etu tua tsilielele Muangana Yesu Kilistu, ange-ndzo yangeya, linga nji vindike Njambi? Amba ni Njambi na fungila vakua vifuti ku aluluka ku uana muono."),
      LitanyContent(id: 287, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.YA_KU_LITAVELA, books: "", content: "Tu aluluise naua, a Yehova, Njambi ua vivunga; tuisa cihanga cove ceke, kaha tu kovoka."),
      LitanyContent(id: 288, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.YA_KU_LITAVELA, books: "Vilinga 11:12,15; Loma 8:14", content: "Kaha Sipilitu ikeye ua nji sikile, ku ya navo, na ku hangula cahi. Kaha omo nja sangumukile ku handeka, Sipilitu Santu ua uile ha vakevo, ngue mua na uile hali etu haze ha katete. Muomu vose ava, Sipilitu ua Njambi a li na ku va tuamena, vakevo vene vana va Njambi."),
      LitanyContent(id: 289, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.YA_KU_LITAVELA, books: "", content: "Tu aluluise naua, a Yehova, Njambi ua vivunga; tuisa cihanga cove ceke, kaha tu kovoka."),
      LitanyContent(id: 290, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.YA_KU_LITAVELA, books: "Vilinga 12:7,8", content: "Kaha talenu, kangelo ua Muangana ua mene hamo neni, na ceke ca tuile mu hondo. Kaha kangelo ua mu lekele, ngueni, Lizitike kaha zala zinkaku ziove, zala kambilikiti kove, nji kave."),
      LitanyContent(id: 291, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.YA_KU_LITAVELA, books: "", content: "Tu aluluise naua, a Yehova, Njambi ua vivunga; tuisa cihanga cove ceke, kaha tu kovoka."),
      LitanyContent(id: 292, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.YA_KU_LITAVELA, books: "II Pet 3:13; Ku-Solola 21:3,4", content: "Vunoni ngue omo ku litavasiano lieni, tu tavela melu a aha na mavu a aha, hakeho ha ka kala vuviuke. Kaha nja vuile lizi lia kamakama lia ku citumamo ca vuangana ku handeka, ngualio, Talenu, tembele ya Njambi i li hamo na vantu, kaha ikeye a ka tumama hamo navo, kaha va ka pua vantu veni, kaha Njambi ivene a ka kala hamo navo, a ka kala Njambi yavo. Kaha a ka hunda masozi ose kumeso avo na kutsa ka ci ka kala naua, muomu viuma via ku livanga vi na hiti."),
      LitanyContent(id: 293, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.YA_KU_LITAVELA, books: "", content: "Tu aluluise naua, a Yehova, Njambi ua vivunga; tuisa cihanga cove ceke, kaha tu kovoka."),

      LitanyContent(id: 294, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.YA_KU_PINGA_VIEKA_NA_VIEKA, books: "Isaya 55:6,7", content: "Lengeyenu Yehova omo mu ku hasa ku mu uana, mu tambakianienu omo a li ha hiehi. Mukua vipi a seze njila yeni, na mukua vuhenge visinganieka vieni; kaha a hiluke kuli Yehova, kaha ikeye a ka mu ecela ku vipi cikumakuma."),
      LitanyContent(id: 295, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.YA_KU_PINGA_VIEKA_NA_VIEKA, books: "Visamo 100:5", content: "Muomu Yehova ikeye ua viuka, ngozi yeni i kala ku miaka yose ya ya-ye, na vucili vueni ku mazimo ose."),
      LitanyContent(id: 296, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.YA_KU_PINGA_VIEKA_NA_VIEKA, books: "Isaya 1:18; I Yoano 1:7", content: "Vutuhu vipi vienu via tili ngengu, vi ka vuka to ngue mema a ku konda; vutuhu via tili nge, vi ka himpuka ngue maka a mpanga. Vunoni nga tu endela mu mutana, ngue omo ikeye a li mu mutana, maninga a Yesu Muaneni a tu vukisa ku vipi viose."),
      LitanyContent(id: 297, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.YA_KU_PINGA_VIEKA_NA_VIEKA, books: "I Yoano 1:8,9", content: "Nga tu handeka, nguetu ka tuesi na vupi, tu ku lionga etu vavene, na vusunga vuahimo muli etu. Vunoni nga tu litavela vipi vietu, ikeye mukua cili na mukua vuviuke, a tu ecela vipi vietu."),
      LitanyContent(id: 298, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.YA_KU_PINGA_VIEKA_NA_VIEKA, books: "Visimo 28:13; 32:1", content: "Uze a fuika ku vivulumuna vieni ka li ka vezika; vunoni uze a litavelavio na ku vi seza a ka tambula cikeketima. Tu litavelienu vipi vietu, muomu ua vezika uze va na mu ecela civulumuna ceni."),
      LitanyContent(id: 299, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.YA_KU_PINGA_VIEKA_NA_VIEKA, books: "Visamo 32:5", content: "Nja litavele vupi vuange kuli ove, na vipi viange ka nja vi suekele: nja handekele nguangue, nji ka litavela vivulumuna viange kuli Yehova, kaha ove ua nji ecelele ku-pihia ca vupi vuange."),
      LitanyContent(id: 300, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.YA_KU_PINGA_VIEKA_NA_VIEKA, books: "Yelemiya 29:11; Vinien 3:40,41", content: "Muomu mu a li ku handeka Yehova ngueni: nji tantekeya visinganieka vize vi nji ku singanieka kuli enu, ngueni Yehova, visinganieka via ciyulo, kati via vipi. Tu konkienu na ku eseka zinjila zietu, na ku hiluka naua kuli Yehova. Tu zundulienu mutima uetu hamo na mavoko etu kuli Njambi muilu."),
      LitanyContent(id: 301, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.YA_KU_PINGA_VIEKA_NA_VIEKA, books: "Visamo 51:1,10; 130:24b", content: "Nji vuileko cikeketima, a Njambi, ngue ku ngozi yove: ngue ku ku-lana ca vikeketima viove vivotsi zimangana vivulumuna viange. Tanga muli ange mutima ua ku lela, a Njambi; kaha haka naua cimbembesi ca ku sangama mu kati kange. Nji tuamene mu njila ize ka ye ku hua, ya ya-ye."),
      LitanyContent(id: 302, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.YA_KU_PINGA_VIEKA_NA_VIEKA, books: "II Kol 5:21; Loma 6:11", content: "Uze ka tantekeyele vupi, ua mu puisile vupi omo yetu; linga etu tu pue vuviuke vua Njambi muli ikeye. Nenu naua ngecize vene litumbulienu ngecize ngue mu vatsi ku vupi, vunoni mu vakua muono kuli Njambi muli Kilistu Yesu."),
      LitanyContent(id: 303, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.YA_KU_PINGA_VIEKA_NA_VIEKA, books: "Efeso 1:7", content: "Muli ikeye tu li na ku kuka na maninga eni, kuecela ca vivulumuna vietu, ngue ku vufuko vua kavumbi keni."),
      LitanyContent(id: 304, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.YA_KU_PINGA_VIEKA_NA_VIEKA, books: "II Kol. 9:15", content: "Tu na santsela kuli Njambi omo vuana vueni vukevuo ka vu tava ku vu tumbula."),

      LitanyContent(id: 305, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.YA_KU_SANTSELA, books: "", content: "Tu santsienu kuli Njambi omo muana lisoluela kuli etu hakati ka viuma vingi a nambe na ku linga, vunoni sina omu muanambe netu na muana eni Yesu Kilistu ikeye Muovole uetu, kasi ikeye na tu lingila njila ya sunga na muono."),
      LitanyContent(id: 306, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.YA_KU_SANTSELA, books: "", content: "Tu na santsela kuli Njambi omo lia ngozi yeni ya yingi."),
      LitanyContent(id: 307, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.YA_KU_SANTSELA, books: "", content: "Tu santsienu kuli Njambi omo tu li na mbangi munene mu mitima yetu ikeye Sipilitu Sandu, novu ikeye a ku manena mu mitima yetu vikuama via ku tu kula nomu a na tu muesa mana na ndzili."),
      LitanyContent(id: 308, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.YA_KU_SANTSELA, books: "", content: "Tu na santsela kuli Njambi omo lia ngozi yeni ya yingi."),
      LitanyContent(id: 309, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.YA_KU_SANTSELA, books: "", content: "Tu santsienu kuli Njambi omo tu li na visoneka vieni via nganda vi na lingi ceke ca zingano zietu na ku iziva ku tantekeya viuma via ku tu kula nevi via muono ua ya-ye ka ue ku hua. Tu santsienu naua ngomu a tu katuila ava va tu soluela lizi lieni lia ku lela na ku tu soluela njila ya cili."),
      LitanyContent(id: 310, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.YA_KU_SANTSELA, books: "", content: "Tu na santsela kuli Njambi omo lia ngozi yeni ya yingi."),
      LitanyContent(id: 311, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.YA_KU_SANTSELA, books: "", content: "Tu santsienu kuli Njambi omo na tu sana ku cikuama ceni ca ku lela, tu linge tunganda va lizi lieni. Tu santsienu omo ku vikuama viaco tuli na mpami ya ku kuasa cingi cihuti cetu Angola, na ku tsontsa naua hakati ketu vunene vua mbunga yeni."),
      LitanyContent(id: 312, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.YA_KU_SANTSELA, books: "", content: "Tu na santsela kuli Njambi omo lia ngozi yeni ya yingi."),
      LitanyContent(id: 313, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.YA_KU_SANTSELA, books: "", content: "Tu santsienu kuli Njambi ngomu ka tu sele ndzimba, vunoni ua tu sela cizilika ca tumbuka ca Yesu ngueni “Hamenu ku vihuti viose na ku longesa vandongesi nomu a li kuminya na ku tsontsa vu mpami ngueni tuli hamolika nenu na ku kotoka ca mavu”."),
      LitanyContent(id: 314, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.YA_KU_SANTSELA, books: "", content: "Tu na santsela kuli Njambi omo lia ngozi yeni ya yingi."),

      LitanyContent(id: 315, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.YA_NATALE, books: "", content: "Vulamba vu ehenu kuli Muangana ua va miangana, ou ua tumine ku ovola vantu va li kuminya ku linga cizango ceni."),
      LitanyContent(id: 316, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.YA_NATALE, books: "", content: "Ku malimi ose va mu sangale nguavo: Aleluya Muangana ua Ciyulo."),
      LitanyContent(id: 317, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.YA_NATALE, books: "Luka 1:32,33", content: "Ikeye a ka pua ua kama, va ka mu luka Muana ua Mukua ku Hiana: kaha Muangana Njambi a ka mu hia citumamo ca vuangana ca ise Ndaviti: kaha a ka sika ndzivo ya Yakomba ku miaka yose ya ya-ye: na vuangana vueni ka vu ka hua."),
      LitanyContent(id: 318, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.YA_NATALE, books: "", content: "Ku malimi ose va mu sangale nguavo: Aleluya Muangana ua Ciyulo."),
      LitanyContent(id: 319, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.YA_NATALE, books: "Luka 1:37,45,48,50", content: "Muomu lizi na limo liahi lia ku fuma kuli Njambi li ka zela ngolo. Kaha uze mukua ku tsiliela na vezika; muomu viuma vize via ku fuma kuli Muangana vi va na handeka neni, vi ka soloka. Muomu ikeye na tale ku vundende vua mukuamanda ueni: Muomu tala, fume hano mazimo ose va ka nji tumbula mukua ku vezika. Na cikeketima ceni ci kala ku mazimo na ku mazimo."),
      LitanyContent(id: 320, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.YA_NATALE, books: "", content: "Ku malimi ose va mu sangale nguavo: Aleluya Muangana ua Ciyulo."),
      LitanyContent(id: 321, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.YA_NATALE, books: "Vahev 1:5,6; Isaya 9:6", content: "Muomu na kangelo muka na handeka lumo, ngueni, ove u Muanange, litangua lia lelo nja ku seme kaha naua, yange nji ka pua Ise kuli ikeye, neni a ka pua Muana kuli ange. Kaha omo a neha naua Muana ua ndala ha mavu, a handeka ngueni, na tungelo vose va Njambi va mu lemese. Cituamo cove ca vuangana, ove Njambi, ci kala-kala miaka na miaka ya ya-ye; na mbueti ya vuolole ikeyo mbueti ya vuangana vuove. Isaya ngueni, va ka mu luka lizina lieni: Mukua ku Komouesa, Mukua Zinguli, Njambi Mukua Ngolo, Ise ya Miaka yose, Muata ua Ciyulo."),
      LitanyContent(id: 322, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.YA_NATALE, books: "", content: "Ku malimi ose va mu sangale nguavo: Aleluya Muangana ua Ciyulo."),
      LitanyContent(id: 323, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.YA_NATALE, books: "Visamo 18:49; 67:5; Lom 14:11; Ku-sol 7:9,10", content: "Ngoco vene nji ka ku santsela, a Yehova, ha kati ka vifuti, kaha nji kemba visangala ku lizina liove. Vantu va ku sangaziale, a Njambi; vantu vose va ku sangaziale. Omo mu nji li na ku yoya, mavuli ose a ka nji tsikamena, na malimi ose a ka litava kuli Njambi. Mu nima yeni nja muene, kaha talenu, civunga ca kama, na umo uahi ua hasa ku va tanda, va ku vifuti viose, na miyati yose, na vantu vose na malimi ose, kaha va tambakana na lizi lia kama, nguavo, vuovole vu li kuli Njambi yetu."),
      LitanyContent(id: 324, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.YA_NATALE, books: "", content: "Ku malimi ose va mu sangale nguavo: Aleluya Muangana ua Ciyulo."),

      LitanyContent(id: 325, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.YA_PASKUA, books: "", content: "Vulamba ku Ise na ku Muana na ku Sipilitu Santu. Ngeci mua ha katete, lelo na ku miaka ya ya-ye."),
      LitanyContent(id: 326, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.YA_PASKUA, books: "", content: "Aleluya!"),
      LitanyContent(id: 327, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.YA_PASKUA, books: "Luka 24:1,2", content: "Vunoni ku litangua lia ku livanga lia simano, te ku ci ku ca, vakevo vezile ku cihilo, va nehele vihemba vize vi va ka viukisile. Kaha va uanene livue va na li andumuna laza ku cihilo."),
      LitanyContent(id: 328, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.YA_PASKUA, books: "", content: "Aleluya!"),
      LitanyContent(id: 329, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.YA_PASKUA, books: "Mat. 28:8,9", content: "Kaha va fumine lusi ku cihilo, na vuoma na ndzolela ya kama, kaha talenu, Yesu ua liuanene navo, ngueni, Hindukenu!"),
      LitanyContent(id: 330, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.YA_PASKUA, books: "", content: "Aleluya!"),
      LitanyContent(id: 331, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.YA_PASKUA, books: "Yoano 20:11,13-15", content: "Vunoni Maliya ua mene ku mbanza ya cihilo na ku lila: ngoco omo ua lilile, ua kotamene, ua talele mu cihilo. Ikeye ua va lekele, ngueni, va na ambata Muangana uange, kaha ka nji tantekeya kuze ku va na muhake. Omo ua handekele ngecize, ua alulukile, kaha wa muene Yesu na manako, kaha ka tantekeyele ngecize ikeye Yesu. Yesu ua handekele neni, ngueni, Mpuevo, vikandzo u ku lilila? Maliya ua alulukile, kaha ua handekele neni, ngueni, Mulongisi."),
      LitanyContent(id: 332, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.YA_PASKUA, books: "", content: "Aleluya!"),
      LitanyContent(id: 333, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.YA_PASKUA, books: "Luka 24:13,15", content: "Kaha talenu, litangua olio vene vavali vavo va ile ku limbo limo lia ku luka Emau. Kaha ca puile ngecize, omo va kele na kuana ku lileka na ku likandula zimpata, ivene Yesu ua va suenene, uendele mumo navo."),
      LitanyContent(id: 334, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.YA_PASKUA, books: "", content: "Aleluya!"),
      LitanyContent(id: 335, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.YA_PASKUA, books: "Yoano 20:19,20", content: "Ngoco vene omo kua puile cinguezi litangua olio, omo va na patele ku mapito muze mu va kele vandongesi, omo vuoma vua va Yundeya, kaha Yesu uezile, ua mene ha kati kavo, kaha uahandekele navo, ngueni, Ciyulo ci kale nenu. Kaha omo na handekele ngecize, ua va lekesele mavoko eni na luhati lueni."),
      LitanyContent(id: 336, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.YA_PASKUA, books: "", content: "Aleluya!"),
      LitanyContent(id: 337, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.YA_PASKUA, books: "Yoano 21:3,4,7", content: "Simono Petulu ua handekele navo, ngueni, Nji ku ya mu ku ta vantsi. Va mu lekele, nguavo, Netu naua tu ku ya nove. Kaha vutsiki vuze na umo i va lavuile uahi. Vunoni omo litangua lia hiangele ku ca, Yesu ua mene ku mukulo ua cisali vunoni vandongesi ka va tantekeyele ngecize ikeye Yesu. Ngoco vene ndongesi uze ya lemene Yesu, ua lekele Petulu, ngueni, Ikeye Muangana."),
      LitanyContent(id: 338, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.YA_PASKUA, books: "", content: "Aleluya!"),
      LitanyContent(id: 339, person: LitanyPerson.DIRECTOR, litanyTitle: NgLitanyTitleSeeder.YA_PASKUA, books: "Ku-Solola 1:10,12-15,17,18", content: "Nja kele muli Sipilitu ku litangua lia Muangana, kaha nja vuile ku nima yange lizi lia kama, ngue lia luneta; kaha nja alulukile ku mona lizi lize lia handekele nange, lia ku lifua na muana muntu, na zala vuzalo vua ku heta ku viliato, na lizitika muiva ua olu mu ntulo. Na mutue ueni na zinkambu zieni via vukile to ngue maka a mpanga a ku vuka, ngue mema a ku konda; na meso eni a lifuile na mulenge ua tuhia; na viliato vieni ngue vunengu vua ku tumuna, ngue va na vu lelesa mu lutengo; na lizi lieni ngue lizi lia mema a engi. Kaha omo nja mu muene, nja uile ku viliato vieni ngue mukua ku tsa. Kaha ikeye ua nji salekele livoko lieni ngue mukua ku tsa. Kaha ikeye ua nji salekele livoko lieni lia cilio, ngueni, kati u ivue vuoma; yange nji mukua ku livanga na mukua ku kotokela, na Mukua ku yoya; nja tsile, kaha tala, nji yoya ku miaka na miaka ya ya-ye, kaha nji li na masapi a ku ku-tsa na a ku Vukalo vua Vatsi."),
      LitanyContent(id: 340, person: LitanyPerson.CONGREGATION, litanyTitle: NgLitanyTitleSeeder.YA_PASKUA, books: "", content: "Aleluya Aleluya, Aleluya!"),
    ];
  }
}