import 'package:ieca_mobile/enums/_import.dart';
import 'package:ieca_mobile/models/_import.dart';
import 'package:ieca_mobile/seeders/portugues/_import.dart';

class HymnsChildren {
  static List<HymnsContent> list =[
    HymnsContent(id: 530, position: 1, content: "Alegres hoje nós viemos,\nNós cordeirinhos seus.\nCorrendo vamos à Igreja,\nNós cordeirinhos seus.", hymnsNumber: HymnsNumberSeeder.CHILDREN_126, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 531, content: "Sim, Cristo salva os pequeninos,\nOs pequeninos, os pequeninos,\nSim Cristo salva os pequeninos;\nOh! Todos sejamos seus.", hymnsNumber: HymnsNumberSeeder.CHILDREN_126, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 532, position: 2, content: "Nós detetamos nossos pecados,\nNós cordeirinhos seus.\nTambém fugimos deste jugo,\nNós cordeirinhos seus.", hymnsNumber: HymnsNumberSeeder.CHILDREN_126, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 533, position: 3, content: "A Sua voz reconhecemos,\nNós cordeirinhos seus.\nErguido está nosso coração,\nNós cordeirinhos seus.", hymnsNumber: HymnsNumberSeeder.CHILDREN_126, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 534, position: 1, content: "Vinde meninos, vinde a Jesus;\nEle ganhou-vos bênçãos na cruz!\nOs pequeninos Ele conduz;\nOh, vinde ao Salvador!", hymnsNumber: HymnsNumberSeeder.CHILDREN_127, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 535, content: "Que alegria, sem pecado ou mal,\nReunir-nos todos, afinal,\nJuntos na pátria celestial,\nPerto do Salvador!", hymnsNumber: HymnsNumberSeeder.CHILDREN_127, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 536, position: 2, content: "Já, sem demora, a todos convém\nIr caminhando à glória de além;\nCristo vos chama, quer vosso bem,\nOh! Vinde ao Salvador!", hymnsNumber: HymnsNumberSeeder.CHILDREN_127, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 537, position: 3, content: "Que ama os meninos, Cristo vos diz;\nQuer receber-vos nesse país;\nQuer conceder-vos vida feliz;\nOh! Vinde ao Salvador.", hymnsNumber: HymnsNumberSeeder.CHILDREN_127, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 538, position: 4, content: "Eis a chamada: “Vinde hoje a mim!”\nOutro não há que vos ame assim!\nSeu é o amor que nunca tem fim;\nOh! Vinde ao Salvador!", hymnsNumber: HymnsNumberSeeder.CHILDREN_127, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 539, position: 1, content: "Por belezas naturais,\nPelo azul do claro céu,\nPor encantos imortais,\nÓ Senhor, ao trono Teu,\nSe erguerá e com fervor\nNossa voz em Teu louvor.", hymnsNumber: HymnsNumberSeeder.CHILDREN_128, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 540, position: 2, content: "Por amigos e irmãos,\nPela luz do puro amor,\nPor poderem nossas mãos\nTrabalhar em Teu favor,\nSe erguerá, também, Senhor,\nNossa voz em Teu louvor.", hymnsNumber: HymnsNumberSeeder.CHILDREN_128, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 541, position: 3, content: "Por Teu grande, Santo Dom,\nQue por nós morreu na cruz,\nPor um guia, Mestre bom,\nQue os fiéis ao Céu conduz,\nSe erguerá, também, Senhor,\nNossa voz em Teu louvor.", hymnsNumber: HymnsNumberSeeder.CHILDREN_128, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 542, position: 1, content: "Vejo no céu resplendente\nDo sol a clara luz;\nQuero viver tão somente\nBrilhando por Jesus.", hymnsNumber: HymnsNumberSeeder.CHILDREN_129, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 543, content: "Brilhando, brilhando,\nQuero brilhar como a luz!\nBrilhando, brilhando,\nSempre brilhar por Jesus!", hymnsNumber: HymnsNumberSeeder.CHILDREN_129, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 544, position: 2, content: "Eu quero em tudo exaltá-Lo,\nNa escola e no estudar;\nNunca também olvidá-Lo\nEm casa e no brincar.", hymnsNumber: HymnsNumberSeeder.CHILDREN_129, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 545, position: 3, content: "Com um viver diligente,\nAssim me quer Jesus;\nSempre com rosto contente,\nBrilhando com a luz.", hymnsNumber: HymnsNumberSeeder.CHILDREN_129, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 546, position: 4, content: "Do feio e triste pecado,\nSenhor, vem-me guardar;\nSempre por Ti amparado\nDesejo, ó Deus, andar.", hymnsNumber: HymnsNumberSeeder.CHILDREN_129, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 547, position: 5, content: "Se esta é a tua vontade,\nBrilhando viverei,\nE pela tua bondade\nAo lindo Céu irei.", hymnsNumber: HymnsNumberSeeder.CHILDREN_129, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 548, position: 1, content: "Perante o trono do Senhor,\nNa glória de Jesus,\nCrianças aos milhares há,\nBrilhando em santa luz.", hymnsNumber: HymnsNumberSeeder.CHILDREN_130, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 549, content: "Cantam: «Glória, glória,\nGlória ao salvador Jesus!»", hymnsNumber: HymnsNumberSeeder.CHILDREN_130, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 550, position: 2, content: "Dos seus pecados o perdão\nJesus lhes concedeu,\nE agora  em sempiterna paz\nCom Ele estão no Céu.", hymnsNumber: HymnsNumberSeeder.CHILDREN_130, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 551, position: 3, content: "Na eterna habitação feliz,\nDe glória, brilho e amor,\nInfantes, salvos por Jesus,\nEntoam seu louvor.", hymnsNumber: HymnsNumberSeeder.CHILDREN_130, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 552, position: 4, content: "Quem na cruenta, amarga cruz\nSeu sangue derramou,\nAs criancinhas já remiu,\nE o Céu lhes outorgou.", hymnsNumber: HymnsNumberSeeder.CHILDREN_130, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 553, position: 5, content: "Na vida amavam a Jesus,\nBuscavam seu amor;\nAgora face a face estão\nCom Ele em seu fulgor.", hymnsNumber: HymnsNumberSeeder.CHILDREN_130, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 554, position: 1, content: "Há um bom amigo acima,\nDo céu azul, sem par,\nE mora nas alturas\nQuem sabe bem amar.\nAmigo mui constante,\nNão tem igual aqui;\nJesus é meu abrigo\nE chama-me p'ra Si.", hymnsNumber: HymnsNumberSeeder.CHILDREN_131, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 555, position: 2, content: "Há descanso lá acima\nDo céu azul, sem par.\nE todos quantos amam\nA Cristo, o vão gozar.\nPecados e perigos\nDesconhecidos são,\nPois Cristo reina firme\nCom poderosa mão.", hymnsNumber: HymnsNumberSeeder.CHILDREN_131, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 556, position: 3, content: "Há morada lá acima\nDo céu azul, sem par.\nMeninos e meninas,\nQuereis ir lá morar?\nO gozo deste mundo\nNão é p'ra comparar\nAo gozo que há com Cristo\nAli no eterno lar.", hymnsNumber: HymnsNumberSeeder.CHILDREN_131, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 557, position: 1, content: "Eis que Cristo vem à terra\nBuscar suas jóias,\nSuas jóias mui preciosas,\nDe muito valor.", hymnsNumber: HymnsNumberSeeder.CHILDREN_132, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 558, content: "Como estrelas da aurora,\nBrilhando na fronte\nDe Jesus, lá na glória,\nAdorno serão.", hymnsNumber: HymnsNumberSeeder.CHILDREN_132, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 559, position: 2, content: "Vem buscá-las, vai levá-las\nAo reino celeste,\nSuas jóias resplendentes,\nDe muito valor.", hymnsNumber: HymnsNumberSeeder.CHILDREN_132, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 560, position: 3, content: "Os meninos e as meninas\nQue servem a Cristo\nSão-Lhe jóias, ricas jóias,\nDe muito valor.", hymnsNumber: HymnsNumberSeeder.CHILDREN_132, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 561, position: 1, content: "Oh! Vinde meninos!\nCantai a linda história\nDo bom Messias dos judeus,\nJesus, o Salvador!\nE repeti com gratidão\nA doce e terna exclamação:\n«Deixai os meninos,\nQue venham a Mim!»", hymnsNumber: HymnsNumberSeeder.CHILDREN_133, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 562, position: 2, content: "Pais crentes, devotos,\nTraziam os filhinhos,\nBuscando a bênção e oração\nDe Cristo-Emanuel.\nMas com palavras de rigor\nSão afastados do Senhor:\n«Levai os meninos,\nTirai-os daqui!»", hymnsNumber: HymnsNumberSeeder.CHILDREN_133, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 563, position: 3, content: "Mas, eis que o bom Mestre\nCom vós suave e meiga\nOs pequeninos chama a Si,\nE aos circunstantes diz:\n«Sobre eles minhas mãos porei,\nE pelos tais Eu orarei;\nDeixai os meninos,\nQue venham a Mim!»", hymnsNumber: HymnsNumberSeeder.CHILDREN_133, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 564, position: 4, content: "Oh! Vinde, meninos;\nJesus vos deu seu sangue\nE vos convida para os céus.\nBuscai a salvação!\nOuvi a voz do Redentor,\nEle é o vosso bom Pastor:\n«Deixai os meninos,\nQue venham a Mim.»", hymnsNumber: HymnsNumberSeeder.CHILDREN_133, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 565, position: 1, content: "Cristo tem amor por mim,\nCom certeza eu creio assim.\nPor amor de mim morreu,\nVivo está por mim no Céu.", hymnsNumber: HymnsNumberSeeder.CHILDREN_134, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 566, content: "Ama os meninos,\nAma os meninos,\nAma os meninos,\nJesus o Salvador.", hymnsNumber: HymnsNumberSeeder.CHILDREN_134, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 567, position: 2, content: "Sim, Jesus me tem amor\nE se fez meu Salvador;\nHoje ainda tem prazer\nDe meninos receber.", hymnsNumber: HymnsNumberSeeder.CHILDREN_134, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 568, position: 3, content: "P’ra gozar Seu rico amor,\nHei-de amar meu Salvador,\nE desejo anunciar\nSeu amor tão singular.", hymnsNumber: HymnsNumberSeeder.CHILDREN_134, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 569, position: 4, content: "Meu Senhor Jesus está\nAo meu lado e levará,\nAfinal, ao céu de luz,\nOs que resgatou na cruz.", hymnsNumber: HymnsNumberSeeder.CHILDREN_134, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 570, position: 1, content: "Louvor, louvor, dai ó criancinhas;\nAo Senhor, ao Senhor.\nLouvor, louvor, dai ó criancinhas\nAo Senhor, ao Senhor.", hymnsNumber: HymnsNumberSeeder.CHILDREN_135, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 571, position: 2, content: "Graças, graças, dai ó criancinhas;\nEle é amor, Ele é amor.", hymnsNumber: HymnsNumberSeeder.CHILDREN_135, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 572, position: 3, content: "Glória, glória, dai ó criancinhas\nAo Senhor, ao Senhor.", hymnsNumber: HymnsNumberSeeder.CHILDREN_135, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 573, position: 4, content: "Honra, honra, dai ó criancinhas;\nEle é amor. Ele é amor.", hymnsNumber: HymnsNumberSeeder.CHILDREN_135, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 574, position: 5, content: "Reverência, dai ó criancinhas\nEle é amor, Ele é amor.", hymnsNumber: HymnsNumberSeeder.CHILDREN_135, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 575, position: 1, content: "Há várias e lindas histórias\nNo Santo Livro de Deus;\nQue explicam a verdadeira justiça\nDos homens que serviram a Deus.", hymnsNumber: HymnsNumberSeeder.CHILDREN_136, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 576, position: 2, content: "Ao ler, de José, a história,\nVendido como escravo,\nMas sendo grande juiz depois;\nPerdoou seus malfeitores.", hymnsNumber: HymnsNumberSeeder.CHILDREN_136, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 577, position: 3, content: "Gostamos, de Moisés, a história:\nBebé escondido no canavial,\nCrescido, dirigente se tornou,\nDos filhos de Israel.", hymnsNumber: HymnsNumberSeeder.CHILDREN_136, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 578, position: 4, content: "David foi bom pastor,\nQue amava as suas ovelhas,\nSendo depois o grande rei,\nReinou com coragem e justiça.", hymnsNumber: HymnsNumberSeeder.CHILDREN_136, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 579, position: 5, content: "Na Bíblia há grande história,\nQue todos tanto gostamos:\nDe Jesus abençoando as crianças;\nQue se tornou nosso amigo.", hymnsNumber: HymnsNumberSeeder.CHILDREN_136, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 580, position: 1, content: "Manda-nos luzir o Senhor Jesus\nComo quando a vela dá de noite a luz.\nQuer que nós brilhemos com a luz do céu;\nTu no teu cantinho, e eu no meu.", hymnsNumber: HymnsNumberSeeder.CHILDREN_137, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 581, position: 2, content: "Ele primeiro a luz para Si requer,\nPercebendo logo se ela enfraquecer.\nSempre a luz mostremos que Jesus nos deu;\nTu no teu cantinho, e eu no meu.", hymnsNumber: HymnsNumberSeeder.CHILDREN_137, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 582, position: 3, content: "Ao redor, então, manda a luz raiar,\nPorque muitas trevas há que dissipar.\nPara reluzirmos Deus nos acendeu,\nTu no teu cantinho e eu no meu.", hymnsNumber: HymnsNumberSeeder.CHILDREN_137, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 583, position: 1, content: "Jesus me guia, que prazer!\nPalavra de consolação!\nEm todo o transe em que estiver,\nMe guia sempre a Sua mão.", hymnsNumber: HymnsNumberSeeder.CHILDREN_138, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 584, content: "Jesus me guia, que prazer!\nÉ sua mão que me conduz,\nEm cada passo me é mister\nQue me dirija meu Jesus.", hymnsNumber: HymnsNumberSeeder.CHILDREN_138, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 585, position: 2, content: "Às vezes, quando em aflições,\nNo meio de perigo e dor,\nPor água mansa ou bravo mar\nMe guia a mão do meu Senhor.", hymnsNumber: HymnsNumberSeeder.CHILDREN_138, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 586, position: 3, content: "Ajuda-me a não murmurar,\nQualquer que for a condição;\nContente vou, pois guiarás\nPor Tua mui bondosa mão.", hymnsNumber: HymnsNumberSeeder.CHILDREN_138, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 587, position: 4, content: "E quando a morte a mim vier\nE a minha vida aqui ceifar,\nPor Ti guardado, meu Senhor,\nContigo espero então morar.", hymnsNumber: HymnsNumberSeeder.CHILDREN_138, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 588, position: 1, content: "Ouço o clamor do bom Pastor,\nPelo deserto assolador,\nSeus cordeirinhos a chamar,\nMui desejoso de os salvar.", hymnsNumber: HymnsNumberSeeder.CHILDREN_139, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 589, content: "Vai buscar! vai buscar!\nMeus cordeirinhos vai buscar!\nVai buscar! vai buscar!\nPara que os possa abençoar.", hymnsNumber: HymnsNumberSeeder.CHILDREN_139, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 590, position: 2, content: "Quem não deseja auxiliar\nSeus cordeirinhos a guardar,\nE encaminhá-los a Jesus,\nFonte de vida, amor e luz?", hymnsNumber: HymnsNumberSeeder.CHILDREN_139, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 591, position: 3, content: "Pelo deserto a padecer,\nPelas estradas a morrer,\nSeus cordeirinhos vai buscar,\nPara que os possa abençoar.", hymnsNumber: HymnsNumberSeeder.CHILDREN_139, typeStanza: HymnsContentCode.VERSE),
  ];
}