import 'package:ieca_mobile/enums/_import.dart';
import 'package:ieca_mobile/models/_import.dart';
import 'package:ieca_mobile/seeders/portugues/_import.dart';

class HymnsChristianHome {
  static List<HymnsContent> list =[

    HymnsContent(id: 494, position: 1, content: "Vem, Deus, abençoar\nEsta santa união,\nA lei fortalecer\nCom Tua imensa bênção.\nRaiando aos noivos\nA graça e paz,\nConsagra-os na alegria,\nVerdade e amor.", hymnsNumber: HymnsNumberSeeder.CHRISTIAN_HOME_118, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 495, position: 2, content: "Ó fonte de amor,\nA Ti nós entregamos\nOs noivos tão queridos\nÀ Tua nobre graça.\nProtege-os de todo\nO mal deste mundo,\nVem erguer sua casa\nNa rocha viva e justa.", hymnsNumber: HymnsNumberSeeder.CHRISTIAN_HOME_118, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 496, position: 3, content: "Que ergam o seu lar\nNa graça e reverência,\nA paz do salvador\nQue reine neste lar.\nAinda que tentados,\nQue tenham coragem,\nE vindo a tristeza,\nQue sejam sempre fortes.", hymnsNumber: HymnsNumberSeeder.CHRISTIAN_HOME_118, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 497, position: 4, content: "Em todo seu labor,\nAvante na alegria;\nLigados sempre estejam,\nNas coisas verdadeiras.\nSeu trilho ilumina,\nDa vida de aquém,\nMantém seus corações,\nSim, na vida de além.", hymnsNumber: HymnsNumberSeeder.CHRISTIAN_HOME_118, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 498, position: 1, content: "Naquele dia nas bodas,\nDe Caná da Galileia.\nComo hóspede Jesus ali,\nRevelou-lhes o seu amor.", hymnsNumber: HymnsNumberSeeder.CHRISTIAN_HOME_119, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 499, position: 2, content: "Quando Jesus viveu aqui,\nEle não teve lar algum.\nMas alegrou-se com todos,\nQue tinham obtido os seus lares.", hymnsNumber: HymnsNumberSeeder.CHRISTIAN_HOME_119, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 500, position: 3, content: "Aos noivos, nós imploramos,\nQue venhas os abençoar.\nQue seu enlace sê fiel,\nGuardando os votos em união.", hymnsNumber: HymnsNumberSeeder.CHRISTIAN_HOME_119, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 501, position: 4, content: "Que tenham fé, confiança e amor,\nQuando tentados por Satã.\nE tudo quanto faz manchar,\nA sua santa união.", hymnsNumber: HymnsNumberSeeder.CHRISTIAN_HOME_119, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 502, position: 5, content: "Que formem um lar de paz,\nDe honra e de compaixão.\nUnidos num só coração,\nPropenso à justiça e paz.", hymnsNumber: HymnsNumberSeeder.CHRISTIAN_HOME_119, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 503, position: 6, content: "Que no seu lar venha abundar,\nA santa paz que vem dos céus.\nQue tenham sempre Cristo ali,\nO guardador fiel dos lares.", hymnsNumber: HymnsNumberSeeder.CHRISTIAN_HOME_119, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 504, position: 1, content: "Abençoado é o lar que caminha\nNa obediência de Jesus Senhor;\nMesmo o Seu amor ali resplandece.\nE mal nenhum o pode destruir.", hymnsNumber: HymnsNumberSeeder.CHRISTIAN_HOME_120, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 505, position: 2, content: "Abençoado é o lar onde todos\nVivem em amor e em santidade.\nO sofrimento não pode apagar\nA luz celeste que ali reluz.", hymnsNumber: HymnsNumberSeeder.CHRISTIAN_HOME_120, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 506, position: 3, content: "Abençoado é o lar se os pais\nDão a Jesus as suas criancinhas.\nE consagrados todos em Jesus Cristo,\nO bom Pastor dos cordeirinhos.", hymnsNumber: HymnsNumberSeeder.CHRISTIAN_HOME_120, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 507, position: 4, content: "Abençoado é o lar consagrado\nNa adoração sincera a Jesus.\nMesmo choupana, ela é abençoada,\nSe o reconhecem como o Seu Senhor.", hymnsNumber: HymnsNumberSeeder.CHRISTIAN_HOME_120, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 508, position: 5, content: "E quando finda a nossa vida terrena,\nNo lar eterno havemos de chegar,\nAquele, preparado para os cristãos,\nO lar de paz que nunca acabará.", hymnsNumber: HymnsNumberSeeder.CHRISTIAN_HOME_120, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 509, position: 1, content: "Perfeito amor, além do entendimento,\nCom devoção buscamos teu favor;\nVem pois fazer perfeito o casamento\nDos filhos teus que se unem pelo amor.", hymnsNumber: HymnsNumberSeeder.CHRISTIAN_HOME_121, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 510, position: 2, content: "Dá-lhe Senhor, confiança eterna e mútua\nE fé constante em Cristo, o Salvador.\nÀs suas almas dá vigor e nutre\nNa busca do mais nobre e puro amor.", hymnsNumber: HymnsNumberSeeder.CHRISTIAN_HOME_121, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 511, position: 3, content: "Vem conceder-lhes força e alegria\nNos dissabores, lutas, provações,\nQue assim conservem juntos a harmonia;\nPerfeitos tendes sempre os corações.", hymnsNumber: HymnsNumberSeeder.CHRISTIAN_HOME_121, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 512, position: 1, content: "Que bendita, que gloriosa luz!\nÉ mais bela que a do dia!\nIlumina as almas a seguir\nCristo, o Mestre, o eterno Guia.", hymnsNumber: HymnsNumberSeeder.CHRISTIAN_HOME_122, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 513, content: "Luz bendita, luz gloriosa,\nConcedida lá dos altos céus\nSó ao crente em Cristo, o Redentor.\nÓ bendita luz de Deus!", hymnsNumber: HymnsNumberSeeder.CHRISTIAN_HOME_122, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 514, position: 2, content: "Sempre canta alegre seu louvor\nQuem possui a luz dos céus;\nGoza afável e celeste paz,\nDom gracioso só de Deus.", hymnsNumber: HymnsNumberSeeder.CHRISTIAN_HOME_122, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 515, position: 3, content: "Vida nova em santa rectidão\nTêm os crentes em Jesus;\nQuem de perto ao Salvador seguir\nD’Ele alcança graça e luz.", hymnsNumber: HymnsNumberSeeder.CHRISTIAN_HOME_122, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 516, position: 4, content: "Esperança, vida e salvação\nHás-de Ter, ó pecador,\nAceitando Cristo, nossa Luz,\nEste Dom do Deus de amor!", hymnsNumber: HymnsNumberSeeder.CHRISTIAN_HOME_122, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 517, position: 1, content: "Jesus, a Ti queremos\nAgora oferecer\nOs nossos pequeninos,\nPrimícias do viver.", hymnsNumber: HymnsNumberSeeder.CHRISTIAN_HOME_123, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 518, position: 2, content: "Entrando nesta vida\nJá têm perigos mil.\nValei-lhes, pois, ó Cristo,\nContra o inimigo vil.", hymnsNumber: HymnsNumberSeeder.CHRISTIAN_HOME_123, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 519, position: 3, content: "Faze que sempre tenham\nNo coração amor\nAos Santos mandamentos,\nPalavra do Senhor.", hymnsNumber: HymnsNumberSeeder.CHRISTIAN_HOME_123, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 520, position: 4, content: "Oh, dobre a Tua graça\nOs zelos paternais,\nGuarda as suas mentes\nDas ilusões fatais.", hymnsNumber: HymnsNumberSeeder.CHRISTIAN_HOME_123, typeStanza: HymnsContentCode.VERSE),

  ];
}