import 'package:ieca_mobile/_import.dart';

class HymnsChurch{
  static List<HymnsContent> list = [

    HymnsContent(id: 383, position: 1, content: "Cabeça da Igreja, és ó Cristo.\nUm alicerce firme e poderoso,\nSendo o Seu povo pedras do edifício.\nVem com ela habitar.", hymnsNumber: HymnsNumberSeeder.CHURCH_92, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 384, position: 2, content: "Tu que morreste pela Tua Igreja,\nPara que seja Santa, imaculada,\nTestemunhando a Tua salvação,\nCom Espírito e o poder.", hymnsNumber: HymnsNumberSeeder.CHURCH_92, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 385, position: 3, content: "Um coração dedicado concede,\nA Tua Igreja Cristo Salvador,\nP´ra que barreiras todas desfeitas,\nPossa melhor servir.", hymnsNumber: HymnsNumberSeeder.CHURCH_92, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 386, position: 4, content: "Tua Igreja somos, Senhor,\nPor todo o canto mande pregar,\nPara que o mundo possa ouvir,\nA Tua salvação.", hymnsNumber: HymnsNumberSeeder.CHURCH_92, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 387, position: 1, content: "Vem ó Salvador, habita\nJuntamente a Tua Igreja,\nDirigí-la e ajudá-la,\nÉ o que Te rogamos.", hymnsNumber: HymnsNumberSeeder.CHURCH_93, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 388, position: 2, content: "Que Ela santa sempre seja,\nEmpenhada em caridade.\nSua fé aumente sempre,\nÉ o que Te rogamos.", hymnsNumber: HymnsNumberSeeder.CHURCH_93, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 389, position: 3, content: "Que Ela evite o silêncio,\nAnte coisas imperfeitas;\nAdvertindo com firmeza,\nÉ o que Te rogamos.", hymnsNumber: HymnsNumberSeeder.CHURCH_93, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 390, position: 4, content: "Seja guia para os cegos,\nE procure os perdidos.\nQue sustente os famintos,\nÉ o que Te rogamos.", hymnsNumber: HymnsNumberSeeder.CHURCH_93, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 391, position: 5, content: "Resplandeça onde há trevas\nO Teu brilho de justiça,\nE a salvação proclame,\nÉ o que Te rogamos.", hymnsNumber: HymnsNumberSeeder.CHURCH_93, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 392, position: 6, content: "Santifique e a purifique,\nNos aspectos em que peca,\nP'ra que seja imaculada,\nÉ o que Te rogamos.", hymnsNumber: HymnsNumberSeeder.CHURCH_93, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 393, position: 1, content: "O reino do Senhor,\nExalto-O com louvor;\nSua Igreja salva está,\nCom o sangue de Jesus.", hymnsNumber: HymnsNumberSeeder.CHURCH_94, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 394, position: 2, content: "Sofro por Seu amor;\nOrando sem cessar;\nContinuamente trabalhar\n‘Té o labutar findar.", hymnsNumber: HymnsNumberSeeder.CHURCH_94, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 395, position: 3, content: "Sublime e santo é,\nO gozo lá nos céus.\nSua justiça viverei\nCom gratidão e paz.", hymnsNumber: HymnsNumberSeeder.CHURCH_94, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 396, position: 4, content: "Os crentes em Jesus,\nNa verdade verão,\nJá nesta vida, o prazer,\nE gozo lá nos céus.", hymnsNumber: HymnsNumberSeeder.CHURCH_94, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 397, position: 1, content: "Jesus, o alicerce é\nDo Templo do Senhor.\nQuem fora d’Ele construir\nSuporte não terá,\nSuporte não terá.", hymnsNumber: HymnsNumberSeeder.CHURCH_95, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 398, position: 2, content: "Jesus, o alicerce é\nPilar seguro é.\nO lar cristão edificou,\nCom todo o Seu poder,\nCom todo o Seu poder.", hymnsNumber: HymnsNumberSeeder.CHURCH_95, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 399, position: 3, content: "Jesus, o alicerce é\nDa Sua Igreja aqui,\nQue sempre prega o eterno dom,\nJustiça, graça e paz.\nJustiça, graça e paz.", hymnsNumber: HymnsNumberSeeder.CHURCH_95, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 400, position: 4, content: "Como esta casa aqui se fez\nO Templo do Senhor,\nOs nossos corações também,\nQue sejam, teus, Jesus.\nQue sejam, teus, Jesus.", hymnsNumber: HymnsNumberSeeder.CHURCH_95, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 401, position: 1, content: "Da Igreja o alicerce\nÉ Cristo o Salvador;\nEm Seu poder descansa;\nÉ forte em Seu amor.\nEnquanto Ele permanece\nEla continuará\nE nele fortalecida,\nJamais perecerá.", hymnsNumber: HymnsNumberSeeder.CHURCH_96, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 402, position: 2, content: "A pura e sã doutrina\nDimana de Jesus,\nE faz a Sua Igreja\nAndar em plena luz;\nO nosso Deus benigno\nPromulga justas leis\nE a todo o mundo manda\nCurvar-se ao Rei dos reis.", hymnsNumber: HymnsNumberSeeder.CHURCH_96, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 403, position: 3, content: "A pedra preciosa\nQue Deus predestinou,\nSustenta pedras vivas\nQue a graça preparou.\nE concluída a obra\nQue a graça já conduz\nA glória do edifício\nToda será Jesus.", hymnsNumber: HymnsNumberSeeder.CHURCH_96, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 404, position: 4, content: "Senhor, a nossa oferta\nAceita com favor,\nE o nosso humilde esforço resulte em\nTeu louvor Os que por\nTi trabalham,\nCom Teu poder sustém!\nE as graças te daremos\nEternamente: Ámen.", hymnsNumber: HymnsNumberSeeder.CHURCH_96, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 405, position: 1, content: "Chuvas de bênçãos teremos,\nÉ a promessa de Deus.\nTempos benditos veremos,\nChuvas de bênçãos dos céus.", hymnsNumber: HymnsNumberSeeder.CHURCH_97, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 406, content: "Chuvas de bênçãos,\nChuvas de bênçãos dos céus;\nGotas de bênçãos já temos;\nChuvas rogamos a Deus.", hymnsNumber: HymnsNumberSeeder.CHURCH_97, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 407, position: 2, content: "Chuvas de bênçãos teremos,\nVida de paz e perdão,\nOs pecadores indignos,\nGraças dos céus obterão.", hymnsNumber: HymnsNumberSeeder.CHURCH_97, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 408, position: 3, content: "Chuvas de bênçãos teremos,\nManda-nos já, ó Senhor!\nDá-nos agora o bom fruto\nDesta palavra de amor.", hymnsNumber: HymnsNumberSeeder.CHURCH_97, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 409, position: 4, content: "Chuvas de bênçãos teremos,\nChuvas mandadas dos céus,\nBênçãos a todos os crentes,\nBênçãos do nosso bom Deus.", hymnsNumber: HymnsNumberSeeder.CHURCH_97, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 410, position: 1, content: "Jesus, Pastor amado,\nJuntos eis-nos aqui!\nConcede que sejamos\nUm corpo só em Ti.\nContendas e malícias\nQue longe de nós vão!\nNenhum pecado impeça\nA nossa santa união.", hymnsNumber: HymnsNumberSeeder.CHURCH_98, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 411, position: 2, content: "Uma família somos,\nFamília de Jesus;\nE uma só, a vida,\nQue nos nasceu na cruz.\nA mesma fé nos une\nNum só divino amor\nE com o mesmo gozo\nServimos ao Senhor.", hymnsNumber: HymnsNumberSeeder.CHURCH_98, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 412, position: 3, content: "Num só caminho estreito\nDeus mesmo nos conduz;\nNão temos esperança\nSenão num só, Jesus;\nSua preciosa morte\nA todos vida traz;\nE pelo mesmo sangue\nNos vem a mesma paz.", hymnsNumber: HymnsNumberSeeder.CHURCH_98, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 413, position: 1, content: "Senhor da ceifa, atende\nA nossa petição:\nQue o bom trabalho siga\nCom vera animação.\nOs campos já branquejam;\nConvidam a ceifar,\nE os preciosos frutos\nNa Igreja arrecadar.", hymnsNumber: HymnsNumberSeeder.CHURCH_99, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 414, position: 2, content: "Somente a Ti compete\nCeifeiros escolher;\nQue façam o serviço\nConforme o teu querer.\nOs ânimos prepara,\nInflama os corações,\nE manda os bons obreiros,\nEm grandes multidões.", hymnsNumber: HymnsNumberSeeder.CHURCH_99, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 415, position: 3, content: "Aumenta em nossas almas\nConsagração e fé.\nNa lida não permitas\nQue nos vacile o pé.\nE cada vez mais fortes,\nMais cheios de fervor,\nConcede que mostremos\nA graça do Senhor.", hymnsNumber: HymnsNumberSeeder.CHURCH_99, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 416, position: 1, content: "Eis-nos ó Pastor divino!\nTodos juntos num lugar,\nComo ovelhas congregados\nTeu auxílio a suplicar;\nSê presente,\nO rebanho a apascentar.", hymnsNumber: HymnsNumberSeeder.CHURCH_100, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 417, position: 2, content: "Aos perdidos em pecado\nSeu perigo faz sentir.\nOh! Reclama os transviados\nDeixa-os Tua voz ouvir;\nAos enfermos,\nPrestes digna-Te acudir.", hymnsNumber: HymnsNumberSeeder.CHURCH_100, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 418, position: 3, content: "Guia os tristes, fatigados\nAo aprisco do Senhor.\nLeva os tenros cordeirinhos,\nNo Teu seio, Bom Pastor;\nÀs pastagens\nDe celeste e doce amor.", hymnsNumber: HymnsNumberSeeder.CHURCH_100, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 419, position: 4, content: "Ó Jesus! Escuta os rogos\nDesta humilde petição.\nVem encher o Teu rebanho\nDe sincera gratidão;\nCantaremos\nTua imensa compaixão.", hymnsNumber: HymnsNumberSeeder.CHURCH_100, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 420, position: 1, content: "Benditos laços são\nOs do fraterno amor,\nQue nesta santa comunhão\nNos unem ao Senhor.", hymnsNumber: HymnsNumberSeeder.CHURCH_101, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 421, position: 2, content: "Ao mesmo trono vão\nAs nossas petições;\nÉ mútuo o gozo ou aflição\nDos nossos corações.", hymnsNumber: HymnsNumberSeeder.CHURCH_101, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 422, position: 3, content: "Aqui tudo é comum,\nO rir e o chorar;\nEm Cristo somos todos um\nNo gozo e no lidar.", hymnsNumber: HymnsNumberSeeder.CHURCH_101, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 423, position: 4, content: "Se desta santa união\nNos vamos separar,\nNo céu eterna comunhão\nHavemos de gozar.", hymnsNumber: HymnsNumberSeeder.CHURCH_101, typeStanza: HymnsContentCode.VERSE),

  ];
}