import 'package:ieca_mobile/_import.dart';

class HymnsEvening {
  static List<HymnsContent> list = [
    HymnsContent(id: 1479, position: 1, content: "Adeus. Adeus. Adeus.\nDescem sombras vespertinas,\nPerto está a escuridão.\nLá no céu, serenas, belas,\nResplandecem as estrelas,\nAdeus. Adeus. Adeus.", hymnsNumber: HymnsNumberSeeder.EVENING_HYMNS_349, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1480, position: 2, content: "Adeus. Adeus. Adeus.\nAtravés da noite escura\nHá quem guarda bem os Seus,\nO Pastor constante e terno,\nNosso Protector Supremo.\nAdeus. Adeus. Adeus.", hymnsNumber: HymnsNumberSeeder.EVENING_HYMNS_349, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1481, position: 3, content: "Adeus. Adeus. Adeus.\nAo nascer o novo dia\nLouvaremos ao Senhor\nPelo amor que nos atende\nE que sempre nos defende.", hymnsNumber: HymnsNumberSeeder.EVENING_HYMNS_349, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 1482, position: 1, content: "Salvador, por Ti guardados,\nDesejamos descansar;\nOs defeitos e os pecados\nTu nos podes perdoar.", hymnsNumber: HymnsNumberSeeder.EVENING_HYMNS_350, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1483, position: 2, content: "Se de noite algum perigo\nNossos leitos investir,\nTeu amor nos dê abrigo\nE nos deixe em paz dormir.", hymnsNumber: HymnsNumberSeeder.EVENING_HYMNS_350, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1484, position: 3, content: "Dos teus olhos, trevas densas\nNão nos podem ocultar;\nTeu cuidado nos dispensas\nNum constante vigiar.", hymnsNumber: HymnsNumberSeeder.EVENING_HYMNS_350, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1485, position: 4, content: "Se esta noite adormecermos\nPara o nosso fim mortal,\nSeja para que acordemos\nNa mansão celestial.", hymnsNumber: HymnsNumberSeeder.EVENING_HYMNS_350, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 1486, position: 1, content: "À tarde, veio a multidão,\nDo povo fraco e sofredor,\nChegando com sua aflição\nAos santos pés do Salvador.", hymnsNumber: HymnsNumberSeeder.EVENING_HYMNS_351, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1487, position: 2, content: "Quem veio com doença e dor,\nVoltou curado p'ra o lar,\nAbençoando, o Salvador,\nQue a todos pode aliviar.", hymnsNumber: HymnsNumberSeeder.EVENING_HYMNS_351, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1488, position: 3, content: "E Tua poderosa mão\nIgual virtude ainda tem,\nA que valeu a multidão,\nÀ nós, a mesma bênção vem.", hymnsNumber: HymnsNumberSeeder.EVENING_HYMNS_351, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1489, position: 4, content: "Pois nós, também, Jesus Senhor,\nComo eles, estamos sem poder;\nContamos com o mesmo amor\nAgora, para nos valer.", hymnsNumber: HymnsNumberSeeder.EVENING_HYMNS_351, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1490, position: 5, content: "Ó Cristo viemos hoje a Ti,\nRogando a mesma compaixão;\nOh, sobre nós, Teus aqui,\nEstende a Tua santa mão.", hymnsNumber: HymnsNumberSeeder.EVENING_HYMNS_351, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 1491, position: 1, content: "Ó Deus, o dia que nos deu findou;\nTrouxeste outra vez a noite.\nCom coros matinais nós Te louvamos.\nÀ noite também, a Ti graças nós daremos.", hymnsNumber: HymnsNumberSeeder.EVENING_HYMNS_352, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1492, position: 2, content: "O sol nos deixou, p'ra visitar também\nOutra gente noutros países.\nTodas as horas, línguas de várias partes,\nCantam os louvores do amor de Deus.", hymnsNumber: HymnsNumberSeeder.EVENING_HYMNS_352, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1493, position: 3, content: "Na escuridão perdeu-se a luz da manhã,\n'Inda assim, é Tua a Igreja.\nEm todo o mundo ela não desiste,\nEm cantar louvores ao Deus da Salvação.", hymnsNumber: HymnsNumberSeeder.EVENING_HYMNS_352, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1494, position: 4, content: "O reino de nosso Deus jamais terá fim,\nTal como um reino terreno.\nEle persiste com poder e glória,\nAté que todos creiam somente Nele.", hymnsNumber: HymnsNumberSeeder.EVENING_HYMNS_352, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 1495, position: 1, content: "Comigo assiste, ó Deus! A noite vem,\nAs trevas crescem, eis, Senhor, convém\nQue me socorra a Tua protecção;\nOh! vem fazer comigo habitação!", hymnsNumber: HymnsNumberSeeder.EVENING_HYMNS_353, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1496, position: 2, content: "Depressa encontrarei o fim mortal;\nDesaparece o gozo terreal;\nMudança vejo em tudo e corrupção;\nComigo faze eterna habitação!", hymnsNumber: HymnsNumberSeeder.EVENING_HYMNS_353, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1497, position: 3, content: "Vem revelar-me teu querer, Senhor!\nDivino Mestre, Rei, Consolador!\nMeu Guia forte, Amparo em tentação!\nVem, vem fazer comigo habitação!", hymnsNumber: HymnsNumberSeeder.EVENING_HYMNS_353, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1498, position: 4, content: "Presente estás nas trevas e na luz;\nNão há perigo andando com Jesus;\nA morte e a tumba não aterrarão\nÀquele em quem fizer habitação!", hymnsNumber: HymnsNumberSeeder.EVENING_HYMNS_353, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1499, position: 5, content: "Ó morte! em Cristo gozo e redenção!\nSepulcro! o pó verá ressurreição!\nNo reino além não há perturbação.\nCom Deus eu herdo eterna habitação.", hymnsNumber: HymnsNumberSeeder.EVENING_HYMNS_353, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 1500, position: 1, content: "Vai fugindo o dia,\nBreve a noite vem,\nVespertina estrela\nJá se avista além.", hymnsNumber: HymnsNumberSeeder.EVENING_HYMNS_354, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1501, position: 2, content: "Ao que mui cansado\nNa tristeza jaz,\nDá, Jesus bendito,\nTeu descanso e paz.", hymnsNumber: HymnsNumberSeeder.EVENING_HYMNS_354, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1502, position: 3, content: "Noite de sossego\nVimos Te pedir;\nQue por Ti guardados,\nVamos nós dormir.", hymnsNumber: HymnsNumberSeeder.EVENING_HYMNS_354, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1503, position: 4, content: "Quando despertamos,\nSeja, bom Senhor,\nPara Te servirmos\nCom maior vigor.", hymnsNumber: HymnsNumberSeeder.EVENING_HYMNS_354, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 1504, position: 1, content: "Estrela da noite, nosso Jesus,\nSol da justiça, do dia a luz,\nTodas as trevas vem dissipar;\nVem nossas almas iluminar.", hymnsNumber: HymnsNumberSeeder.EVENING_HYMNS_355, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1505, position: 2, content: "Vida dos mortos e salvação\nDos que contritos buscam perdão.\nTu, que consolo aos tristes dás,\nVem mesmo agora dar-nos a paz.", hymnsNumber: HymnsNumberSeeder.EVENING_HYMNS_355, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1506, position: 3, content: "Pastor bondoso, meigo Jesus,\nQue padeceste na amarga cruz,\nVives agora p'ra nos guardar;\nVem Teu rebanho apascentar.", hymnsNumber: HymnsNumberSeeder.EVENING_HYMNS_355, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1507, position: 4, content: "Ó Rei divino, eterno Deus,\nSenhor da terra, do mar e céus.\nTodo o pecado vem subjugar;\nVem hoje mesmo em nós reinar.", hymnsNumber: HymnsNumberSeeder.EVENING_HYMNS_355, typeStanza: HymnsContentCode.VERSE),

  ];
}