import 'package:ieca_mobile/_import.dart';

class HymnsFutureLife {
  static List<HymnsContent> list = [
    HymnsContent(id: 1384, position: 1, content: "Ó Deus, gratos estamos\nPela memória\nDos teus Obreiros\nPor Ti recebidos.\nNós Te louvamos;\nOs glorificaste;\nAleluia! Aleluia!", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_328, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1385, position: 2, content: "Tu ó Deus, os tinhas fortificado;\nDe todos os sofrimentos\nOs ajudaste\nE ainda Eras a Sua\nLuz Santa;\nAleluia! Aleluia!", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_328, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1386, position: 3, content: "Quando as tentações\nMil nos atacam,\nOu se nos enfraquece\nO sofrimento,\nFaz lembrar-nos os já vitoriosos,\nAleluia! Aleluia!", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_328, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1387, position: 4, content: "Dê-nos, pois, corações\nDe persistência,\nTirando deles\nSempre o exemplo da fé,\nP'ra como eles, sermos triunfantes.\nAleluia! Aleluia!", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_328, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1388, position: 5, content: "Graças à Ti pela\nNossa unidade,\nCom os que entraram\nNo Teu repouso,\nPois a sua salvação\nTambém é nossa.\nAleluia! Aleluia!", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_328, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 1389, position: 1, content: "Há uma terra de prazer,\nMorada dos que crêem.\nO dia eterno reina ali,\nTristezas nunca têm. (2x)", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_329, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1390, position: 2, content: "É primavera sempre ali,\nE as flores durarão\nAlegres campos, verdes, bons,\nNa linda terra estão. (2x)", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_329, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1391, position: 3, content: "Porém, à entrada do país\nJaz um profundo mar.\nPor suas águas nós, mortais,\nHavemos de passar. (2x)", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_329, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1392, position: 4, content: "Os viajantes, com temor,\nÀ vista desse mar,\nTransidos tremem de terror,\nE querem recuar. (2x)", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_329, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1393, position: 5, content: "Mas Cristo já caminho abriu,\nTirou da morte a dor.\nCom gozo os salvos hão-de entrar\nNaquele lar de amor. (2x)", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_329, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 1394, position: 1, content: "Se de mágoas de pecados\nPrestes está a desfalecer;\nConfiando em Jesus Cristo,\nEle bem te ajudará.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_330, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1395, content: "Vai contigo em toda a parte\nEle te ajudará.\nNa alegria e na tristeza,\nTeu amigo bem leal.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_330, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 1396, position: 2, content: "Com o coração bem triste,\nDe aflição e de temores,\nConfiando em Jesus Cristo,\nTe consola com amor", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_330, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1397, position: 3, content: "Quando a vida e perturbada,\nChame pelo Salvador\nCrendo Nele para sempre,\nNunca mais te perderás.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_330, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1398, position: 4, content: "Quando terminar a vida,\nE da morte ir além,\nTeu auxílio, Jesus Cristo,\nDe alegria Te encherá.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_330, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 1399, position: 1, content: "Anseio a paz que qual um rio corre,\nQue no deserto faz brotar a flor.\nAnseio a fé que neste pouco tempo\nDescansa no bendito Salvador.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_331, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1400, position: 2, content: "Há pouco tempo p'ra velar orando,\nE contra o inimigo batalhar.\nHá pouco tempo para aqui, chorando,\nLançar semente para então ceifar.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_331, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1401, position: 3, content: "Por pouco tempo, as lâmpadas zelando,\nDevemos, bem despertos, esperar;\nAssim pois pressuroso o noivo vindo\nCom Ele nós iremos pra seu lar.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_331, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 1402, position: 1, content: "Cá sofremos aflição,\nCá desgostos perto estão,\nMas lá no céu há paz.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_332, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1403, content: "Oh! Será alegre,\nAlegre, sim, alegre!\nOh, será alegre,\nOnde não há separação.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_332, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 1404, position: 2, content: "Muitas vezes, com pezar,\nTemos de nos apartar\nDos mais amados aqui.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_332, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1405, position: 3, content: "Todos que amam ao Senhor,\nSalvos pelo Seu favor,\nCom Ele vão morar.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_332, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1406, position: 4, content: "Criancinhas lá estarão,\nQue alcançaram a salvação\nPor meio de Jesus.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_332, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1407, position: 5, content: "Vivos hemos de encontrar,\nOs que nos custou a deixar\nNo mundo triste aqui.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_332, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 1408, position: 1, content: "Ao Senhor Jesus louvai,\nSua graça aplaudi.\nJunto d'Ele estaremos,\nNo Seu país, lá no céu.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_333, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1409, content: "Quando lá chegarmos nós,\nAlegria eterna teremos.\nLá Jesus veremos;\nSeu nome vamos glorificar.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_333, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 1410, position: 2, content: "Passageiros somos na terra,\nCom embaraços sem fim,\nMas, terminada a jornada,\nA canseira acabará.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_333, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1411, position: 3, content: "Companheiros, prossigamos,\nCom coragem O seguir.\nSe O servirmos nesta terra,\nSua glória vamos ver.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_333, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1412, position: 4, content: "Desejamos bom galardão,\nQue achamos no Seu país;\nEm breve a porta vai abrir\nNos farão entrar no Céu.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_333, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 1413, position: 1, content: "Qual o adorno desta vida?\nÉ o amor. É o amor.\nAlegria é concedida\nPelo amor. Pelo amor.\nÉ benigno é paciente,\nNão se torna maldizente,\nNão se torna maldizente\nEste meigo amor.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_334, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1414, position: 2, content: "Com suspeitas não se alcança\nDoce amor. Doce amor.\nOnde houver desconfiança,\nAi do amor! Ai do amor!\nPois mostremos tolerância,\nMuitas vezes a arrogância,\nMuitas vezes a arrogância,\nMurcha e mata o amor.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_334, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1415, position: 3, content: "Ó cristão, ao teu vizinho\nMostra amor. Mostra amor.\nO valor não é mesquinho\nDeste amor. Deste amor.\nO supremo Deus nos ama,\nCristo para os céus nos chama,\nCristo para os céus nos chama,\nOnde reina o amor.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_334, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 1416, position: 1, content: "No país celestial,\nLá há imensa glória;\nTal beleza é eterna,\nPois, lá, noite não há mais, não.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_335, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1417, content: "Lágrimas, Deus limpará;\nLá não haverá mais morte.\nAnos, jamais contarão,\nPois, lá, morte não há mais, não.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_335, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 1418, position: 2, content: "Os portões são mui brilhantes,\nTal país é de glória.\nSuas ruas são de Ouro;\nE lá, noite não há mais, não.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_335, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1419, position: 3, content: "Os portões lá nunca fecham,\nTal país é de glória;\nMal nenhum ali penetra,\nE lá, noite não há mais, não.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_335, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1420, position: 4, content: "Lá jamais sol haverá,\nTal país é de glória\nPois Deus O iluminará,\nE lá, noite não há mais, não.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_335, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 1421, position: 1, content: "Quando lá do céu descendo\nPara os seus Jesus voltar,\nE o clarim de Deus\nA todos proclamar.\nQue chegou o grande Dia\nDo triunfo do meu Rei\nEu por sua imensa\nGraça lá estarei.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_336, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1422, content: "Quando enfim, chegar o Dia\nDo triunfo do meu Rei,\nQuando enfim chegar o Dia,\nPela graça de Jesus eu lá estarei!", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_336, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 1423, position: 2, content: "Nesse dia quando os mortos\nHão-de a voz de Cristo ouvir,\nE dos seus sepulcros\nHão-de ressurgir.\nOs remidos reunidos\nLogo aclamarão seu Rei,\nE por sua imensa\nGraça lá estarei.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_336, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1424, position: 3, content: "Pelo mundo rejeitado\nFoi Jesus meu Salvador;\nDesprezaram, insultaram\nMeu Senhor;\nMas faustoso vem o dia\nDo triunfo do meu Rei,\nE por Sua imensa\nGraça lá estarei.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_336, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1425, position: 4, content: "De mim mesmo nada tenho\nEm que possa confiar,\nMas Jesus morreu na cruz\nP'ra me salvar;\nTão somente n’Ele espero;\nSim, e sempre esperarei\nPois por Sua imensa\nGraça lá estarei.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_336, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 1426, position: 1, content: "Na presença, estar, de Cristo,\nEm Sua glória, que será?\nLá no céu, em pleno gozo,\nMinha alma o verá.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_337, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1427, content: "Face a face, espero vê-lo,\nNo além do céu de luz;\nFace a face em plena glória,\nHei de ver o meu Jesus.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_337, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 1428, position: 2, content: "Oh! que glória será ve-lo!\nQue O possa eu mirar!\nEis, em breve vem o dia,\nQue Sua glória há-de mostrar.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_337, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1429, position: 3, content: "Quanto gozo há em Cristo,\nQuando não houver mais dor,\nQuando cessar o perigo,\nE gozarmos pleno amor.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_337, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1430, position: 4, content: "Face a face, quão glorioso,\nHá de ser o existir,\nVendo o rosto de quem veio\nNossas almas redimir!", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_337, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 1431, position: 1, content: "Na cidade de Deus\nNão entra o pecador.\nÉ toda brilho,\nÉ toda brilho,\nSem mancha seu fulgor.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_338, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1432, position: 2, content: "Com bondade perdoa\nAo pobre pecador.\nLava-me as culpas,\nLava-me as culpas,\nBendito Salvador.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_338, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1433, position: 3, content: "Teu filho quero ser\nP'ra sempre meu Senhor.\nÉs meu amparo,\nÉs meu amparo,\nContra o vil tentador.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_338, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1434, position: 4, content: "Ah! Quando lá estiver,\nSalvo p'la Tua cruz,\nPuro, sem mancha,\nPuro, sem mancha,\nGozarei Tua luz.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_338, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 1435, position: 1, content: "Jesus, caminho É,\nDa vida lá no céu.\nEle conduz-nos, e O seguimos\nÀ glória lá no céu.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_339, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1436, content: "Lá, sofrimento não há,\nLá, sofrimento não há.\nMuita alegria lá veremos,\nLá, sofrimento não há.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_339, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 1437, position: 2, content: "Encontremos em Jesus\nAs lindas casas no céu.\nHá alguém que já nos preparou,\nAs lindas casas no céu.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_339, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1438, position: 3, content: "Virá p'ra nos buscar,\nP'ra lá com Ele estar.\nTal como outrora prometeu,\nP'ra lá com Ele estar.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_339, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 1439, position: 1, content: "Oh, pensai desse lar lá do céu,\nBem ao lado do rio de luz,\nOnde os santos p'ra sempre já gozam\nDa presença de nosso Jesus.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_340, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1440, content: "Lá no céu, lá no céu.\nOh, pensai desse lar lá no céu.\nLá no céu, lá no céu.\nOh, pensai desse lar lá no céu.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_340, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 1441, position: 2, content: "Oh, pensai dos amigos lá no céu,\nQue a jornada já têm acabado,\nE dos cantos que soam nos ares\nNo palácio de Deus preparado.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_340, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1442, position: 3, content: "Hei-de ver lá do céu meu Jesus,\nFace a face seu rosto mirar.\nLonge, longe de cuidados e tristezas,\nCom Jesus vou p'ra sempre morar.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_340, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 1443, position: 1, content: "Cristo, do céu em breve virá,\nEle afirmou-nos e não tardará.\nOh, que alegria, que glória será,\nQuando Jesus regressar!", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_341, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1444, content: "Cristo não tarda, não tarda a voltar,\nCristo não tarda a voltar.\nOh, que alegria, que glória será,\nQuando Jesus regressar!", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_341, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 1445, position: 2, content: "Em breve os mortos ressurgirão.\nTodos os crentes, oh, se encontrarão!\nJuntos alegres, ao céu subirão,\nQuando Jesus regressar.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_341, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1446, position: 3, content: "Breve esta terra gozará paz;\nPreso, encerrado, será Satanás!\nTudo o que é vil deixaremos atrás,\nQuando Jesus regressar.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_341, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1447, position: 4, content: "Cristo, não tarda, não tarda em vir;\nQuando será no glorioso porvir?\nOh! que alegria teremos em ir,\nQuando Jesus regressar.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_341, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 1448, position: 1, content: "Jerusalém de Jehová\nNos altos céus está.\nQuando hei de lá chegar,\nCom alegria ali?", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_342, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1449, position: 2, content: "No seu monte de Sião\nHabita ali Jehová.\nJunto do rio de vida,\nEstá ali a Sua mansão.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_342, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1450, position: 3, content: "Há lá uma fonte de David,\nTambém o sol de Deus.\nHá uma árvore de vida\nQue cura as almas.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_342, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1451, position: 4, content: "Aquela terra é linda\nTal como a primavera.\nNão há doença e fome,\nTão pouco há lá dor.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_342, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 1452, position: 1, content: "Os corações dos justos\nEstão já na mão de Deus.\nFindaram a sua jornada;\nFindaram toda a sua lida.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_343, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1453, position: 2, content: "Os incautos dizem:\nÉ mera perca de tempo\nNós, porém, convicto estamos:\nMudaremos p’ra vida eterna.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_343, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1454, position: 3, content: "O poder de Deus\nÉ o que os guarda e sempre,\nSua vida é abundante;\nEternamente gozarão.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_343, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1455, position: 4, content: "Como o ouro brilha,\nDeus assim os vai provar,\nP'ra serem mesmo fortes.\nIsento da impureza.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_343, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1456, position: 5, content: "A nossa vida terrena\nÉ como um sonho a noite;\nA nossa força carnal\nDesaparece sem tardar.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_343, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 1457, position: 1, content: "Em breve a vida vai findar;\nAqui não mais eu cantarei,\nPorque no Céu irei morar,\nLá na presença do meu Rei.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_344, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1458, content: "E face a face vê-Lo-ei!\nDe graça salvo, cantarei!\nE face a face vê-Lo ei\nDe graça salvo cantarei", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_344, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 1459, position: 2, content: "Chegando o dia de esplendor,\nQuando Jesus me vier buscar,\nBem certo estou de que o Senhor\nNo Céu a mim dará lugar.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_344, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1460, position: 3, content: "Ali a voz me soará\nDe Cristo, eterno Redentor:\n«Fiel, bom servo, bem está;\nDesfruta o gozo do Senhor».", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_344, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1461, position: 4, content: "Por meu Jesus eu vou viver,\nFazer a minha luz brilhar,\nE cada dia vou fazer\nAquilo que ao Senhor honrar.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_344, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 1462, position: 1, content: "Com Jesus há morada feliz,\nPrometida e segura nos céus,\nAvistamos o santo país\nPela fé na palavra de Deus.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_345, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1463, content: "No celeste porvir!\nCom Jesus no celeste porvir.\nCom Jesus no celeste porvir.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_345, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 1464, position: 2, content: "Pacientes podemos penar,\nSe sofrermos por nosso Jesus,\nPois sem culpa, sem falta ou pesar,\nViveremos no Reino da luz.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_345, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1465, position: 3, content: "De descanso perfeito, eternal,\nDesfrutando o labor que passou,\nCantaremos em tom triunfal,\nOs louvores de quem nos amou.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_345, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 1466, position: 1, content: "Breve, em glória, Cristo virá.\nDia alegre p'ra mim será.\nFindas tristezas, lutas, a dor,\nLogo veremos Cristo, o Senhor!", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_346, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1467, content: "Dourada aurora Cristo virá.\nDourada aurora, glória será.\nAs duras lutas, passadas já,\nVitorioso, Cristo virá!", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_346, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 1468, position: 2, content: "Que alegria, nos céus de luz;\nGozo eterno com meu Jesus.\nNum só momento, com o Senhor,\nSim, transformado por Seu amor!", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_346, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1469, position: 3, content: "Oh, que encontro, lá com Jesus,\nPaz infinita, eterna luz.\nCom os remidos, entoarei\nGratos louvores ao grande Rei!", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_346, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 1470, position: 1, content: "Fonte de amor perene,\nManancial de luz;\nÁgua da vida corre\nDo trono de Jesus,\nCalmo rio! belo rio!\nQuero estar também\nOnde as água sempre correm\nDesse rio além.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_347, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1471, position: 2, content: "Muitos de nós já foram\nCantar essa harmonia,\nQue as lindas harpas tocam\nCom santa melodia.\nSanto rio! Junto ao rio,\nVou cantar também,\nOnde as vozes nunca cessam,\nNa Jerusalém.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_347, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1472, position: 3, content: "Límpida fonte corre.\nBrilhante corre a flux.\nQuem fez aquela alvura?\nO sangue de Jesus!\nCorre rio, calmo corre!\nCorra assim a paz\nEm minha alma para sempre,\nCorra mais e mais.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_347, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 1473, position: 1, content: "Eu avisto uma terra feliz,\nOnde irei para sempre morar;\nHá mansões nesse lindo país,\nQue Jesus foi p'ra nós preparar.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_348, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1474, content: "Vou morar, vou morar\nNessa terra, celeste porvir!?", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_348, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 1475, position: 2, content: "Cantarei nesse lindo país\nBelos hinos ao meu Salvador,\nPois ali viverei bem feliz,\nSem angústias, tristezas, nem dor.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_348, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1476, content: "Vou cantar, vou cantar\nNessa terra...", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_348, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 1477, position: 3, content: "Deixarei este mundo afinal\nPara ir a Jesus adorar;\nNessa linda cidade real\nMil venturas sem fim vou gozar.", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_348, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 1478, content: "Vou gozar, vou gozar\nNessa terra...", hymnsNumber: HymnsNumberSeeder.FUTURE_LIFE_348, typeStanza: HymnsContentCode.CHOIR),

  ];
}