import 'package:ieca_mobile/_import.dart';

class HymnsHolyCommunion{
  static List<HymnsContent> list = [

    HymnsContent(id: 438, position: 1, content: "Aqui reunidos, vemos o Senhor;\nAqui sentados, vemos Deus dos Céus,\nE prometemos toda confiança;\nAqui pensamos Deus em todo ser.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_105, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 439, position: 2, content: "O Pão da vida comemos aqui;\nAqui bebemos da vide dos céus;\nAqui a bênção da vida fruir,\nE o perdão de Cristo receber.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_105, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 440, position: 3, content: "Aqui, despida toda agonia,\nFortalecidos com poder de Deus;\nQue nos é dado pelo seu perdão.\nPois, sacrifício por nós padeceu.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_105, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 441, position: 4, content: "Cristo é justiça que nos libertou;\nDas impurezas nos santificou;\nDe vestes brancas, Ele nos vestiu,\nP’ra sermos coerdeiros lá nos céus.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_105, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 442, position: 5, content: "Agora finda a nossa Oração;\nMas sempre unidos no amor de Deus.\nFinda a partilha do vinho e do pão,\nCristo, Senhor conosco sempre está.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_105, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 443, position: 1, content: "Enquanto, ó Salvador,\nTeu livro ler,\nMeus olhos vem abrir,\nTe quero ver\nDa mera letra, além,\nA Ti, Senhor;\nEu busco a Ti, Jesus,\nMeu Salvador.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_106, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 444, position: 2, content: "À beira-mar, Jesus\nPartiste o pão,\nSatisfazendo ali\nA multidão;\nDa vida o pão és Tu,\nVem, pois assim,\nSatisfazer, Senhor,\nA mim, a mim.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_106, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 445, position: 3, content: "Libertadoras são\nAs Tuas leis,\nEternamente sãs\nE bem fiéis.\nDe toda a escravidão\nVem-me livrar,\nE na verdade, ó Deus,\nMeus pés guiar.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_106, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 446, position: 4, content: "Assim, p’ra te servir\nLivre serei,\nFazer o que Te apraz,\nDesejarei.\nTudo serás então\nP’ra mim, Senhor,\nE deleitar-me-ei\nNo Teu amor.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_106, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 447, position: 1, content: "Da Ceia participar,\nReconheço meu Senhor;\nQue foi morto e ressurgiu;\nSenhor, eu Te louvarei.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_107, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 448, position: 2, content: "Estes símbolos aqui,\nDo Caminho para os céus;\nRevelando o amor do Pai.\nSenhor, eu Te louvarei.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_107, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 449, position: 3, content: "Que eles ergam os olhos meus\nPara a cruz do meu Jesus;\nConvidando-me a aceitar.\nSenhor, eu Te louvarei.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_107, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 450, position: 4, content: "Ao tomar o Santo Pão,\nVejo o Corpo do Senhor;\nQue imolado foi na Cruz.\nSenhor, eu Te louvarei.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_107, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 451, position: 5, content: "Como o vinho santo é,\nO sinal do sangue Seu;\nVertido na Cruz por mim,\nSenhor, eu Te louvarei.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_107, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 452, position: 1, content: "Tributai, ó vós, remidos,\nGratos hinos a Jesus;\nTendes certa a herança eterna,\nConquistada pela cruz.\nOh! Cantai alegremente,\nPois derradeiro dia,\nA Jesus encontrareis.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_108, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 453, position: 2, content: "Nesta vida achais tristezas,\nMorte, dor, separação;\nAchareis no céu tesouros\nQue jamais se perderão.\nNa cidade gloriosa\nReina Cristo em esplendor;\nNão há pranto nem pecado\nNa presença do Senhor.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_108, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 454, position: 3, content: "Para as bodas do Cordeiro,\nVós, remidos, entrareis,\nE do fruto da videira\nNo seu reino bebereis.\nExultai, pois, alegrai-vos,\nQue vereis ali Jesus;\nLouvareis, eternamente\nAo Cordeiro em santa luz.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_108, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 455, position: 1, content: "Faminto, ó Salvador,\nManá celestial,\nEu venho a Ti, Senhor,\nPão vivo, divinal!", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_109, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 456, position: 2, content: "Sedento, clamo a Ti,\nÓ Rocha secular!\nVem, água viva, aqui\nMinha alma saciar!", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_109, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 457, position: 3, content: "Meu pobre coração\nAlmeja, ó Senhor,\nEm doce comunhão\nGozar o Teu amor.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_109, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 458, position: 4, content: "Não sei por onde vou,\nMas isto sei, Senhor:\nNa Tua mão estou,\nE Tu és todo amor!", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_109, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 459, position: 1, content: "Videira certa é Jesus\nSendo Seu Pai o lavrador.\nCeamos juntos, árvore És Tu.\nE nós já somos um em Ti.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_110, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 460, position: 2, content: "O pão da vida nosso Jesus,\nCeamos e a recordar.\nSeu corpo entregue por nós na Cruz,\nNos alimenta o coração.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_110, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 461, position: 3, content: "Na Tua mesa o vinho,\nBebemos e a recordar.\nO seu precioso sangue que verteu,\nNos purifica o coração.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_110, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 462, position: 4, content: "Comamos juntos ó irmãos,\nE Sua morte recordar.\nTemos saudades, ante a morte\nJesus vai nos ressuscitar.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_110, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 463, position: 1, content: "Bom é o alimento\nDa graça do Bom Deus.\nP’ra os crentes é apetitoso,\nO alimento lá do Céu.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_111, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 464, position: 2, content: "Como irmãos unidos\nCá estamos nós aqui.\nCeando o pão do Céu,\nE bebendo o vinho.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_111, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 465, position: 3, content: "O pão simboliza\nO corpo de Jesus.\nO vinho simboliza\nO sangue de Jesus.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_111, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 466, position: 4, content: "De tudo nos recorda\nComo por nós morreu.\nTambém nos apresenta,\nComo Ele nos amou.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_111, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 467, position: 5, content: "Ó Deus, nós Te adoramos,\nMil graças damos nós.\nPor Ter-nos dado o seu Filho,\nO nosso Salvador.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_111, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 468, position: 1, content: "Ó Cristo, Pão da vida,\nDescido desde o céu,\nPão para as nossas almas,\nQue o Pai Celeste deu.\nEm Ti nos alegramos,\nFartando-nos aqui\nDo alento e da doçura\nQue achamos sempre em Ti.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_112, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 469, position: 2, content: "Da santa e eterna vida\nDa qual Tu és o Autor,\nSustento e alimento\nÉs Tu também, Senhor.\nSem Ti não nos assistem\nNem forças, nem poder,\nE só em Ti comendo\nPodemos bem viver.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_112, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 470, position: 3, content: "Ó Cristo, Pão da vida,\nA Ti louvamos nós,\nE ao Pai, gratos, erguemos\nA nossa alegre voz.\nHumildes celebramos\nO amor que forneceu\nP'ra nosso alimento\nO santo Pão do céu.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_112, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 471, position: 1, content: "Ó pão, só por amor partido,\nÓ vinho dado por amor!\nÓ Cristo, vida nos tens sido:\nMorreste pelo pecador!", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_113, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 472, position: 2, content: "Ó pão, só por graça partido,\nSedento está o nosso coração!\nCom tempestades deste mundo,\nTransforma-nos, Deus compassivo.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_113, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 473, position: 3, content: "Ó pão, só por paciência partido,\nFraco está o nosso coração!\nDá-nos a força do Espírito,\nP'ra que vençamos as tentações.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_113, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 474, position: 4, content: "Ao pecador atribulado,\nConsolo agora lhe vem dar;\nDe tua Ceia, ó Cristo amado,\nNossa alma vem alimentar.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_113, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 475, position: 5, content: "Ó Cristo, nosso Salvador,\nEternamente reina em nós.\nQuebra os grilhões do vil pecado,\nQue impedem em nós ver Tua glória.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_113, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 476, position: 1, content: "Pão da vida, Pão dos céus,\nPão de Deus é meu Jesus;\nPão que dá ao coração\nAlegria paz e luz.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_114, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 477, position: 2, content: "Sangue que Jesus verteu,\nÉ divino, é eficaz;\nEste sangue ao coração\nForça, amor e vida traz.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_114, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 478, position: 3, content: "Vem Jesus me abençoar;\nEnche-me de Ti, Senhor.\nPois almejo Te servir,\nE Te honrar, meu Salvador.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_114, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 479, position: 1, content: "Quem me poderá salvar?\nCristo que verteu Seu sangue.\nOnde as manchas vou limpar?\nSó no Seu precioso sangue.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_115, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 480, content: "Oh! que preciosa paz,\nQue vem da Sua cruz!\nA qual me dá Jesus\nPelo Seu precioso sangue!", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_115, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 481, position: 2, content: "Vejo a minha salvação\nSó no Seu precioso sangue.\nDeus concede-me perdão\nPelo precioso sangue.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_115, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 482, position: 3, content: "Dele vem perfeita paz\nPelo Seu precioso sangue.\nInfalível e eficaz,\nEsse tão precioso sangue.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_115, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 483, position: 4, content: "Minha justificação\nTenho  no precioso sangue;\nGozo traz ao coração\nEsse tão precioso sangue.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_115, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 484, position: 5, content: "Entrarei no céu, enfim,\nPelo seu precioso sangue;\nLouvarei, então, sem fim,\nEsse tão precioso sangue.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_115, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 485, position: 1, content: "Que consolação tem meu coração,\nDescansando no poder de Deus.\nEle tem prazer em me proteger,\nDescansando no poder de Deus.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_116, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 486, content: "Descansando\nNos eternos braços do meu Deus,\nEstou seguro,\nDescansando no poder de Deus.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_116, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 487, position: 2, content: "Sempre avante vou, bem contente estou,\nDescansando no poder de Deus.\nTudo hei-de vencer pelo seu poder,\nDescansando no poder de Deus.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_116, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 488, position: 3, content: "Não recearei, nada temerei,\nDescansando no poder de Deus.\nGozo paz e amor junto ao meu Senhor,\nDescansando no poder de Deus.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_116, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 489, position: 4, content: "Lutas sem cessar hei de atravessar,\nDescansando no poder de Deus.\nNão me deixará, mas me susterá,\nDescansando no poder de Deus.", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_116, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 490, position: 1, content: "Seja Bendito o Cordeiro\nQue na cruz por nós padeceu!\nSeja bendito o Seu sangue\nQue por nós pecadores verteu!\nEis, nesse sangue lavados,\nCom roupas que tão alvas são,\nOs pecadores remidos,\nQue perante seu Deus hoje estão!", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_117, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 491, content: "Alvo mais que a neve!\nAlvo mais que a neve!\nSim, nesse sangue lavado,\nMais alvo que a neve serei!", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_117, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 492, position: 2, content: "Quão espinhosa a coroa\nQue Jesus por nós suportou!\nOh! Quão profundas as chagas\nQue nos provam o quanto Ele amou!\nEis, nessas chagas, pureza\nPara o mais torpe pecador\nPois que mais alvos que a neve\nO teu sangue nos torna, Senhor!", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_117, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 493, position: 3, content: "Se nós a ti confessarmos,\nE seguirmos na tua luz,\nTu não somente perdoas,\nPurificas também, ó Jesus!\nSim, e de todo o pecado!\nQue maravilha desse amor!\nPois que mais alvos que a neve\nO teu sangue nos torna, Senhor!", hymnsNumber: HymnsNumberSeeder.HOLY_COMMUNION_117, typeStanza: HymnsContentCode.VERSE),

  ];
}