import 'package:ieca_mobile/models/_import.dart';
import 'package:ieca_mobile/seeders/portugues/_import.dart';

class HymnsYouth {
  static List<HymnsContent> list =[
    HymnsContent(id: 592, position: 1, content: "O mundo é tão lindo\nE cheio está de prazeres,\nMas eu não sigo o mundo.\nPorque há de acabar.\nMinh'alegria é esta,\nPortanto nela estou:\nSeguir p'ra todo o sempre,\nSeguir a Jesus.", hymnsNumber: HymnsNumberSeeder.YOUTH_140, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 593, content: "Hei de seguir a Cristo,\nHei de seguir a Cristo,\nHei de seguir a Cristo\nEternamente.", hymnsNumber: HymnsNumberSeeder.YOUTH_140, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 594, position: 2, content: "Não obstante ser um jovem\nCaminho com Jesus,\nMas dizem que é horrendo\nEnquanto não chegar.\nE dizem que é imenso,\nO sofrimento alí.\nPorém eu sigo a Cristo,\nEternamente.", hymnsNumber: HymnsNumberSeeder.YOUTH_140, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 595, position: 3, content: "Sou um peregrino certo,\nE tudo hei de encontrar.\nTudo hei de dar a Cristo,\nE Ele há de atender.\nEle há de socorrer-me\nE as lágrimas limpar.\nAlegremente hei de seguir\nEternamente.", hymnsNumber: HymnsNumberSeeder.YOUTH_140, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 596, position: 4, content: "O sofrimento e medo,\nNem outra aflição.\nCom Cristo ao meu lado,\nNão tenho o que temer;\nMesmo que a morte venha,\nHei de ressuscitar.\nHei de ficar com Cristo\nEternamente.", hymnsNumber: HymnsNumberSeeder.YOUTH_140, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 597, position: 1, content: "Eterna Luz Celeste,\nDeus Omnipotente,\nNossa escola amada,\nCom Teu favor reveste.", hymnsNumber: HymnsNumberSeeder.YOUTH_141, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 598, position: 2, content: "Aqui as almas vençam\nPensamentos baixos,\nSeja-lhes provada,\nA paz da Tua bênção.", hymnsNumber: HymnsNumberSeeder.YOUTH_141, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 599, position: 3, content: "À vera sapiência\nPura, luminosa,\nSeja acrescentada,\nHumilde reverência.", hymnsNumber: HymnsNumberSeeder.YOUTH_141, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 600, position: 4, content: "As aulas acabadas,\nVamos nós alegres\nMinistrando sempre\nCom almas consagradas.", hymnsNumber: HymnsNumberSeeder.YOUTH_141, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 601, position: 5, content: "Nas lutas desta vida\nGraça nos dispensa,\nAté enfim chegarmos\nÀ Terra Prometida.", hymnsNumber: HymnsNumberSeeder.YOUTH_141, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 602, position: 1, content: "Convidados pelo amor\nDe Jesus o Salvador,\nVamos nós seguindo a luz\nEspalhada pela cruz.", hymnsNumber: HymnsNumberSeeder.YOUTH_142, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 603, position: 2, content: "Esperança divinal\nDuma herança imortal,\nIlumina o coração\nAtravés da escuridão.", hymnsNumber: HymnsNumberSeeder.YOUTH_142, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 604, position: 3, content: "Santo exemplo de Jesus!\nComo brilha a Sua luz!\nE nos manda com amor\nReflectir o Seu fulgor.", hymnsNumber: HymnsNumberSeeder.YOUTH_142, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 605, position: 4, content: "Manda-nos também lidar;\nSempre contra o mal lutar;\nOs Seus servos bons terão\nPrecioso galardão.", hymnsNumber: HymnsNumberSeeder.YOUTH_142, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 606, position: 5, content: "Na tristeza ou tentação,\nGrande é a consolação\nDe Jesus connosco estar\nP'ra fazer-nos triunfar.", hymnsNumber: HymnsNumberSeeder.YOUTH_142, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 607, position: 1, content: "O meu coração sofredor\nDescanso seguro encontrou,\nSeguindo os conselhos de amor\nDo Pai que do mal me chamou.", hymnsNumber: HymnsNumberSeeder.YOUTH_143, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 608, content: "Cantai, cantai\nNo templo de nosso Senhor!\nCantai, cantai!\nAo mundo mostrai seu amor!", hymnsNumber: HymnsNumberSeeder.YOUTH_143, typeStanza: HymnsContentCode.CHOIR),
    HymnsContent(id: 609, position: 2, content: "Nos astros esparsos nos céus,\nDa lua no brando clarão,\nEu leio poemas de Deus\nQue outorga aos contritos perdão.", hymnsNumber: HymnsNumberSeeder.YOUTH_143, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 610, position: 3, content: "No livro bendito encontrei\nPalavras de amor e de luz\nE canto celeste escutei\nDos anjos, saudando Jesus.", hymnsNumber: HymnsNumberSeeder.YOUTH_143, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 611, position: 4, content: "Os males do mundo olvidei,\nPor isso  me pus a cantar;\nCom Deus para sempre estarei,\nIrei com Jesus ao seu lar.", hymnsNumber: HymnsNumberSeeder.YOUTH_143, typeStanza: HymnsContentCode.VERSE),

    HymnsContent(id: 612, position: 1, content: "Ó jovens, que ventura\nVos é servir a Deus!\nCom vida santa e pura\nCorrer caminho aos céus;\nChegai-vos sem demora\nA Cristo, o Salvador;\nAproveitai agora,\nFugi da eterna dor.", hymnsNumber: HymnsNumberSeeder.YOUTH_144, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 613, position: 2, content: "Por que só na velhice\nServir a Deus quereis?\nTambém, quem foi que disse\nQue lá vós chegareis?\nNão dura a mocidade\nMais que mimosa flor;\nCorrei com brevidade\nA dar-vos ao Senhor.", hymnsNumber: HymnsNumberSeeder.YOUTH_144, typeStanza: HymnsContentCode.VERSE),
    HymnsContent(id: 614, position: 3, content: "Que pobre sacrifício\nA Deus oferecer:\nDeixar pecado e vício\nSó ao envelhecer!\nSe endureceis vossa alma\nÀ santa vocação,\nLembrai que Deus condena\nA vossa dilação.", hymnsNumber: HymnsNumberSeeder.YOUTH_144, typeStanza: HymnsContentCode.VERSE),

  ];
}