import 'package:ieca_mobile/models/_import.dart';
import 'package:ieca_mobile/seeders/portugues/_import.dart';

class LitanyContentSeeder{

  static List<LitanyContent> items() {
    return [
      LitanyContent(id: 1, position: 1, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.CALL_TO_WORSHIP, books: "Habacuque 2:20", content: "O Senhor está no seu santo templo: cale-se diante dele toda a terra"),
      LitanyContent(id: 2, position: 2, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.CALL_TO_WORSHIP, books: "Salmo 85:8", content: "Escutarei o que Deus, o Senhor, disser; porque falará de paz ao seu povo"),
      LitanyContent(id: 3, position: 3, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.CALL_TO_WORSHIP, books: "Salmo 46:1", content: "Deus é o nosso refúgio e fortaleza, socorro bem presente na angústia"),
      LitanyContent(id: 4, position: 4, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.CALL_TO_WORSHIP, books: "Salmo 46:2", content: "Pelo que não temeremos, ainda que a terra se mude, e ainda que os montes se transportem para o meio dos mares"),
      LitanyContent(id: 5, position: 5, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.CALL_TO_WORSHIP, books: "Salmo 130:7", content: "Esperai no Senhor, porque no Senhor há abundante misericórdia"),
      LitanyContent(id: 6, position: 6, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.CALL_TO_WORSHIP, books: "Salmo 130:5", content: "Aguardo ao Senhor; a minha alma o aguarda, e espero na sua palavra"),
      LitanyContent(id: 7, position: 7, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.CALL_TO_WORSHIP, books: "Salmo 34:3", content: "Engrandecei ao Senhor comigo, e juntos exaltemos o seu nome"),
      LitanyContent(id: 8, position: 8, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.CALL_TO_WORSHIP, books: "Salmo 96:4", content: "Porque grande é o Senhor, e digno de louvor"),
      LitanyContent(id: 9, position: 9, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.CALL_TO_WORSHIP, books: "Salmo 103:8", content: "Misericordioso e piedoso é o Senhor: longânimo e grande em benignidade"),
      LitanyContent(id: 10, position: 10, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.CALL_TO_WORSHIP, books: "Salmo 103:10", content: "Não nos tratou segundo os nossos pecados, nem nos retribuiu segundo as nossas iniquidades"),
      LitanyContent(id: 11, position: 11, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.CALL_TO_WORSHIP, books: "Salmo 103:11", content: "Quanto o céu está elevado acima da terra, assim é grande a sua misericórdia para com os que o temem"),
      LitanyContent(id: 12, position: 12, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.CALL_TO_WORSHIP, books: "Salmo 103:12", content: "Quanto está longe o oriente do ocidente, assim afasta de nós as nossas transgressões"),
      LitanyContent(id: 13, position: 13, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.CALL_TO_WORSHIP, books: "Provérbios 3:5", content: "Confia no Senhor de todo o teu coração, e não te estribes no teu próprio entendimento"),
      LitanyContent(id: 14, position: 14, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.CALL_TO_WORSHIP, books: "Salmo 139:23-24", content: "Sonda-me, ó Deus, e conhece o meu coração: prova-me, e conhece os meus pensamentos. E vê se há em mim algum caminho mau, e guia-me pelo caminho eterno"),
      LitanyContent(id: 15, position: 15, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.CALL_TO_WORSHIP, books: "I João 3:1", content: "Vede quão grande caridade nos tem concedido o Pai; que fôssemos chamados filhos de Deus"),
      LitanyContent(id: 16, position: 16, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.CALL_TO_WORSHIP, books: "II Coríntios 9:15", content: "Graças a Deus, pelo seu dom inefável"),

      LitanyContent(id: 17, position: 17, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.CONFESSION_OF_SIN, books: "Salmo 80:1-2", content: "Ó Pastor de Israel, dá ouvidos; tu, que guias a José como a um rebanho, que te assentas entre os Querubins, resplandece! Perante Efraim, Benjamim e Manassés, desperta o teu poder, e vem salvar-nos"),
      LitanyContent(id: 18, position: 18, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.CONFESSION_OF_SIN, books: "Salmo 80:7", content: "Faze-nos voltar, ó Deus dos exércitos; faze resplandecer o teu rosto, e seremos salvos"),
      LitanyContent(id: 19, position: 19, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.CONFESSION_OF_SIN, books: "Salmo 80:8-9", content: "Trouxeste uma vinha do Egipto: lançaste fora as nações, e a plantaste. Preparaste-lhe lugar e fizeste com que ela aprofundasse raízes, e assim encheu a terra"),
      LitanyContent(id: 20, position: 20, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.CONFESSION_OF_SIN, books: "Salmo 80:7", content: "Faze-nos voltar, ó Deus dos exércitos; faze resplandecer o teu rosto, e seremos salvos"),
      LitanyContent(id: 21, position: 21, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.CONFESSION_OF_SIN, books: "Salmo 80:14-15", content: "Ó Deus dos exércitos, volta-te, nós te rogamos, atende dos céus, e vê, e visita esta vinha; e a videira que a tua dextra plantou, e o sarmento que fortificaste para ti"),
      LitanyContent(id: 22, position: 22, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.CONFESSION_OF_SIN, books: "Salmo 80:7", content: "Faze-nos voltar, ó Deus dos exércitos; faze resplandecer o teu rosto, e seremos salvos"),
      LitanyContent(id: 23, position: 23, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.CONFESSION_OF_SIN, books: "", content: "Tem misericórdia de nós, ó Deus, segundo a tua benignidade; apaga as nossas transgressões, segundo a multidão das tuas misericórdias. Cria em nós, ó Deus, um coração puro, e renova em nós um espírito recto"),
      LitanyContent(id: 24, position: 24, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.CONFESSION_OF_SIN, books: "Salmo 80:7", content: "Faze-nos voltar, ó Deus dos exércitos; faze resplandecer o teu rosto, e seremos salvos"),
      LitanyContent(id: 25, position: 25, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.CONFESSION_OF_SIN, books: "Salmo 51:2,4,12,15,17", content: "Lava-nos completamente da nossa iniquidade, e purifica-nos do nosso pecado. Contra ti, contra ti somente pecamos, e fizemos o que a teus olhos parece mal, para que sejas justificado quando falares, e puro quando julgares. Abre, Senhor, os nossos lábios, e a nossa boca entoará o teu louvor. Torna a dar-nos a alegria da tua salvação, e sustém-nos com um espírito voluntário. A um coração quebrantado e contrito não desprezarás, ó Deus"),
      LitanyContent(id: 26, position: 26, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.CONFESSION_OF_SIN, books: "Salmo 80:7", content: "Faze-nos voltar, ó Deus dos exércitos; faze resplandecer o teu rosto, e seremos salvos"),
      LitanyContent(id: 27, position: 27, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.CONFESSION_OF_SIN, books: "Actos 11:17,18", content: "Se Deus concedeu àqueles gentios o mesmo Dom que a nós, quando havemos crido no Senhor Jesus Cristo, quem era então eu, para que pudesse resistir a Deus? Na verdade, até aos gentios Deus deu o arrependimento para a vida"),
      LitanyContent(id: 28, position: 28, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.CONFESSION_OF_SIN, books: "Salmo 80:7", content: "Faze-nos voltar, ó Deus dos exércitos; faze resplandecer o teu rosto, e seremos salvos"),
      LitanyContent(id: 29, position: 29, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.CONFESSION_OF_SIN, books: "Actos 11:12,15,Romanos 8:14", content: "Disse-me o Espírito que fosse com eles, nada duvidando. E, quando comecei a falar, caiu sobre eles o Espírito Santo como também sobre nós ao princípio. Porque, todos os que são guiados pelo Espírito de Deus, esses são filhos de Deus"),
      LitanyContent(id: 30, position: 30, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.CONFESSION_OF_SIN, books: "Salmo 80:7", content: "Faze-nos voltar, ó Deus dos exércitos; faze resplandecer o teu rosto, e seremos salvos"),
      LitanyContent(id: 31, position: 32, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.CONFESSION_OF_SIN, books: "Actos 12:7-8", content: "Eis que sobreveio o anjo do Senhor, e resplandeceu uma luz na prisão. E disse-lhe o anjo: Cinge-te, e acta as tuas alparcas. Lança às costas a tua capa, e segue-me"),
      LitanyContent(id: 32, position: 32, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.CONFESSION_OF_SIN, books: "Salmo 80:7", content: "Faze-nos voltar, ó Deus dos exércitos; faze resplandecer o teu rosto, e seremos salvos"),
      LitanyContent(id: 33, position: 33, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.CONFESSION_OF_SIN, books: "II. Pedro 3:13; Apocalípse 21:3,4", content: "Mas, segundo a sua promessa, aguardamos novos céus e nova terra, em que habita a justiça. E ouvi uma grande voz, do céu, que dizia: Eis aqui o tabernáculo de Deus com os homens, pois com eles habitará, e eles serão o seu povo, e o mesmo Deus estará com eles e será o seu Deus. E Deus limpará dos seus olhos toda a lágrima; e não haverá mais morte, nem pranto, nem clamor, nem dor; porque já as primeiras coisas são passadas"),
      LitanyContent(id: 34, position: 34, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.CONFESSION_OF_SIN, books: "Salmo 80:7", content: "Faze-nos voltar, ó Deus dos exércitos; faze resplandecer o teu rosto, e seremos salvos"),

      LitanyContent(id: 35, position: 35, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.GENERAL_SUPPLICATION, books: "Isaías 55:6-7", content: "Buscai ao Senhor enquanto se pode achar. Invocai-o enquanto está perto. Deixe o ímpio o seu caminho, e o homem maligno os seus pensamentos, e se converta ao Senhor, que se compadecerá dele. Torne para o nosso Deus, porque grandioso é em perdoar"),
      LitanyContent(id: 36, position: 36, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.GENERAL_SUPPLICATION, books: "Salmo 100:5", content: "Porque o Senhor é bom, e eterna a sua misericórdia. A sua verdade estende-se de geração em geração"),
      LitanyContent(id: 37, position: 37, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.GENERAL_SUPPLICATION, books: "Isaías 1:18; I. João 1:7", content: "Ainda que os vossos pecados sejam como a escarlata, eles se tornarão brancos como a neve; ainda que sejam vermelhos como o carmesim, se tornarão como a branca lã. Se andarmos na luz, como ele na luz está, o sangue de Jesus Cristo, seu Filho, nos purifica de todo o pecado"),
      LitanyContent(id: 38, position: 38, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.GENERAL_SUPPLICATION, books: "I. João 1:8,9", content: "Se dissermos que não temos pecado, enganamo-nos a nós mesmos, e não há verdade em nós. Se confessarmos os nossos pecados, ele é fiel e justo para nos perdoar os pecados, e nos purificar de toda a injustiça"),
      LitanyContent(id: 39, position: 39, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.GENERAL_SUPPLICATION, books: "Provérbios 28:13; I João 1:9; Salmo 32:1", content: "O que encobre as suas transgressões, nunca prosperará; mas o que as confessa e as deixa, alcançará misericórdia. Confessemos, pois, os nossos pecados, porque bem-aventurado é aquele cuja transgressão é perdoada, e cujo pecado é coberto"),
      LitanyContent(id: 40, position: 40, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.GENERAL_SUPPLICATION, books: "Salmo 32:5", content: "Confessei-te o meu pecado, e a minha maldade não encobri; dizia eu: Confessarei ao Senhor as minhas transgressões; e tu perdoaste a maldade do meu pecado"),
      LitanyContent(id: 41, position: 41, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.GENERAL_SUPPLICATION, books: "Jeremias 29:11-13;Lamentações 3: 40-41", content: "Porque eu sei os pensamentos que penso de vós, diz o Senhor: Pensamentos de paz, e não de mal, para vos dar o fim que esperais. Então me invocareis e buscar-me-eis, e me achareis, quando me buscardes de todo o vosso coração. Esquadrinhemos, pois, os nossos caminhos, experimentemo-los, e voltemos ao Senhor. Levantemos os nossos corações para Deus nos céus"),
      LitanyContent(id: 42, position: 42, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.GENERAL_SUPPLICATION, books: "Salmo 51:1,10;139:24", content: "Tem misericórdia de mim, ó Deus, segundo a tua benignidade: apaga as minhas transgressões, segundo a multidão das tuas misericórdias. Cria em mim, ó Deus, um coração puro, e renova em mim um espírito recto. Guia-me pelo caminho eterno"),
      LitanyContent(id: 43, position: 43, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.GENERAL_SUPPLICATION, books: "2 Coríntios 5:21;Romanos 6:11", content: "Àquele que não conheceu pecado, o fez pecado por nós, para que nele fôssemos feitos justiça de Deus. Assim, também, vós, considerai-vos como mortos para o pecado, mas vivos para Deus em Cristo Jesus, nosso Senhor."),
      LitanyContent(id: 44, position: 44, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.GENERAL_SUPPLICATION, books: "Efésios 1:7", content: "Nele temos a redenção pelo seu sangue, a remissão das ofensas, segundo as riquezas da sua graça"),
      LitanyContent(id: 45, position: 45, person: LitanyPerson.DIRECTOR_CONGREGATION, litanyTitle: LitanyTitleSeeder.GENERAL_SUPPLICATION, books: "2 Coríntios 9:15", content: "Graças a Deus, pelo seu Dom inefável"),

      LitanyContent(id: 46, position: 46, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.THANKSGIVING, content: "Dêmos graças a Deus, pela sua revelação a nós homens, através de muitas coisas faladas e feitas, mas especialmente, por nos ter falado pelo seu Filho Jesus Cristo, nosso Salvador, feito nosso Caminho, nossa Verdade e nossa Vida"),
      LitanyContent(id: 47, position: 47, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.THANKSGIVING, content: "Graças a Deus, pelo seu dom inefável"),
      LitanyContent(id: 48, position: 48, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.THANKSGIVING, content: "Dêmos graças a Deus, pela grande testemunha que temos em nossos corações, o Espírito Santo, e pela Obra de Salvação que este Espírito Santo tem consumado em nós, e ainda pela inteligência e força que nos tem proporcionado"),
      LitanyContent(id: 49, position: 49, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.THANKSGIVING, content: "Graças a Deus, pelo seu dom inefável"),
      LitanyContent(id: 50, position: 50, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.THANKSGIVING, content: "Dêmos graças a Deus, pelas Sagradas Escrituras, feitas lâmpadas para os nossos pés e que nos podem guiar para o caminho da salvação e vida eterna. Dêmos-lhe também graças por nos ter enviado aqueles que nos explicaram a sua Santa Palavra e nos indicaram a verdadeira salvação"),
      LitanyContent(id: 51, position: 51, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.THANKSGIVING, content: "Graças a Deus, pelo seu dom inefável"),
      LitanyContent(id: 52, position: 52, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.THANKSGIVING, content: "Dêmos graças a Deus, por nos ter chamado a entrar na Sua Santa Obra e a sermos Seus mensageiros. Dêmos-lhe também graças, porque sabemos que nesta obra estamos a ajudar o povo da nossa terra, contribuindo, deste modo para a expansão do poder da sua Igreja, entre nós"),
      LitanyContent(id: 53, position: 53, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.THANKSGIVING, content: "Graças a Deus, pelo seu dom inefável"),
      LitanyContent(id: 54, position: 54, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.THANKSGIVING, content: "Dêmos graças a Deus, porque não nos deixou na dúvida, porém, deu-nos o compreensível mandato de Nosso Senhor Jesus Cristo, que diz: Ide por todo o mundo e pregai o Evangelho a toda a criatura; e Ele mesmo nos deixou a firme promessa quando disse: Eis que estou convosco todos os dias, até à consumação dos séculos"),
      LitanyContent(id: 55, position: 55, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.THANKSGIVING, content: "Graças a Deus, pelo seu dom inefável"),

      LitanyContent(id: 56, position: 56, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.PETITION_FOR_COURAGE, books: "Salmo 46:1-2", content: "Deus é o nosso refúgio e fortaleza, socorro bem presente na angústia. Pelo que não temeremos, ainda que a terra se mude, e ainda que os montes se transportem para o meio dos mares"),
      LitanyContent(id: 57, position: 57, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.PETITION_FOR_COURAGE, books: "Salmo 23:1-3", content: "O Senhor é meu Pastor: nada me faltará. Deitar-me faz em verdes pastos, guia-me mansamente a águas tranquilas. Refrigera a minha alma; guia-me pelas veredas da justiça, por amor do seu nome"),
      LitanyContent(id: 58, position: 58, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.PETITION_FOR_COURAGE, books: "Salmo 27:1", content: "O Senhor é a minha luz e a minha salvação; a quem temerei? O Senhor é a força da minha vida; de quem me recearei?"),
      LitanyContent(id: 59, position: 59, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.PETITION_FOR_COURAGE, books: "Salmo 23;4", content: "Ainda que eu andasse pelo vale da sombra da morte, não temeria mal algum, porque tu estás comigo; a tua vara e o teu cajado me consolam"),
      LitanyContent(id: 60, position: 60, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.PETITION_FOR_COURAGE, books: "Salmo 91:1-2. 11-12", content: "Aquele que habita no esconderijo do Altíssimo, à sombra do Omnipotente descansará. Direi do Senhor: Ele é o meu Deus, o meu refúgio, a minha fortaleza, e n’Ele confiarei. Porque aos seus anjos dará ordem a teu respeito, para te guardarem em todos os teus caminhos. Eles te sustentarão nas suas mãos, para que não tropeces com o teu pé em pedra"),
      LitanyContent(id: 61, position: 61, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.PETITION_FOR_COURAGE, books: "Salmo 121:7-8", content: "O Senhor te guardará de todo o mal: Ele guardará a tua alma. O Senhor guardará a tua entrada e a tua saída, desde agora e para sempre"),
      LitanyContent(id: 62, position: 63, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.PETITION_FOR_COURAGE, books: "Salmo 91:15-16", content: "Ele me invocará, e eu lhe responderei; estarei com ele na angústia; livrá-lo-ei, e o glorificarei. Dar-lhe-ei abundância de dias, e lhe mostrarei a minha salvação"),
      LitanyContent(id: 63, position: 63, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.PETITION_FOR_COURAGE, books: "Salmo 121:3-5", content: "Não deixará vacilar o teu pé: aquele que te guarda não tosquenejará. Eis que não tosquenejará nem dormirá o guarda de Israel. O Senhor é quem te guarda"),
      LitanyContent(id: 64, position: 64, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.PETITION_FOR_COURAGE, books: "Salmo 119:1-3", content: "Bem-aventurados os que trilham caminhos rectos, e andam na lei do Senhor. Bem-aventurados os que guardam os seus testemunhos, e o buscam de todo o coração, e não praticam iniquidades, mas andam nos seus caminhos"),
      LitanyContent(id: 65, position: 65, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.PETITION_FOR_COURAGE, books: "Salmo 119:5-6", content: "Oxalá os meus caminhos fossem dirigidos de maneira a poder eu observar os teus estatutos. Então não ficaria confundido, atentando eu para todos os teus mandamentos"),

      LitanyContent(id: 66, position: 66, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.CHRISTMAS, content: "Glória seja dada ao Senhor dos Senhores, que foi enviado para a remissão dos homens que prometem fazer a sua vontade"),
      LitanyContent(id: 67, position: 67, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.CHRISTMAS, content: "Em todas as línguas, seja Ele louvado. Aleluia ao Príncipe da Paz!"),
      LitanyContent(id: 68, position: 68, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.CHRISTMAS, books: "Lucas 1:32-33", content: "Ele será grande, e será chamado Filho do Altíssimo; e o Senhor Deus lhe dará o trono de David, seu pai; e reinará eternamente na casa de Jacob, e o seu reino não terá fim"),
      LitanyContent(id: 69, position: 69, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.CHRISTMAS, content: "Em todas as línguas, seja Ele louvado. Aleluia ao Príncipe da Paz!"),
      LitanyContent(id: 70, position: 70, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.CHRISTMAS, books: "Lucas 1:37, 45, 48-50", content: "Porque, para Deus, nada é impossível. Aquele que Nele confia é bem-aventurado, porque as coisas que o Senhor lhe disser serão cumpridas. Maria, a mãe do Senhor, disse: Eis que desde agora, todas as gerações me chamarão bem-aventurada: porque me fez grandes coisas o Poderoso; e Santo é o seu Nome. E a sua misericórdia é de geração em geração, sobre os que o temem"),
      LitanyContent(id: 71, position: 71, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.CHRISTMAS, content: "Em todas as línguas, seja Ele louvado. Aleluia ao Príncipe da Paz!"),
      LitanyContent(id: 72, position: 72, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.CHRISTMAS, books: "Hebreus 1:5-6,8; Isaias 9:6", content: "Assim diz Deus Pai, Tu és meu Filho, hoje te gerei. E disse mais: Eu lhe serei por Pai, e ele me será por Filho. Quando introduz no mundo o primogénito, diz: Todos os anjos de Deus o adorem. O teu trono, ó Deus, subsiste pelos séculos dos séculos; ceptro de equidade é o ceptro do teu reino. E Isaías diz: E o seu nome será: Maravilhoso, Conselheiro, Deus Forte, Pai da eternidade, Príncipe da paz"),
      LitanyContent(id: 73, position: 73, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.CHRISTMAS, content: "Em todas as línguas, seja Ele louvado. Aleluia ao Príncipe da Paz!"),
      LitanyContent(id: 74, position: 74, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.CHRISTMAS, books: "Salmo 18:49;67:5; Romanos 14:11;Ap.7:9-10", content: "Louvar-te-ei, ó Senhor, entre os gentios. Exaltai o Senhor. Louvem-no todos os povos. Diz o Senhor que todo o joelho se dobrará diante de mim, e toda a língua confessará a Deus. Uma multidão incontável, de todas as nações e tribos e povos e línguas, e clamavam com grande voz dizendo: Salvação ao nosso Deus"),
      LitanyContent(id: 75, position: 75, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.CHRISTMAS, content: "Em todas as línguas, seja Ele louvado. Aleluia ao Príncipe da Paz!"),

      LitanyContent(id: 76, position: 76, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.PALM_SUNDAY, content: "A multidão que ia adiante e a que seguia, clamava dizendo: Hozana ao Filho de David; bendito o que vem em nome do Senhor! Louvemos ao Senhor. Deus é Espírito e importa que os que O adoram, O adorem em Espírito e em verdade. Dai graças ao Senhor e bendizei o seu nome; anunciai entre todos os povos as suas maravilhas. Cantai-lhe hinos de louvor, inclinai os vossos corações a Deus"),
      LitanyContent(id: 77, position: 77, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.PALM_SUNDAY, content: "Apresentamos a Deus os nossos corações"),
      LitanyContent(id: 78, position: 78, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.PALM_SUNDAY, content: "Senhor, abre os nossos olhos"),
      LitanyContent(id: 79, position: 79, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.PALM_SUNDAY, content: "Para que vejamos as maravilhas da Tua Palavra"),
      LitanyContent(id: 80, position: 80, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.PALM_SUNDAY, content: "Senhor, abre as nossas bocas"),
      LitanyContent(id: 81, position: 81, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.PALM_SUNDAY, content: "Para que delas saia todo o louvor"),
      LitanyContent(id: 82, position: 82, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.PALM_SUNDAY, content: "Louvai ao Senhor"),
      LitanyContent(id: 83, position: 83, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.PALM_SUNDAY, content: "O nome do Senhor seja louvado"),

      LitanyContent(id: 84, position: 84, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.EASTER, content: "Glória ao Pai, e ao Filho, e ao Espírito Santo"),
      LitanyContent(id: 85, position: 85, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.EASTER, content: "Como era no princípio, agora e sempre, por todos os séculos. Ámen"),
      LitanyContent(id: 86, position: 86, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.EASTER, books: "Lucas 24:1-2", content: "E, no primeiro dia da semana, muito de madrugada, as mulheres foram Ao sepulcro, levando as especiarias que tinham preparado. E acharam a pedra revolvida do sepulcro"),
      LitanyContent(id: 87, position: 87, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.EASTER, content: "Aleluia!"),
      LitanyContent(id: 88, position: 88, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.EASTER, books: "Mateus 28:8-9", content: "E, saindo elas pressurosamente do sepulcro, com temor e grande alegria, eis que Jesus lhes sai ao encontro, dizendo: Eu vos saúdo"),
      LitanyContent(id: 89, position: 89, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.EASTER, content: "Aleluia!"),
      LitanyContent(id: 90, position: 90, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.EASTER, books: "João 20:11,13-15", content: "E Maria estava chorando fora, junto do sepulcro. Estando ela pois, chorando, abaixou-se para o sepulcro. Ela disse: Levaram o meu Senhor e não sei onde o puseram. E, tendo dito isto, voltou-se para trás, e viu Jesus em pé, mas não sabia que era Jesus. Disse-lhe Jesus: Mulher, porque choras? Ela, voltando-se, disse-lhe: Raboni"),
      LitanyContent(id: 91, position: 91, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.EASTER, content: "Aleluia!"),
      LitanyContent(id: 92, position: 92, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.EASTER, books: "Lucas 24:13,15", content: "E eis que, no mesmo dia, iam dois deles para uma aldeia, cujo nome era Emaús. E aconteceu que, indo eles falando entre si, e fazendo perguntas um ao outro, o mesmo Jesus se aproximou e ia com eles"),
      LitanyContent(id: 93, position: 93, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.EASTER, content: "Aleluia!"),
      LitanyContent(id: 94, position: 94, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.EASTER, books: "João 20:19-20", content: "E serradas as portas onde os discípulos, com medo dos Judeus, se tinham ajuntado, chegou Jesus e pôs-se no meio e disse-lhes: Paz seja convosco! E, dizendo isto, mostrou-lhes as suas mãos e o lado"),
      LitanyContent(id: 95, position: 95, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.EASTER, content: "Aleluia!"),
      LitanyContent(id: 96, position: 96, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.EASTER, books: "João 21:3-4,7", content: "Disse-lhes Simão Pedro: Vou pescar. Disseram-lhe eles: Também nós vamos contigo. E naquela noite nada apanharam. E, sendo já manhã, Jesus se apresentou na praia. Então aquele discípulo a quem Jesus amava, disse a Pedro: É o Senhor!"),
      LitanyContent(id: 97, position: 97, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.EASTER, content: "Aleluia!"),
      LitanyContent(id: 98, position: 98, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.EASTER, books: "Apocalipse 1:10,12-15, 17-18", content: "Eu fui arrebatado em espírito, no dia do Senhor, e ouvi detrás de mim uma grande voz. E virei-me para ver quem falava comigo. E, virando-me, vi um semelhante ao Filho do Homem, vestido até aos pés de um vestido comprido e cingido pelos peitos com um cinto de ouro. E, a sua cabeça e cabelos eram brancos, como lã branca, como a neve, e os seus olhos como chama de fogo; e. os seus pés, semelhantes a latão reluzente, como se tivessem sido refinados numa  fornalha, e a sua voz como a voz de muitas águas. E eu, quando o vi, caí a seus pés como morto; e ele pôs sobre mim a sua dextra, dizendo: Não temas, Eu sou o primeiro e o último e o que vivo e fui morto, mas eis aqui, estou vivo para todo o sempre, e tenho as chaves da morte e do inferno"),
      LitanyContent(id: 99, position: 99, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.EASTER, content: "Aleluia, Aleluia, Aleluia!"),

      LitanyContent(id: 100, position: 100, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.PENTECOST, content: "Ó Espírito Criador, Senhor da vida e luz, dador dos bens da graça, e Consolador dos corações e nosso Guia"),
      LitanyContent(id: 101, position: 101, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.PENTECOST, content: "Ouve-nos, ó Espírito Santo, e ajuda-nos"),
      LitanyContent(id: 102, position: 102, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.PENTECOST, content: "Ó Espírito, fonte da sabedoria e Inteligência, que procedes do Nosso Pai que está nos céus e de Jesus Cristo seu Filho: olha para nós misericordiosamente e dirige-nos na tua luz, para que nos separemos das trevas e prossigamos a colaborar naquilo que é verdadeiramente Teu"),
      LitanyContent(id: 103, position: 103, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.PENTECOST, content: "Ouve-nos, ó Espírito Santo, e ajuda-nos"),
      LitanyContent(id: 104, position: 104, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.PENTECOST, content: "Ó Espírito de Amor e Misericórdia que desceste abundantemente sobre nosso Senhor Jesus Cristo, pois te revelaste na sua imaculada vida aqui na terra, no seu profundo ensino e na sua morte redentora, para a nossa salvação. Tu fizeste a glória da sua ressurreição. Abre os nossos corações à tua influência"),
      LitanyContent(id: 105, position: 105, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.PENTECOST, content: "Ouve-nos, ó Espírito Santo, e ajuda-nos"),
      LitanyContent(id: 106, position: 106, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.PENTECOST, content: "Ó Espírito Divino, que tens sido concedido a todos que rogam a Deus Nosso Pai, que és Aquele que nosso Senhor Jesus Cristo prometeu enviar aos seus discípulos. Vem a nós, sê connosco e, mediante a tua graça, protege-nos de toda a malícia"),
      LitanyContent(id: 107, position: 107, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.PENTECOST, content: "Ouve-nos, ó Espírito Santo, e ajuda-nos"),
      LitanyContent(id: 108, position: 108, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.PENTECOST, content: "Ó Espírito Todo-Poderoso, como desceste sobre a Igreja no princípio, transformando os discípulos em grandes testemunhas do nosso Salvador, concedendo-lhes vitórias na luta contra as muitas dificuldades. Assim te suplicamos que desças sobre a Igreja em Angola, na África e em todo o mundo; concede que haja neles o resplendor da tua luz e o poder de Deus para salvar todo o mundo"),
      LitanyContent(id: 109, position: 109, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.PENTECOST, content: "Ouve-nos, ó Espírito Santo, e ajuda-nos"),
      LitanyContent(id: 110, position: 110, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.PENTECOST, content: "Ó Espírito Divino, que interpretas os ensinamentos de nosso Senhor Jesus Cristo, explica-nos todas as palavras e semeia-as em nossos corações. Rectifica os nossos pensamentos e purifica os nossos corações, para que fiquemos cheios da tua graça e da tua vida, e para que produzamos aquele fruto abundante que glorifica o teu Santo Nome"),
      LitanyContent(id: 111, position: 111, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.PENTECOST, content: "Ouve-nos, ó Espírito Santo, e ajuda-nos"),

      LitanyContent(id: 112, position: 112, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.FIRST_DAY_OF_THE_YEAR, content: "Findos os dias do ano velho, e já no primeiro dia do Ano-Novo, dediquemo-nos a Deus e peçamos-lhe para nos guardar com a Sua bondade, a qual nos cerque e nos guie no decorrer do Ano-Novo que nos concedeu"),
      LitanyContent(id: 113, position: 113, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.FIRST_DAY_OF_THE_YEAR, content: "(Canta a 1ª estrofe do Hino N.º 22)"),
      LitanyContent(id: 114, position: 114, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.FIRST_DAY_OF_THE_YEAR, content: "m viagem da nossa vida neste Ano, prometemos deixar-nos guiar por Jesus, pois Ele mesmo disse: não se turbe o vosso coração, credes em Deus, credes também em mim. Eu sou o caminho e a verdade e a vida"),
      LitanyContent(id: 115, position: 115, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.FIRST_DAY_OF_THE_YEAR, content: "(Canta a 2ª estrofe do Hino N.º 22)"),
      LitanyContent(id: 116, position: 116, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.FIRST_DAY_OF_THE_YEAR, content: "Hoje é o primeiro dia do ano. Alguém sabe como há-de ser este ano? Somente o sabe Deus. Por isso purificai-vos pela bondade de Jesus. Porque o justo tem vida pela bondade e esperança, porque a fé é o firme fundamento das cosas que se esperam e a prova das coisas que não se vêm"),
      LitanyContent(id: 117, position: 117, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.FIRST_DAY_OF_THE_YEAR, content: "(Canta a 3ª estrofe do Hino N.º 22)"),
      LitanyContent(id: 118, position: 118, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.FIRST_DAY_OF_THE_YEAR, content: "Crianças, rapazes, raparigas, homens, mulheres, velhos e todos vós, não amemos a Deus só com palavras e a língua, mas sim com obras e verdade. Somos conhecidos se tivermos a verdadeira fé n’Ele e a Ele oferecermos os nossos corações. Deus é Omnisciente. Perante a Sua face nada está oculto. Ele conhece os segredos de todos os corações. Ai de quem tiver quaisquer segredos perante Ele, porque Deus vê tudo. Bemaventurados os que têm corações puros e a eles seja dado tudo o que a Deus pedirem, porque respeitam os mandamentos do Senhor e fazem tudo aquilo que é recto diante d’Ele"),
      LitanyContent(id: 119, position: 119, person: LitanyPerson.CONGREGATION, litanyTitle: LitanyTitleSeeder.FIRST_DAY_OF_THE_YEAR, content: "(Canta a 4ª estrofe do Hino N.º 22)"),
      LitanyContent(id: 120, position: 120, person: LitanyPerson.DIRECTOR, litanyTitle: LitanyTitleSeeder.FIRST_DAY_OF_THE_YEAR, content: "Seu mandamento é que confiemos em Seu Filho, Jesus Cristo, amando-nos uns aos outros. Aquele que guarda os mandamentos do Senhor está com Deus e Deus com ele. Sabemos que Ele está connosco, porque nos enviou o Espírito Santo. Ámen"),
    ];
  }
}